INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1, 1, 0, "ここに困ってる人は\nいないよね…？\nよかった♪", 100101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 2, 1, 0, "冒険の準備準備…\nうん、忘れ物はなし！\n…あっ！", 100101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 3, 1, 0, "リズムを意識して…\nわんつーわんつー！\nうん、調子はバッチリ", 100101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 4, 1, 0, "ここで特訓していい？\n物は壊さないように\n気をつけるね！", 100101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 5, 1, 0, "なんか落ち着かないや\nもしかして、\n困ってる人が\n近くにいるのかな…？", 100101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 6, 1, 0, "えへへ…いつも\n一緒にいてくれて\nありがとねっ！", 100101, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 7, 1, 0, "ちゃんと覚えてるよ\n騎士くんの声…\nあの時はありがとね！", 100101, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 8, 2, 0, "あははっ！\nやったなー！\nお返しだー！\nこちょこちょこちょ…", 100101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 9, 2, 0, "どうしたの？\nもしかして困ってる？\nあたしにまかせて！", 100101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 10, 2, 0, "えっとね…しっぽは\n触っちゃダメだよ？", 100101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 11, 2, 0, "反応しない特訓…\nだったけど無理～！\n悔しいよぉ～！", 100101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 12, 2, 0, "ふぁふぃ？\nやられた～！　ほっぺ\nぷにぷにしないでぇ～", 100101, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 13, 2, 0, "えへへ…\n騎士くんの手だ…\nあったかいな…♪", 100101, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 14, 2, 0, "おはよー！\n今日も元気に\nいっちゃうよー！", 100101, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 15, 2, 0, "ユイちゃんに\nおやつ作って\nもらおうかな～♪", 100101, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 16, 2, 0, "寝るのも修行だって\nこないだレイさんが\n言ってたんだ…", 100101, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 17, 3, 0, "ちゃんとケアして\n傷が残らないように\nしないと…", 100101, 0, 1);
