INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 23, 3, 0, "美味しいけど、\n食べ過ぎないように\nしないと…！！", 100101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 24, 3, 0, "文字が多いと\n眠くなっちゃう…\n危ない危ない！", 100101, 0, 1);
