INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 28, 1, 0, "留守番はちょっと\n苦手かも…\nなんか寂しいんだもん", 100102, 0, 1);
