INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 49, 3, 0, "色んなことを\n知っておくのは\n大事だよね！", 100102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 51, 1, 0, "お片付けも済んだし\n今度は洗濯ものを\nたたもうかな", 100201, 0, 1);
