INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 60, 2, 0, "困ったことが\nあったら\nいつでも話してね", 100201, 0, 1);
