INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 66, 2, 0, "遅くまでお疲れ様\n明日も頑張ろうね\nふわぁ…", 100201, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 67, 3, 0, "よし…\nこれなら\n褒めてもらえるかな？", 100201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 68, 3, 0, "がんばることも\n必要だけど、\nお休みも大切だよね", 100201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 69, 3, 0, "かわいいなぁ…\nわたしも今度\n作ってみよう。\nえっと、材料は…", 100201, 0, 1);
