INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 72, 3, 0, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 100201, 0, 1);
