INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 87, 2, 0, "騎士クンに\n伝えたいことがあるの\nとっても大切な…\nこ、今度、話すね！", 100202, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 88, 2, 0, "ふふふっ…そんなとこ\nツンツンしないで\nくすぐったいよ～", 100202, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 89, 2, 0, "おはよう\nよく眠れた？　今日も\n一日、頑張ろうね♪", 100202, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 90, 2, 0, "お腹空いちゃった…\nそうだ。騎士クンも\n一緒にお昼食べない？", 100202, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 91, 2, 0, "もうこんな時間…\nそろそろ寝なくちゃ\nおやすみなさい", 100202, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 92, 3, 0, "よし…\nこれなら\n褒めてもらえるかな？", 100202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 93, 3, 0, "がんばることも\n必要だけど\nお休みも大切だよね", 100202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 94, 3, 0, "かわいい～♪\n騎士クンは\nこういうのって好き？", 100202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 95, 3, 0, "戦う前にもこうやって\n落ち着けたら\nいいのに", 100202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 96, 3, 0, "あの…！\nよ、良かったら\n一緒に…！", 100202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 97, 3, 0, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 100202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 98, 3, 0, "最近服のサイズが\n合わないんだけど…\n食べ過ぎなのかな？", 100202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 99, 3, 0, "…やっぱり\n月刊恋愛指南は\nためになる…", 100202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 101, 1, 0, "装備の手入れでも\nしようかな…", 100301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 102, 1, 0, "次に鍛えるべきは\n肉体面か精神面か\nどちらにしよう…？", 100301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 103, 1, 0, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 100301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 104, 1, 0, "仲間なんていらない\nそう思ってた頃も\nあったな…", 100301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 105, 1, 0, "考えごとをしていると\nつい部屋中を\n歩いてしまうな", 100301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 106, 1, 0, "彼は\nどこまで強くなるのか\n私も負けられないな", 100301, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 107, 1, 0, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 100301, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 108, 2, 0, "ああ、キミだったのか\n人の気配がしたから\n誰かと思ったよ", 100301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 109, 2, 0, "斬りかかるつもりなら\nちゃんとかかってきて\n…呼んだだけ？", 100301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 110, 2, 0, "なに？　私に相談？\n戦いについてなら\n喜んで", 100301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 111, 2, 0, "今のが突き？\n軽い！　もっと鋭く！\nさあ、もう一回！", 100301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 112, 2, 0, "ひゃっ！　ど、どこを\n触ってるの！？\n…油断した", 100301, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 113, 2, 0, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 100301, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 114, 2, 0, "おはよう\n今日も充実した\n一日にしていこう", 100301, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 115, 2, 0, "そろそろ\nお茶の時間か\nキミも一緒にどう？", 100301, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 116, 2, 0, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 100301, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 117, 3, 0, "戦いを経てもなお\n美しくあるように…\nこれでよし♪", 100301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 118, 3, 0, "戦ってばかりで\n休まないと、また\nユイに怒られるな", 100301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 119, 3, 0, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 100301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 120, 3, 0, "これはいい…\n戦いの前の精神統一に\nもってこいじゃないか", 100301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 121, 3, 0, "時々は落ち着いた\n時間を過ごすのも\n悪くない…", 100301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 122, 3, 0, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 100301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 123, 3, 0, "食事は大切だけど\n食べ過ぎには\n注意しておこう…", 100301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 124, 3, 0, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 100301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 126, 1, 0, "海だけじゃなくて\n川でも釣りがしたいな\n道具も新調しよう", 100302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 127, 1, 0, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 100302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 128, 1, 0, "彼は\nどこまで強くなるのか\n私も負けられないな", 100302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 129, 1, 0, "そろそろ修行の\n内容を新しくしよう\nより厳しいものにね", 100302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 130, 1, 0, "強くなって…誰にも\n妨げられない自由な\n生き方がしたい", 100302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 131, 1, 0, "まさか風邪を引くとは\n彼の看病のおかげで\nすっかり良く\nなったけど", 100302, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 132, 1, 0, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 100302, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 133, 2, 0, "どうしたの？\nなんでもない？\nそれなら良かった", 100302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 134, 2, 0, "あんまり突っつくと\nお返しするよ？\n覚悟は出来てる？", 100302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 135, 2, 0, "ねぇ、今度また\n釣りに行かない？\n大きいのを狙おう", 100302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 136, 2, 0, "修行がしたいの？\nそれを早く言ってよ\nすぐに出発しよう！", 100302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 137, 2, 0, "ひょっとして\n今のも一本だなんて\n言わないよね？", 100302, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 138, 2, 0, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 100302, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 139, 2, 0, "今日は仕事？\nそれとも修行？\n良ければ付き合うよ", 100302, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 140, 2, 0, "休息も時には\n必要だと、仲間から\n教わったよ", 100302, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 141, 2, 0, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 100302, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 142, 3, 0, "髪の長さを少し\n変えてみようかな…\nわ、私は何を…！", 100302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 143, 3, 0, "休息があってこそ\n万全な状態で\n戦えるんだ", 100302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 144, 3, 0, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 100302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 145, 3, 0, "体と同様に\n心もほぐれるね…\nはぁ…", 100302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 146, 3, 0, "休む時は休む\nヒヨリもそろそろ\n覚えて欲しいな…", 100302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 147, 3, 0, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 100302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 148, 3, 0, "修行のせいか\n最近食べる量が\n増えてきたような…", 100302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 149, 3, 0, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 100302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 151, 1, 0, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 100401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 152, 1, 0, "落とし穴…どこに\n作ったんだっけ？", 100401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 153, 1, 0, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 100401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 154, 1, 0, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 100401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 155, 1, 0, "かくれんぼする人\nこの指、と～まれ！", 100401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 156, 1, 0, "ミソギ以外の人に\nイタズラされちゃ\nダメだよ！", 100401, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 157, 1, 0, "ミソギと遊ぶ？\nミソギに遊ばれる？", 100401, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 158, 2, 0, "いいこと思いついた！\nい、イタズラとかじゃ\nないよ～", 100401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 159, 2, 0, "うわっ！？\n　にいちゃんが、\nミソギにイタズラ？", 100401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 160, 2, 0, "にいちゃんの\n通りそうなところに\nバナナの皮を…", 100401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 161, 2, 0, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 100401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 162, 2, 0, "ここ、ミソギ探検隊の\n基地にしようよ！", 100401, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 163, 2, 0, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 100401, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 164, 2, 0, "にいちゃん、おはよ！\n目覚まし時計を\n止めておいたのに、\nよく起きられたね～♪", 100401, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 165, 2, 0, "にいちゃんが\n昼寝したら、\nイタズラし放題♪", 100401, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 166, 2, 0, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 100401, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 167, 3, 0, "イタズラには\nお色気も必要…\nうん、完璧！", 100401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 168, 3, 0, "座ったら、ブーッって\nいう音が出たら、\nおもしろいな～♪", 100401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 169, 3, 0, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 100401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 170, 3, 0, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 100401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 171, 3, 0, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 100401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 172, 3, 0, "探検家は\n体力勝負だよー！", 100401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 173, 3, 0, "みんなに分けて\nあげようかな～\n激辛ソースも\n入れちゃうけどね！", 100401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 174, 3, 0, "こういう所に\n宝の地図が\n隠れてるんだよね～", 100401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 176, 1, 0, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 100402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 177, 1, 0, "うぅ、探検に出たい！\nじっとしてなんか\nいられないよ～！", 100402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 178, 1, 0, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 100402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 179, 1, 0, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 100402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 180, 1, 0, "かくれんぼする人\nこの指、と～まれ！", 100402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 181, 1, 0, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 100402, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 182, 1, 0, "にいちゃんが喜ぶ\nイタズラ、何かない\nかな～？", 100402, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 183, 2, 0, "ミソギがしかけた\nイタズラ\n見つけられるかな？", 100402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 184, 2, 0, "探検する？\n冒険する？\nそれとも…\nイタズラされる？", 100402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 185, 2, 0, "将来の夢は探検家！\n探したい物はないけど", 100402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 186, 2, 0, "にいちゃんに宿題！\n落とし穴を10個\n掘ること！", 100402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 187, 2, 0, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 100402, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 188, 2, 0, "ここ、ミソギ探検隊の\n基地にしようよ！", 100402, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 189, 2, 0, "にいちゃん\n探検に行こうよ！\n今からなら遭難しても\n夜には帰れるよ！", 100402, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 190, 2, 0, "にいちゃんが昼寝したら\nイタズラし放題♪", 100402, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 191, 2, 0, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 100402, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 192, 3, 0, "イタズラには\nお色気も必要…\nうん、完璧！", 100402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 193, 3, 0, "ゆっくりまったりして\n次のイタズラを\n考えよ～！", 100402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 194, 3, 0, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 100402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 195, 3, 0, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 100402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 196, 3, 0, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 100402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 197, 3, 0, "探検家は\n体力勝負だよー！", 100402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 198, 3, 0, "探検のおやつに\nぴったりかも～！", 100402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 199, 3, 0, "こういう所に\n宝の地図が\n隠れてるんだよね～", 100402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 201, 1, 0, "音楽は全部好きだけど\n一番好きなのは\nやっぱりジャズかな", 100601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 202, 1, 0, "はぁ、はぁ…\n一人でもいっぱい\nしないと…\n特訓…", 100601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 203, 1, 0, "お姉ちゃんには\nもっと自信を\n持ってほしいなぁ", 100601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 204, 1, 0, "お姉ちゃんのマネ\nしちゃおうかな？", 100601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 205, 1, 0, "双子って、\n好みのタイプも\n似ちゃうのかな？", 100601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 206, 1, 0, "お兄ちゃんと私は\n相性抜群です♪", 100601, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 207, 1, 0, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！", 100601, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 208, 2, 0, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！", 100601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 209, 2, 0, "うふふ、お兄ちゃん！\n今日は何を\nするんですか？", 100601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 210, 2, 0, "そんなところ\n触っちゃダメ♪", 100601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 211, 2, 0, "お兄ちゃん、イジワル\nしないでください…", 100601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 212, 2, 0, "お兄ちゃん、もっと\n上級編のテクニック、\n教えてくださいね！", 100601, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 213, 2, 0, "コーチ、\n今日も特訓\nお願いします！", 100601, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 214, 2, 0, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪", 100601, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 215, 2, 0, "こんな明るい時間から\nす、するんですか…\n二人っきりの特訓…", 100601, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 216, 2, 0, "夜はこれからですよ\nお兄ちゃん♪", 100601, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 217, 3, 0, "世界一、かわいく\nなるんだから♪", 100601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 218, 3, 0, "気持ちいい♪\nもう立てないよ～", 100601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 219, 3, 0, "かわいい♪\nアカリのライバル\nだね", 100601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 220, 3, 0, "はぁ…気持ちいい、\n私、このままダメに\nなっちゃいそう…", 100601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 221, 3, 0, "はぁ…アカリ、\nとろけちゃう…", 100601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 222, 3, 0, "はぁ…はぁはぁ、\n私…もう…ダメ\n限界です…！", 100601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 223, 3, 0, "わぁ、おいしい～\nけど食べ過ぎは美容に\nよくないですよね", 100601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 224, 3, 0, "この楽譜、\nサックスで吹いて\nみたいなぁ♪", 100601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 226, 1, 0, "このごろ一人でいると\nお兄ちゃんのこと\nばかり考えちゃう…", 100602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 227, 1, 0, "はぁ、はぁ…もっと\nいっぱいしなくちゃ\n…練習", 100602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 228, 1, 0, "このお部屋、なんだか\n落ち着きます…\nお兄ちゃんが\nいるから？", 100602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 229, 1, 0, "みんなかわいいな～\n世界一かわいくなる\nのは、難しいな…", 100602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 230, 1, 0, "双子って、\n好みのタイプも\n似ちゃうのかな？", 100602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 231, 1, 0, "いつか世界でいちばん\nかわいくなって\nお兄ちゃんと…", 100602, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 232, 1, 0, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！", 100602, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 233, 2, 0, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！", 100602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 234, 2, 0, "二人きりの秘密の特訓\n今度はいつですか？\n待ち遠しいです…", 100602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 235, 2, 0, "え？　ここでするの？\nみんなの前で恥ずか\nしいな…筋トレ", 100602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 236, 2, 0, "お兄ちゃん、イジワル\nしないでください…", 100602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 237, 2, 0, "お兄ちゃんといると\nわ、私…ドキドキ\nしてきちゃう…", 100602, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 238, 2, 0, "コーチ、\n今日も特訓\nお願いします！", 100602, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 239, 2, 0, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪", 100602, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 240, 2, 0, "お兄ちゃんに\n食べてもらいたいなぁ\n手作りのお昼ごはん", 100602, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 241, 2, 0, "お風呂入ってきますね\nそのあとは…\n二人でい～っぱい\n遊びましょうね", 100602, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 242, 3, 0, "お姉ちゃんのマネ\nしちゃおうかな？", 100602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 243, 3, 0, "気持ちいい♪\nもう立てないよ～", 100602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 244, 3, 0, "かわいい♪\nアカリのライバル\nだね", 100602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 245, 3, 0, "はぁ…気持ちいい、\n私、このままダメに\nなっちゃいそう…", 100602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 246, 3, 0, "あったか～い…\nまるでお兄ちゃんが\n隣にいる時みたい", 100602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 247, 3, 0, "汗をかくと\n気持ちいいですよね", 100602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 248, 3, 0, "お兄ちゃんの特訓に\n付き合うために\nたっぷり\n食べておかないと♪", 100602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 249, 3, 0, "この楽譜\nサックスで吹いて\nみたいなぁ♪", 100602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 251, 1, 0, "プリン、プリン、\nプリン、プリン、\nプリン、プリン～♪", 100701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 252, 1, 0, "う～ら～め～し～や～\nなの～♪", 100701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 253, 1, 0, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～", 100701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 254, 1, 0, "幽霊の正体見たり、\nミヤコなの～", 100701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 255, 1, 0, "ミヤコがいきなり\n消えたら、みんな\n驚くかもなの～♪", 100701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 256, 1, 0, "アイツがいて、\nプリンもある。\nきっとここが天国なの", 100701, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 257, 1, 0, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～", 100701, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 258, 2, 0, "姿を消して、オマエを\n見張ってやるの～\nむふふ～♪", 100701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 259, 2, 0, "もし成仏した先に\nプリンがなかったら\n地獄なの～…", 100701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 260, 2, 0, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！", 100701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 261, 2, 0, "死んだ人には\nお供え物をするの～\nミヤコにも、プリンを\n毎日お供えするの～", 100701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 262, 2, 0, "たくさんオマエと\n遊べて、\n成仏しそうなくらい\nうれしいの～！", 100701, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 263, 2, 0, "オマエのこと\n気に入ったからずっと\nとり憑いてやるの～", 100701, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 264, 2, 0, "う～　眠いの…\n二度目の二度寝を\nするの…", 100701, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 265, 2, 0, "お昼ご飯はプリンなの\n３時のおやつも、\nプリンなの～", 100701, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 266, 2, 0, "ディナーは豪華に、\nプリンのフルコース\nなの～♪", 100701, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 267, 3, 0, "体が透けちゃうから\nうまく使えないの～", 100701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 268, 3, 0, "まったりなの～\nあとはプリンがあれば\n完璧なの～", 100701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 269, 3, 0, "む～…かわいいけど\nプリンは分けて\nあげないの～", 100701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 270, 3, 0, "は～　いい気分なの…\n成仏しそうなの…", 100701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 271, 3, 0, "も、もしかして\nミヤコ、ここで\n火葬されちゃうの？", 100701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 272, 3, 0, "幽霊には、体力も\n筋力も関係ないの～！", 100701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 273, 3, 0, "ぱくぱくモグモグ…\nミヤコ、幽霊だけど、\nお腹はすくの～", 100701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 274, 3, 0, "プリンの写真集が\nあればいいのに\nなの～", 100701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 276, 1, 0, "うらめしや～…\n裏の飯より\nプリンがいいの～", 100702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 277, 1, 0, "プリン♪　プリン♪\nプリン♪　なの～♪　", 100702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 278, 1, 0, "世界中のケーキ屋が\nプリンのおいしさを\n競い合えばいいの～！\n審査員はミヤコなの～", 100702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 279, 1, 0, "買ってきたプリンには\n全部ミヤコの名前を\n書いておくの～", 100702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 280, 1, 0, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～ ", 100702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 281, 1, 0, "アイツがいて\nプリンもある\nきっとここが天国なの", 100702, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 282, 1, 0, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～", 100702, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 283, 2, 0, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！", 100702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 284, 2, 0, "もし成仏した先に\nプリンがなかったら\n地獄なの～…", 100702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 285, 2, 0, "今日はオマエと\nプリンを買いに行くの\n強制なの～　", 100702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 286, 2, 0, "死んだ人には\nお供え物をするの～\nミヤコにも、プリンを\n毎日お供えするの～", 100702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 287, 2, 0, "たくさんオマエと\n遊べて\n成仏しそうなくらい\nうれしいの～！ ", 100702, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 288, 2, 0, "ミヤコのプリン\nオマエにだったら\n分けてあげるの～ ", 100702, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 289, 2, 0, "う～　眠いの…\n二度目の二度寝を\nするの… ", 100702, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 290, 2, 0, "う～ら～め～し～や～\n…明るい時間だと\n驚かせないの～ ", 100702, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 291, 2, 0, "ひゅ～どろどろ～ん☆\n真っ暗なうちに\nいたずらしまくるの～♪", 100702, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 292, 3, 0, "体が透けちゃうから\nうまく使えないの～ ", 100702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 293, 3, 0, "落ち着くの～\nいまプリン食べたら\nきっといつもより\nおいしいの～♪ ", 100702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 294, 3, 0, "む～…かわいいけど\nプリンは分けて\nあげないの～　", 100702, 0, 1);
