INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 296, 3, 0, "も、もしかして\nミヤコ、ここで\n火葬されちゃうの？ ", 100702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 297, 3, 0, "幽霊には、体力も\n筋力も関係ないの～！　", 100702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 298, 3, 0, "おいしいの～\nプリン味だったら\nもっとおいしいの～ ", 100702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 299, 3, 0, "プリンの写真集が\nあればいいのに\nなの～ ", 100702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 301, 1, 0, "もし太陽が滅んでも\nボクの美しさで大地を\n照らしてあげる！", 100801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 302, 1, 0, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね", 100801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 303, 1, 0, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪", 100801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 304, 1, 0, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！", 100801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 305, 1, 0, "うわっ昨日より可愛く\nなってる。明日は\nどうなっちゃう\nのかな？", 100801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 306, 1, 0, "このボクがいるだけで\nあの人への\nおもてなしだよね", 100801, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 307, 1, 0, "ボクの可愛さは、\nふふ…あの人の\nためのものさ", 100801, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 308, 2, 0, "ねぇ～、ボクって\n可愛い？\nふふ、知ってる☆", 100801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 309, 2, 0, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 100801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 310, 2, 0, "ふふ、ボクに触ると\nキミも美しく\nなっちゃうかもよ？", 100801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 311, 2, 0, "どうかな、\n今日のボク。\n美しい？　可愛い？\nねえ、どっち？", 100801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 312, 2, 0, "あの人も今、\n可愛いボクのこと\n考えてるかな…", 100801, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 313, 2, 0, "美しい…\nキミの瞳に写るボクは\nより一層きれいだ", 100801, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 314, 2, 0, "美しいボクの\n美しい一日が\n始まるよ！", 100801, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 315, 2, 0, "さーて、午後からも\n可愛く美しく\n華麗に優雅に♪", 100801, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 316, 2, 0, "バラの花びら\nいっぱい浮かべた\nお風呂に入ってから\n寝ようっと♪", 100801, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 317, 3, 0, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 100801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 318, 3, 0, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 100801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 319, 3, 0, "わあ、可愛い！\n…まあ、ボクほど\nじゃないけどさ！", 100801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 320, 3, 0, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 100801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 321, 3, 0, "気安く触ったら\n火傷する…\n炎はボクに似ているね", 100801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 322, 3, 0, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 100801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 323, 3, 0, "ボクが食べると、\n食べ物まで\n可愛いなあ…", 100801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 324, 3, 0, "ボクが触った瞬間に、\nこの本は美を\n携えてしまうんだ", 100801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 326, 1, 0, "ボクの美しさは\nみんなのために\nあるんだよ！", 100802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 327, 1, 0, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね", 100802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 328, 1, 0, "これ以上美しくなると\nボクをめぐって\n大陸中で戦争が\n始まっちゃう！", 100802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 329, 1, 0, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！", 100802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 330, 1, 0, "ボクの可愛さについて\nみんな話し合わない\nのかな…？", 100802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 331, 1, 0, "このボクがいるだけで\nあの人への\nおもてなしだよね", 100802, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 332, 1, 0, "あの人も今\n可愛いボクのこと\n考えてるかな…", 100802, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 333, 2, 0, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪", 100802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 334, 2, 0, "ボクの美しさに嫉妬\nしたって意味ないよね\n次元が違うんだから", 100802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 335, 2, 0, "ボクの化粧品使う？\n一緒に可愛くなろうよ", 100802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 336, 2, 0, "ボクがいるだけで\nこの部屋は美の神殿♪\nランドソルの新たな\n観光名所になるね！", 100802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 337, 2, 0, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 100802, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 338, 2, 0, "美しい…\nキミの瞳に写るボクは\nより一層綺麗だ", 100802, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 339, 2, 0, "朝日と共に\nボクの美しさも\n周りを照らすよ", 100802, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 340, 2, 0, "ボクの美しさを\n維持するために\nお昼ご飯食べなくちゃ", 100802, 2, 1);
