INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 342, 3, 0, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 100802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 343, 3, 0, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 100802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 344, 3, 0, "可愛いものを\n可愛いって言える\nボクって可愛いよね！", 100802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 345, 3, 0, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 100802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 346, 3, 0, "冷えは美容の大敵\nなんだから！\nここ、ボクの場所ね！", 100802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 347, 3, 0, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 100802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 348, 3, 0, "ボクが食べると\n食べ物まで\n可愛いなあ…", 100802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 349, 3, 0, "ボクが触った瞬間に\nこの本は美を\n携えてしまうんだ", 100802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 351, 1, 0, "「第１０部２０章\n終焉戦争、開幕」\n…これでよし！", 100901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 352, 1, 0, "今度シグルドに\n私の魔法を一つ\n伝授してやるか", 100901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 353, 1, 0, "ここでは風が\n吹いてこないな…\n換気したい！", 100901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 354, 1, 0, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 100901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 355, 1, 0, "まずは包帯をとって\n手から龍の形の\n炎を出して…", 100901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 356, 1, 0, "シグルド…\n今頃何処で何を\nしているのだろう…", 100901, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 357, 1, 0, "最近、彼の姿が\nまぶしく見える…\nどうして…？", 100901, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 358, 2, 0, "辞書を知らないか？\n…探している\n理由は聞くな", 100901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 359, 2, 0, "そうだ！\n貴様の技にも\n名前をつけてやろう！", 100901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 360, 2, 0, "私に触れても力を\n奪われないのは\n貴様くらいだぞ？", 100901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 361, 2, 0, "何事だ？\nまさか我が瞳に\n魅入られたのか？", 100901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 362, 2, 0, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 100901, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 363, 2, 0, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 100901, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 364, 2, 0, "「覚醒」めたか…\n行くぞ、悪を討つ\n戦いに出発だ！", 100901, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 365, 2, 0, "昼間は「ヤツら」も\n襲ってはこない…\n束の間の休息だ", 100901, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 366, 2, 0, "どうした？　夜はまだ\nこれから…ふぁ…\nね、眠くなどないぞ！", 100901, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 367, 3, 0, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 100901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 368, 3, 0, "くっ…！\n私を眠りの国へ\n誘うつもりか！", 100901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 369, 3, 0, "このようなものに\n懐柔されるものか！\n…されるものか！", 100901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 370, 3, 0, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 100901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 371, 3, 0, "これが我らの求めた\n安寧か…\n悪くない…", 100901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 372, 3, 0, "この程度\n前世での激闘に\n比べれば…！", 100901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 373, 3, 0, "最近金欠でな…\nいやぁありがたい…", 100901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 374, 3, 0, "ここにも\n前世の記憶が\n眠っていたとは…", 100901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 376, 1, 0, "「第１３部１章\n閃光のシグルド」\n…ついに登場だな", 100902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 377, 1, 0, "何故だ…何故\nこうもあっさり金欠に\n逆戻りするのだ！？", 100902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 378, 1, 0, "やはり転生者という\n設定はカッコイイ…\n天才だな、私！", 100902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 379, 1, 0, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 100902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 380, 1, 0, "そろそろ今日の分の\n魔力を溜めないと\nまた魔力切れが…", 100902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 381, 1, 0, "最近奴がそばに\nいるだけで、鼓動が\n早くなるのは何故だ？", 100902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 382, 1, 0, "シ、シグルド…！\nだめだ…妄想でも\n上手く言えない…", 100902, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 383, 2, 0, "なんだ？　新たな\n賞金の情報を\n持ってきたのか？", 100902, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 384, 2, 0, "名乗りやポーズは\nその強度で相手を\n圧倒する技術だ", 100902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 385, 2, 0, "いいか、魔法の\n詠唱中は絶対に\n声をかけるなよ？", 100902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 386, 2, 0, "おお、ちょうど今\n貴様専用の設定集を\n作成中だぞ！", 100902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 387, 2, 0, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 100902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 388, 2, 0, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 100902, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 389, 2, 0, "夜更かしのせいか\nまだ眠気が…いや！\nこれは組織の罠だ！", 100902, 1, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 390, 2, 0, "平穏な時に\n出来るだけ魔力を\n溜めておかねば…", 100902, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 391, 2, 0, "夜になると、力が\n増した気分になるな\n…なりませんか？", 100902, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 392, 3, 0, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 100902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 393, 3, 0, "お、おのれ\nヒュプノス…\n私は負けないぞ…", 100902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 394, 3, 0, "くっくっくっ…\n貴様は特別団員に\nしてやろう", 100902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 395, 3, 0, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 100902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 396, 3, 0, "これが我らの求めた\n安寧か…\n悪くない…", 100902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 397, 3, 0, "新たな戦いに備えて\n魔力だけでなく\n体力も鍛えねば！", 100902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 398, 3, 0, "食事は魔力に\n変換する体力の\n元になるのだ！", 100902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 399, 3, 0, "フフフ…\nこれを覚えれば\nもっと強く…！", 100902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 401, 1, 0, "王子はんほんまに\nはんなりしてて、\n王子はんって感じやわ", 101001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 402, 1, 0, "この絵本の王子はん、\n王子はんみたいで\nええなぁ♪", 101001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 403, 1, 0, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 101001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 404, 1, 0, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 101001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 405, 1, 0, "王子はんのために\n糸を紡いで、\n編み物でもしまひょか", 101001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 406, 1, 0, "マホ姫と王子はん、\n絵本の世界より\nベストカップルや", 101001, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 407, 1, 0, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 101001, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 408, 2, 0, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 101001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 409, 2, 0, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 101001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 410, 2, 0, "王子はんは、\n絵本から出てきた人\nみたいやね～", 101001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 411, 2, 0, "うちの話し方\nのんびりしてます\nやろか？", 101001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 412, 2, 0, "マホ姫の手は\nつべたいから、\n王子はんが温めて？", 101001, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 413, 2, 0, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 101001, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 414, 2, 0, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 101001, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 415, 2, 0, "お茶請けはお大福が\nよろしおすけど\nクッキーもええなあ", 101001, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 416, 2, 0, "うち、普段から\n夢見がちやけど…\nほんに眠い時間やわぁ", 101001, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 417, 3, 0, "王子はんに\n会いに行くなら、\nおめかしせな\nあかんなぁ", 101001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 418, 3, 0, "はんなりした\nええお椅子やわぁ\nほっこりしますなあ", 101001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 419, 3, 0, "かいらしもんが\nいっぱいで、\nマホマホ王国みたい\nやわぁ", 101001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 420, 3, 0, "心地ええどす。まるで\nお花畑にいるようやね", 101001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 421, 3, 0, "ぽかぽかどす。\n心まであったかく\nなりますわぁ", 101001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 422, 3, 0, "はぁはぁ、お姫はんは\n激しい運動は\nしぃひんもんどす～", 101001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 423, 3, 0, "もぐもぐ…あんまり\n速う食べられへんけど\n美味しいわぁ", 101001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 424, 3, 0, "絵本、絵本～\n幸せやわぁ♪", 101001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 426, 1, 0, "王子はんにとって\n自慢の姫になるえ～", 101002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 427, 1, 0, "カボチャの馬車に\nガラスの靴…\n欲しいなぁ", 101002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 428, 1, 0, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 101002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 429, 1, 0, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 101002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 430, 1, 0, "ほんまなぁ\nこの絵本が面白いんよ\n王子はんに\n読んであげよ", 101002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 431, 1, 0, "マホ姫と王子はん\n絵本の世界より\nベストカップルや", 101002, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 432, 1, 0, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 101002, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 433, 2, 0, "たまには、王子はんの\n好きな絵本も\n知りたいな～", 101002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 434, 2, 0, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 101002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 435, 2, 0, "そのうち、王子はんの\nお国にも連れてって\nもらえますやろか？", 101002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 436, 2, 0, "絵本読むんも\nええなぁ…", 101002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 437, 2, 0, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 101002, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 438, 2, 0, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 101002, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 439, 2, 0, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 101002, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 440, 2, 0, "マホマホ王国では\nこの時分は\nみんなお昼寝\nしてたんよ", 101002, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 441, 2, 0, "夜に飛んだり鳴いたり\nする鳥はんもおるんよ\n昼は寝てはるんかねぇ", 101002, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 442, 3, 0, "王子はんに\n会いに行くなら\nおめかしせな\nあかんなぁ", 101002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 443, 3, 0, "王子はんも一緒に\nゆっくりしていっても\nええんよ？", 101002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 444, 3, 0, "かいらしい子やねぇ\nうちとお友だちに\nなってくれるん？", 101002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 445, 3, 0, "癒しアイテムって\nほんま、好っきゃわぁ", 101002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 446, 3, 0, "うちのお城にも\nほしいわぁ。帰ったら\nさっそく準備せな\nあかんなぁ", 101002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 447, 3, 0, "部屋の中で運動\nできるのも、なかなか\n面白いもんやわぁ", 101002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 448, 3, 0, "おいひいわ～\nマホマホ王国には\nない味やわぁ", 101002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 449, 3, 0, "しっかり知識を\n身につけんと\n魔法も使えへん\nからなぁ", 101002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 451, 1, 0, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 101101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 452, 1, 0, "おやつのケーキは\n苺かチョコ…\nどっちも食べよう！", 101101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 453, 1, 0, "い、いけない！\n縫い針落としちゃった", 101101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 454, 1, 0, "ケーキをクレープで\n巻いたら…\n大ヒット商品の予感！", 101101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 455, 1, 0, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 101101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 456, 1, 0, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 101101, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 457, 1, 0, "ここがお兄ちゃんの\n部屋…\nドキドキしちゃう～", 101101, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 458, 2, 0, "ねぇ、おにいちゃん\n一緒に、スイーツでも\n食べにいかない？", 101101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 459, 2, 0, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 101101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 460, 2, 0, "勉強、見てくれるん\nですか？　やった～♪", 101101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 461, 2, 0, "服がほつれてますよ？\n直してあげます！", 101101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 462, 2, 0, "私をお嫁さんに\nしたいなら、予約は\nお早目に、ですよ♪", 101101, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 463, 2, 0, "ねぇねぇお兄ちゃん！\n…えへへ、\n呼んでみただけ♪", 101101, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 464, 2, 0, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 101101, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 465, 2, 0, "お昼休憩に\n甘いものが\n食べたいです～", 101101, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 466, 2, 0, "ねむいです～\nでももうちょっと\n勉強しないと～…", 101101, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 467, 3, 0, "この服、かわいい♪\n今度作って\nみよっと～！", 101101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 468, 3, 0, "勉強で疲れたから\n休憩しよ～っと", 101101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 469, 3, 0, "かわいい♪\n私が服を作って\nあげよっか？", 101101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 470, 3, 0, "リラックスできます～\nお兄ちゃんが隣に\nいるみたい！", 101101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 471, 3, 0, "のんびりしたら、\nまた勉強しよっと！", 101101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 472, 3, 0, "運動すると勉強も\nはかどるよね！", 101101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 473, 3, 0, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 101101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 474, 3, 0, "お兄ちゃんの記憶を\n戻す方法、どこかに\n書いてないかな？", 101101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 476, 1, 0, "ケーキの食べ過ぎで\nお腹が…うぅ、\n因果横暴だよー…", 101102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 477, 1, 0, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 101102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 478, 1, 0, "い、いけない！\n縫い針落としちゃった", 101102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 479, 1, 0, "笑う時には服着ます！", 101102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 480, 1, 0, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 101102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 481, 1, 0, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 101102, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 482, 1, 0, "お兄ちゃんが帰って\nきたら、何して遊ぼう\nかな～？", 101102, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 483, 2, 0, "勉強、見てくれるん\nですか？　やった～♪", 101102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 484, 2, 0, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 101102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 485, 2, 0, "おにいちゃん！\nそろそろ、おやつに\nしませんか？", 101102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 486, 2, 0, "勉強は…\n明日にしようっと", 101102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 487, 2, 0, "妹だけど、大人な\nレディですからね！", 101102, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 488, 2, 0, "な、ナデナデとか\n恥ずかしいな～", 101102, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 489, 2, 0, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 101102, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 490, 2, 0, "お昼休憩に\n甘いものが\n食べたいです～", 101102, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 491, 2, 0, "お兄ちゃんは\n夜強いんですね\n私は眠くて…", 101102, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 492, 3, 0, "服装や髪形が変わると\n気分も変わるよね", 101102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 493, 3, 0, "ここで寝ちゃったら\n受験勉強が～…\nぐーー", 101102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 494, 3, 0, "かわいい♪\n私が服を作って\nあげよっか？", 101102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 495, 3, 0, "いまならどんな試験も\n合格できそうです！", 101102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 496, 3, 0, "のんびりしたら\nまた勉強しよっと！", 101102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 497, 3, 0, "お兄ちゃんを\n守るためには\n毎日、鍛えなくちゃ！", 101102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 498, 3, 0, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 101102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 499, 3, 0, "この本の服\nかわいいなぁ♪\n私に作れるかな？", 101102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 501, 1, 0, "う～ん、今なら\nどこでだって\n眠れちゃいそう…", 101201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 502, 1, 0, "たくさん寝すぎて、\n夢の中か現実か\nわかんなく\nなっちゃうんだよね～", 101201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 503, 1, 0, "…はっ！？\nね、寝てないよ？\nホントだよっ", 101201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 504, 1, 0, "ぐっすり眠って\nパワーを\nフルチャージっ☆", 101201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 505, 1, 0, "もし、超能力のことが\nバレたら…ブルブル", 101201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 506, 1, 0, "テレパシーで\nキミの心を覗けたら\n…なんてね！", 101201, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 507, 1, 0, "キミを抱き枕に…\nじょ、冗談冗談！", 101201, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 508, 2, 0, "ん～、なになに？\n一緒にお昼寝する？", 101201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 509, 2, 0, "忘れ物しちゃった。\nま、いっか！\nえ、ダメ？", 101201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 510, 2, 0, "おおっ、相談ごと？\nなにかな\nなにかな～？", 101201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 511, 2, 0, "う～ん、もしかして\nキミにも仲良しになる\n超能力があるのかも！", 101201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 512, 2, 0, "むむ～…どう？\nテレパシーで、私の\n気持ちを送ったよ！", 101201, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 513, 2, 0, "えっ？　私とキミとの\n相性？　むむ～…\nうん、バッチリだよ☆", 101201, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 514, 2, 0, "まだこんな時間？\nベッドに行って、\n二度寝しなくちゃ～", 101201, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 515, 2, 0, "適度なお昼寝は\n健康にいいんだって！\n８時間くらいかな？", 101201, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 516, 2, 0, "どんなにお昼寝しても\n夜は眠いんだ～", 101201, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 517, 3, 0, "ん～、また寝癖…\nいっつも\n寝てるからかなぁ？", 101201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 518, 3, 0, "一休み、一休み…\nどうせなら百休み\nくらいしようかな…", 101201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 519, 3, 0, "これ…可愛い！\nシオリンに\n届けてあげたい！", 101201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 520, 3, 0, "ん～…気分爽快！\nそれじゃ、おやすみ～\n…すやぁ…", 101201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 521, 3, 0, "気持ちいい…\nはっ！　ダメダメ！\nまた寝るとこだった！", 101201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 522, 3, 0, "眠くならないように、\n運動しよっと！", 101201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 523, 3, 0, "お腹いっぱいになると\n眠くなるよね～", 101201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 524, 3, 0, "睡眠学習って\n本を読みながら\n寝るんだっけ？", 101201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 526, 1, 0, "う～ん、今なら\nどこでだって\n眠れちゃいそう…", 101202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 527, 1, 0, "もっといっぱい\n超能力使えたら\nいいのにな～", 101202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 528, 1, 0, "ねえねえ、\n今から人助け、\n付き合ってほしいなー", 101202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 529, 1, 0, "ぐっすり眠って\nパワーを\nフルチャージっ☆", 101202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 530, 1, 0, "人助け、頑張ったら\nなんだか、眠く…\n…はっ、ダメダメ！", 101202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 531, 1, 0, "キミと一緒なら、\nなんだって出来る！\n間違いない！", 101202, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 532, 1, 0, "キミを抱き枕に…\nじょ、冗談冗談！", 101202, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 533, 2, 0, "忘れ物しちゃった\nま、いっか！\nえ、ダメ？", 101202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 534, 2, 0, "なに？　困ってる？\nちょうど良かった！\n今は体力満タンだよ☆", 101202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 535, 2, 0, "一緒に\nお出かけしたいなー\nデートしよっ！", 101202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 536, 2, 0, "うんうん、そうだねっ\n困ってる人がいたら\n見過ごせないよねっ！", 101202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 537, 2, 0, "むむ～…どう？\nテレパシーで、私の\n気持ちを送ったよ！", 101202, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 538, 2, 0, "う～だんだん眠く…\nねぇ一緒に寝よ？", 101202, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 539, 2, 0, "キミって早起きだね！\n私は寝ようかな～", 101202, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 540, 2, 0, "適度なお昼寝は\n健康にいいんだって！\n８時間くらいかな？", 101202, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 541, 2, 0, "どんなにお昼寝しても\n夜は眠いんだ～", 101202, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 542, 3, 0, "ん～、また寝癖…\nいっつも\n寝てるからかなぁ？", 101202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 543, 3, 0, "一休み、一休み…\nどうせなら百休み\nくらいしようかな…", 101202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 544, 3, 0, "これ…可愛い！\nシオリンに\n届けてあげたい！", 101202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 545, 3, 0, "ん～…気分爽快！\nそれじゃ、おやすみ～\n…すやぁ…", 101202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 546, 3, 0, "うう～ん、なんだか\nいつもより眠ーく\nなってきたよ…", 101202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 547, 3, 0, "よーし\n困ってる人のために\nパワーアップ☆", 101202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 548, 3, 0, "食べてすぐ寝ると\n牛になるんだっけ？\nまぁ、いいかぁ～", 101202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 549, 3, 0, "睡眠学習って\n本を読みながら\n寝るんだっけ？", 101202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 551, 1, 0, "モデルは楽しいよ！\nファンの子みんな\n応援してくれるしー！", 101601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 552, 1, 0, "今日のおやつは\n駄菓子に決定～！\nいただきまーす！", 101601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 553, 1, 0, "んー、今日のおやつは\n何にしよう？\nやっぱアメ玉かな！", 101601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 554, 1, 0, "ここで撮影会、\nしようかな～♪", 101601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 555, 1, 0, "７かける８はぁ\n７＋７＋７＋７＋…\nん、何個数えたっけ？", 101601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 556, 1, 0, "やば…\nこのままじゃ、うち\nキュン死にかも…", 101601, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 557, 1, 0, "好きなことして\nヒデサイもいて\nうちって超～\nリア充だよね♪", 101601, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 558, 2, 0, "うちの写真撮るの？\nどんなポーズ？", 101601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 559, 2, 0, "ネイルの勉強中～♪\nうまくなったら、\nヒデサイにしてあげる", 101601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 560, 2, 0, "宿題見てくんない？\nひとりじゃムリ～", 101601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 561, 2, 0, "みてこのネイル♪\n超エモくな～い？\nヒデサイもする～？", 101601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 562, 2, 0, "新しい美容液で\n肌まじスベスベ～\nヒデサイも\n触ってみる？", 101601, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 563, 2, 0, "ヒデサイと会ってると\n顔熱くなるんだよね\n熱なんかないのに…", 101601, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 564, 2, 0, "早起きとか\nつらたんだよねー\n昼すぎまで寝てたい", 101601, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 565, 2, 0, "撮影にむけて\nダイエットちうだから\nお昼ぬいてるんだー", 101601, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 566, 2, 0, "お腹すいたー\nでもこの時間に\n食べたら絶対に\n太るやつだよねー", 101601, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 567, 3, 0, "どんなシーンにも合う\nきれいめわがまま\nちゃんとコーデだよ♪", 101601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 568, 3, 0, "ちょっち疲れたかも？\nガン寝しちゃおっかな", 101601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 569, 3, 0, "きゃわたーん☆\n君もおしゃれしよっか", 101601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 570, 3, 0, "いいにおいじゃん！\nこれと同じにおいの\n香水ないかな？", 101601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 571, 3, 0, "あったまるー\nこれ、サイツヨに\n癒しじゃない？", 101601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 572, 3, 0, "あはは、なんか\nウォーキングの練習\nしてるみたーい！", 101601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 573, 3, 0, "体形維持しないと\nいけないけど\nついつい食べちゃう！", 101601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 574, 3, 0, "全然読めない～\nそだ、ヒデサイに\n読んでもらおっと！", 101601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 576, 1, 0, "そろそろ\n仕事の時間だ～\n出かける準備\nしよっと！", 101602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 577, 1, 0, "今日のおやつは\n駄菓子に決定～！\nいただきまーす！", 101602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 578, 1, 0, "ヒデサイを見習って\nうちも少し勉強して\nみよっかな…　", 101602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 579, 1, 0, "体形維持とか\n気にしてるんだよ～", 101602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 580, 1, 0, "７かける８はぁ\n７＋７＋７＋７＋…\nん、何個数えたっけ？", 101602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 581, 1, 0, "やば…\nこのままじゃ、うち\nキュン死にかも…　", 101602, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 582, 1, 0, "ヒデサイと会ってると\n顔熱くなるんだよね\n熱なんかないのに…", 101602, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 583, 2, 0, "うちの写真撮るの？\nどんなポーズ？", 101602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 584, 2, 0, "ネイルの勉強中～♪\nうまくなったら、\nヒデサイに\nしてあげる♪", 101602, 0, 1);
