INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 597, 3, 0, "食べた分は\n消費しないとね～", 101602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 598, 3, 0, "体形維持しないと\nいけないけど\nついつい食べちゃう！", 101602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 599, 3, 0, "全然読めない～\nそだ、ヒデサイに\n読んでもらおっと！", 101602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 601, 1, 0, "やっさー\n踊ってると、どんどん\n楽しくなるさー！", 101701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 602, 1, 0, "このお菓子、でーじ\nおいしいねー！\nキミも食べる？", 101701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 603, 1, 0, "ハイターイ！　元気な\nあいさつが一番さ～！", 101701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 604, 1, 0, "あまった食材があれば\nチャンプルーにして\n食べたいさー！ ", 101701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 605, 1, 0, "さぁ、みんなで歌って\n踊るさ～♪", 101701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 606, 1, 0, "毎日、最高に幸せさ～\nキミのおかげだねー", 101701, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 607, 1, 0, "キミへの気持ちを\n踊りで表現\nしてみるさ～", 101701, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 608, 2, 0, "海でひと泳ぎ\nしたいさ～\nキミもどう？", 101701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 609, 2, 0, "あぎひゃっ！\n急に声かけたら、\nビックリするさ～", 101701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 610, 2, 0, "踊りなら、\n私が手とり足とり\n教えてあげるさ～！", 101701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 611, 2, 0, "空手の型の\n練習するさー\nキミもどう？", 101701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 612, 2, 0, "よ～し、手をつないで\n踊るさ～！\n恥ずかしがること\nないさ～", 101701, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 613, 2, 0, "キミと一緒だから、\n毎日楽しいこと\nばっかりさー", 101701, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 614, 2, 0, "ハイターイ！\n今日も一日、\nがんばるさー！", 101701, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 615, 2, 0, "午後も頑張るために\nお昼ご飯は\nてびちにするさー！", 101701, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 616, 2, 0, "故郷では見えない星が\n見られるんだねえ。\nなんか不思議さぁ", 101701, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 617, 3, 0, "夢中で踊ってたら、\n髪の毛ぼさぼさに\nなってるさ～！？", 101701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 618, 3, 0, "よく食べてよく踊って\nよく休む。\nそれが元気の秘訣さ～", 101701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 619, 3, 0, "ちゅらかーぎーねー！\nキミはどこから\n来たのー？", 101701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 620, 3, 0, "いい香り～、って\n私もカオリだったさ！\nなんだか照れるね☆", 101701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 621, 3, 0, "故郷はあんまり暖房を\n使わなかったから、\n新鮮さー！", 101701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 622, 3, 0, "汗をかくのは\n気持ちいいさー！\nどんどん行くよー！", 101701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 623, 3, 0, "うーん、おいしいさ～\nゴーヤっぽいものを\n混ぜたいさ～", 101701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 624, 3, 0, "おおー、故郷の民話！\nこんなとこで見るとは\n懐かしいさぁ～！", 101701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 626, 1, 0, "焦らなくても\nなんくるないさ～\nなるようになるさ～", 101702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 627, 1, 0, "さぁ、みんなで歌って\n踊るさ～♪", 101702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 628, 1, 0, "元気がないときは\n私のところに来るさー", 101702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 629, 1, 0, "あまった食材があれば\nチャンプルーにして\n食べたいさー！ ", 101702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 630, 1, 0, "ノリノリな音楽と\n踊りで、疲れを\n吹き飛ばすさ～！", 101702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 631, 1, 0, "毎日、最高に幸せさ～\nキミのおかげだねー", 101702, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 632, 1, 0, "幸せすぎて踊らずには\nいられないさ～", 101702, 0, 8);
