INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 637, 2, 0, "キミと踊ってる時が\n一番幸せさ～\nキミもそう思ってたら\nうれしいさ～", 101702, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 638, 2, 0, "海に沈む夕日は\nとっても綺麗さ～！\n今度一緒に見ようね～ ", 101702, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 639, 2, 0, "気持ちのいい朝さー！\nひとっ走り\nしてくるさー！", 101702, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 640, 2, 0, "午後も頑張るために\nお昼ご飯は\nてびちにするさー！ ", 101702, 2, 1);
