INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 642, 3, 0, "夢中で踊ってたら\n髪の毛ぼさぼさに\nなってるさ～！？", 101702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 643, 3, 0, "よく食べてよく踊って\nよく休む\nそれが元気の秘訣さ～", 101702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 644, 3, 0, "ちゅらかーぎーねー！\nキミはどこから\n来たのー？ ", 101702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 645, 3, 0, "いい香り～、って\n私もカオリだったさ！\nなんだか照れるね☆ ", 101702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 646, 3, 0, "故郷はあんまり暖房を\n使わなかったから\n新鮮さー！", 101702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 647, 3, 0, "徹夜で踊り続ける\nためにも、まずは\n体を鍛えるさ～", 101702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 648, 3, 0, "もぐもぐ…もっと\nおかわりがほしいさー", 101702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 649, 3, 0, "おおー、故郷の民話！\nこんなとこで見るとは\n懐かしいさぁ～！ ", 101702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 651, 1, 0, "はあ…\nすてきな小説だった～\nキュンキュンしちゃう", 101801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 652, 1, 0, "このお部屋\n女の子ばかりね。\n魔族の女子校を\n思い出しちゃう♪", 101801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 653, 1, 0, "次のテスト\n恋愛小説から\n出題したら\nダメかしら…？", 101801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 654, 1, 0, "キンコンカンコ～ン♪\n授業をはじめま～す", 101801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 655, 1, 0, "はー…\nちょっと肩が\nこっちゃった…", 101801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 656, 1, 0, "私、あの子のこと\n無意識に特別扱い\nしちゃってるかな…", 101801, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 657, 1, 0, "物語だと生徒が先生を\n好きになるのにな…\n逆の場合は…", 101801, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 658, 2, 0, "あん！　見ちゃダメよ\n明日のテストの問題を\n作ってるとこなの！", 101801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 659, 2, 0, "勉強勉強で\n疲れたよね？\n膝枕してあげる！", 101801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 660, 2, 0, "テストがんばったね～\nご褒美に先生が\n特別な授業\nしてあげるね？", 101801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 661, 2, 0, "学院の外でも\n私は君の先生よ\nなんでも聞いてね♪", 101801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 662, 2, 0, "今読んでるのは\nヒロインが年下の子と\n恋に落ちる話…あ、\n深い意味はないのよ～", 101801, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 663, 2, 0, "生徒は全員可愛いけど\n君は少し特別…\nみんなには内緒よ？", 101801, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 664, 2, 0, "学校に\n遅刻しないように\n早く準備しないとね", 101801, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 665, 2, 0, "男の子といっしょに\nお弁当を食べるの、\n夢なんだ～", 101801, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 666, 2, 0, "明日の予習はやった？\n先生も手伝うから\n寝る前にやりましょ♪", 101801, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 667, 3, 0, "もしかして…\n少し露出多いかしら？", 101801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 668, 3, 0, "リラックスすると\nα波が出て、勉強が\nはかどるんですって", 101801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 669, 3, 0, "かわいい♪　あなたも\n私の生徒になる？", 101801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 670, 3, 0, "学院にもこういう\n安らぐ設備が\n必要かしらね？", 101801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 671, 3, 0, "まだ寒いわぁ\n誰か隣に\n来てくれたら…", 101801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 672, 3, 0, "運動部の顧問も\nできるかしら？", 101801, 0, 1);
