INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 677, 1, 0, "このお部屋\n女の子ばかりね\n魔族の女子校を\n思い出しちゃう♪", 101802, 0, 1);
