INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 679, 1, 0, "はあ…\nすてきな小説だった～\nキュンキュンしちゃう", 101802, 0, 1);
