INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 690, 2, 0, "午後の授業って\n眠いでしょう？\n気持ちは分かるけど\nがんばってね♪", 101802, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 691, 2, 0, "明日の予習はやった？\n先生も手伝うから\n寝る前にやりましょ♪", 101802, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 692, 3, 0, "もしかして…\n少し露出多いかしら？", 101802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 693, 3, 0, "私の膝枕と、どっちが\nフカフカかしら？", 101802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 694, 3, 0, "可愛いわね～\n教室に置いたら\n生徒が喜ぶかしら？　", 101802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 695, 3, 0, "は～落ち着くなぁ～\n教師には\nこういうゆとりも\n必要よね♪　", 101802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 696, 3, 0, "まだ寒いわぁ\n誰か隣に\n来てくれたら…　", 101802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 697, 3, 0, "運動部の顧問も\nできるかしら？　", 101802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 698, 3, 0, "あん！\nお胸にこぼしちゃった", 101802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 699, 3, 0, "あぁーん♪\nこういう物語みたいな\n恋愛あこがれる～　", 101802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 701, 1, 0, "おにいちゃんは、\nにんじん食べられる？", 102001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 702, 1, 0, "えへへ、\nお絵かきしよ～♪", 102001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 703, 1, 0, "あのね、ミミと\nあそんでほしいな～", 102001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 704, 1, 0, "さっきね、きれいな\nお花見つけたよ。\nおにいちゃんにも\n見せてあげたいな～", 102001, 0, 1);
