INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 751, 1, 0, "はぅぅ…\n部屋のどこにいたら\nいいんだろう…", 102101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 752, 1, 0, "お外より\nお部屋の中の方が\n落ち着くな…", 102101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 753, 1, 0, "クッキー焼いたら\nみんな食べてくれる\nかなぁ…", 102101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 754, 1, 0, "あ、あめんぼあかいな\nあいうえお！", 102101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 755, 1, 0, "お芝居には\n人間観察が大事…\nじーーーーーー…", 102101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 756, 1, 0, "お兄ちゃんを見ると、\n顔が赤くなっちゃうの\n…な、なんでかな？", 102101, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 757, 1, 0, "れ、恋愛劇…\nお、お兄ちゃんと\n練習…", 102101, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 758, 2, 0, "こ、この本、とっても\nおもしろいんですよ♪", 102101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 759, 2, 0, "い、一緒に遊んで\nくれるんですか？\nや、やったぁ～♪", 102101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 760, 2, 0, "ランドソルには\n慣れましたか？\nわ、私はまだ…", 102101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 761, 2, 0, "お、お兄ちゃん、\nお、お話の練習に\n付き合って欲しいです", 102101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 762, 2, 0, "リボンがかわいい？\nほ、ほぇぇぇ～～\nは、恥ずかしいですぅ", 102101, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 763, 2, 0, "お兄ちゃん…♪\nえへへ、呼んでみた\nだけです…♪", 102101, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 764, 2, 0, "今日は一日、\n恥ずかしがらずに\n過ごせるかなあ…？", 102101, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 765, 2, 0, "お昼ご飯、どうぞ♪\nわ、私、お料理、\nうまくなったかな？", 102101, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 766, 2, 0, "おにいちゃんが\n帰ってくるまで、\nがんばって\n起きてました", 102101, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 767, 3, 0, "可愛い女の子に変身！\n出来ますように…♪", 102101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 768, 3, 0, "ふかふかで\nあったかくて…\nお兄ちゃんみたい♪", 102101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 769, 3, 0, "この子を相手に、\n演技の練習～", 102101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 770, 3, 0, "リボンや服に\n染み込ませておこう。\nいつでもいい匂い♪", 102101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 771, 3, 0, "えへへ。\nぽっかぽかです…", 102101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 772, 3, 0, "はぁはぁ…\nふひぃ…\nはうぅぅぅ～～", 102101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 773, 3, 0, "もぐもぐ…えへへ、\n美味しいものを\n食べてると幸せです", 102101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 774, 3, 0, "戯曲を読んで\n自分が演じるところを\n想像するの…", 102101, 0, 1);
