INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 777, 1, 0, "ふぁ～、この部屋は\n時間がゆったりと\n流れてるみたい", 102102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 778, 1, 0, "この台本、感動…\n涙が止まらないよぉ\nいつか演じてみたいな", 102102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 779, 1, 0, "お芝居には\n人間観察が大事…\nじーーーーーー…", 102102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 780, 1, 0, "あえいうえおあお\nかけきくけ、かっ…\nむ、難しいですぅぅ", 102102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 781, 1, 0, "お、お腹が空いた時は\n言ってくださいね\nが、頑張って\n作ります！", 102102, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 782, 1, 0, "れ、恋愛劇…\nお、お兄ちゃんと\n練習…", 102102, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 783, 2, 0, "こ、この本、とっても\nおもしろいんですよ♪", 102102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 784, 2, 0, "お兄ちゃんと\nいっぱい話せた日は、\n嬉しくなっちゃいます", 102102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 785, 2, 0, "何か私にできる事\n…そうだ！\nお掃除しますね！", 102102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 786, 2, 0, "私、ちょっとずつ、\n自分に自信が\nついてきたかもです…", 102102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 787, 2, 0, "リボンがかわいい？\nほ、ほぇぇぇ～～\nは、恥ずかしいですぅ", 102102, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 788, 2, 0, "お兄ちゃん…♪\nえへへ、呼んでみた\nだけです…♪", 102102, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 789, 2, 0, "おはようございます♪\n声出しして、お芝居の\n練習しなくちゃ", 102102, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 790, 2, 0, "お茶にしますか？\nクッキー焼いたので、\nよかったら…", 102102, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 791, 2, 0, "寝る前に\n台本を読むと、\n覚えやすいんです", 102102, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 792, 3, 0, "可愛い女の子に変身！\n出来ますように…♪", 102102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 793, 3, 0, "はぅ…\nフッカフカァ…\n癒されちゃいます…", 102102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 794, 3, 0, "この子を相手に\n演技の練習～", 102102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 795, 3, 0, "リボンや服に\n染み込ませておこう\nいつでもいい匂い♪", 102102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 796, 3, 0, "えへへ\nぽっかぽかです…", 102102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 797, 3, 0, "え、演劇には\nた、体力も重要\nだから～", 102102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 798, 3, 0, "残さず\n食べますね…！", 102102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 799, 3, 0, "戯曲を読んで\n自分が演じるところを\n想像するの…", 102102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 801, 1, 0, "ねえ、人見知りを\n治すための練習に\n付き合ってくれない？", 102201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 802, 1, 0, "アカリみたいに\nかわいくなるには、\nどうすればいいのかな", 102201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 803, 1, 0, "レアカードをゲット！\nねえねえ、\nすごいでしょ！？", 102201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 804, 1, 0, "ア、アイツと同じ\n部屋にいるのよね…\nって、だから何よ！？", 102201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 805, 1, 0, "楽しいゲームって\nやめどきが\n見つからないわ", 102201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 806, 1, 0, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？", 102201, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 807, 1, 0, "うぅ…最近、\nアイツを見るとなんか\nドキドキするのよね", 102201, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 808, 2, 0, "ゲームに負けた罰\nだからって、\nくすぐるのは…\nあはは…ダメだって！", 102201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 809, 2, 0, "アンタと話すのは割と\n緊張しないのよね\n…なんでかな", 102201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 810, 2, 0, "待ちきれない…\n新作ゲームの発売日が\n待ちきれない…", 102201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 811, 2, 0, "アンタは人見知り\nしないタチよね？\nいいなあ～…", 102201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 812, 2, 0, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！", 102201, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 813, 2, 0, "アンタと…そ、その\nこうして話してるのも\nわわ、悪くないわね！", 102201, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 814, 2, 0, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！", 102201, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 815, 2, 0, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！", 102201, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 816, 2, 0, "夜はこれからよ！\n朝まで\nゲームするからね！", 102201, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 817, 3, 0, "アカリみたいに\n可愛くなるのは\n難しいかな", 102201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 818, 3, 0, "寝っ転がりながら\nゲームができれば\n気分が良さそう", 102201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 819, 3, 0, "可愛い…けど、\n私には似合わないかな", 102201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 820, 3, 0, "はあ、リラックスして\nゲームができるわ", 102201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 821, 3, 0, "ん…暖かくて、\n寝落ちしちゃいそう", 102201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 822, 3, 0, "ゲーム感覚でやると、\n体動かすのも\n楽しいわね", 102201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 823, 3, 0, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ", 102201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 824, 3, 0, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 102201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 826, 1, 0, "さあ、今日は\n何のゲームで\n遊ぼうかな？", 102202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 827, 1, 0, "一緒にゲームができる\n人がいるっていうのも\n新鮮だな…", 102202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 828, 1, 0, "レアカードをゲット！\nすごいでしょ！？", 102202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 829, 1, 0, "アカリってどうやって\n友達作ってるんだろ？\nコツがあるのかな…", 102202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 830, 1, 0, "楽しいゲームって\nやめどきが\n見つからないわ", 102202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 831, 1, 0, "うぅ…最近\nアイツを見るとなんか\nドキドキするのよね", 102202, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 832, 1, 0, "ね、ねえ\nもう少しその…\n一緒にゲームしない？", 102202, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 833, 2, 0, "ねえ、人見知りを\n治すための練習に\n付き合ってくれない？", 102202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 834, 2, 0, "欲しいゲームが\n見つからなくて…\n探すの手伝って！", 102202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 835, 2, 0, "ねえ、見て！\n新しいゲームを\n買って来たの！", 102202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 836, 2, 0, "ゲームに負けた罰\nだからって、くすぐる\nのは…あ、あはは！\nだ、ダメだって！", 102202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 837, 2, 0, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！", 102202, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 838, 2, 0, "あ、あのさ…\nもう少し、そこに\nいてくれてもいいわよ", 102202, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 839, 2, 0, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！", 102202, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 840, 2, 0, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！", 102202, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 841, 2, 0, "夜はこれからよ！\n朝まで\nゲームするからね！", 102202, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 842, 3, 0, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？", 102202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 843, 3, 0, "ふああ…\n徹夜明けには\n辛い誘惑だな…", 102202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 844, 3, 0, "可愛い…けど、\n私には似合わないかな", 102202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 845, 3, 0, "こういうのに詳しいと\n女の子っぽく\n見えたりするのかな", 102202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 846, 3, 0, "寒い日は\nここでゲームするのが\n最高のぜいたくよね", 102202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 847, 3, 0, "最近ゲームばかりだし\nたまにはいいかな？", 102202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 848, 3, 0, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ", 102202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 849, 3, 0, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 102202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 851, 1, 0, "じゃんけんぽん！\nあっち向いてほい！\nあ～ん、負けたぁ\n　『勝ったぜ！』", 102301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 852, 1, 0, "ぷうきちがいれば、\nなんでも出来ちゃうよ", 102301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 853, 1, 0, "発声練習しよっと\nぷうきち\nフルスイ～ング！", 102301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 854, 1, 0, "ここで、ぷうきち\nフルスイングは、\nまずいかな～？", 102301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 855, 1, 0, "この部屋、ぷうきちを\n置く場所はないのかな", 102301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 856, 1, 0, "お兄ちゃんって…\nちょっとぷうきちに\n似てるかも♪", 102301, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 857, 1, 0, "お兄ちゃんにも、\nぷうきちのこと\n好きになって\nもらいたいな～", 102301, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 858, 2, 0, "おやつは、\n私とぷうきちの\n二人分用意してね♪", 102301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 859, 2, 0, "ぷうきちが、お腹\nペコペコだって～\n何かちょうだい！", 102301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 860, 2, 0, "『アヤネに手出したら\n…坊ちゃんでも、\n分かってんな？』", 102301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 861, 2, 0, "ぷうきちとの\n連携を磨いてるの！\nもっと仲良くなるよ！", 102301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 862, 2, 0, "特別だからね？\nもっと触っていいよ\n…ぷうきちのこと", 102301, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 863, 2, 0, "お兄ちゃんのこと、\n「ぷう兄」って\n呼ぼうかな？", 102301, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 864, 2, 0, "お兄ちゃんって\n朝早いんだね～\n私はまだ眠いよ～", 102301, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 865, 2, 0, "ふぁ～…ぷうきちと\nお昼寝してくるね…", 102301, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 866, 2, 0, "ぷうきちと\nおしゃべりしてたら\nもうこんな時間…", 102301, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 868, 3, 0, "『座り心地がいいな』\nぷうきちは座れない\nでしょ？", 102301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 869, 3, 0, "かわいい～！\nぷうきちのお友達に\nしちゃおうかな？", 102301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 870, 3, 0, "いいニオイ～♪\nぷうきちも\n気に入ったみたい！", 102301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 871, 3, 0, "ぽわぽわする～\nぷうきちも\n気持ちいい？", 102301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 873, 3, 0, "おいしい～\nぷうきちも\n食べてみる？", 102301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 874, 3, 0, "ぷうきち！\nこの本、読んで～！", 102301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 901, 1, 0, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 102501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 902, 1, 0, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 102501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 903, 1, 0, "どうしたら転ばなく\nなるんでしょうか？", 102501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 904, 1, 0, "わわわわ～～っ\nふう…\nきゃ、きゃあっ！", 102501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 905, 1, 0, "私のドジで、\nこのお部屋を\n壊しちゃったら\nどうしましょう～～", 102501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 906, 1, 0, "おいしい料理で\nお出迎えしなくちゃ！", 102501, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 907, 1, 0, "一生懸命\nご奉仕しますよ～♪", 102501, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 908, 2, 0, "耳かきしましょうか？\nえ？　こわい？", 102501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 909, 2, 0, "お疲れでしたら、\nお昼寝しませんか？\n膝枕もしますよ？", 102501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 910, 2, 0, "おかえりなさいませ\nご主人さま♪", 102501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 911, 2, 0, "すみません\nすみません\nすみません～～", 102501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 912, 2, 0, "メイドの私が、\nこんな気持ちを持って\nいいんでしょうか…", 102501, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 913, 2, 0, "私のこと、\n専属メイドと思って\n頼ってくださいね", 102501, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 914, 2, 0, "おはようございます♪\n今日も一日がんば…\nきゃ、きゃあ！", 102501, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 915, 2, 0, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！", 102501, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 916, 2, 0, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした", 102501, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 917, 3, 0, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ", 102501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 918, 3, 0, "休憩が終わったら\nお洗濯にお料理に\nお掃除もしないと！", 102501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 919, 3, 0, "かわいい♪\nあとでお洗濯\nしてあげますね", 102501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 920, 3, 0, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです", 102501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 921, 3, 0, "ここでお洗濯ものを\n乾かしましょうか", 102501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 922, 3, 0, "あわわわっ…\nこ、転びそうです～！", 102501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 923, 3, 0, "これくらい\nおいしいものを\n作ってみたいです～", 102501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 924, 3, 0, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 102501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 926, 1, 0, "失敗したら、この\nお部屋追い出され\nちゃうかも～？", 102502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 927, 1, 0, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 102502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 928, 1, 0, "どうやったら、立派な\nメイドになれるのかな", 102502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 929, 1, 0, "塩にはＳマーク\n砂糖にはＳマーク\n…あ、あれ！？", 102502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 930, 1, 0, "ご用があれば\nなんでも言って\nくださいね", 102502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 931, 1, 0, "迷惑をかけすぎて…\n嫌われちゃったら\nどうしましょう", 102502, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 932, 1, 0, "メイドの私が\nこんな気持ちを持って\nいいんでしょうか…　", 102502, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 933, 2, 0, "おでかけですか？\nいってらっしゃいませ\nご主人さま♪", 102502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 934, 2, 0, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 102502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 935, 2, 0, "私、またなにか\nやっちゃいましたか？", 102502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 936, 2, 0, "おいしいお茶っ葉が\n手に入りました～♪\n一緒に飲みませんか？", 102502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 937, 2, 0, "転んだふりであの人に\n…って！\nなんでもないですよ！", 102502, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 938, 2, 0, "私、できることなら\nなんでもやりますよ\nあなたのためですから", 102502, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 939, 2, 0, "おはようございます♪\n今日も一日がんば…\nう、うわあ！", 102502, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 940, 2, 0, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！", 102502, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 941, 2, 0, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした", 102502, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 942, 3, 0, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ", 102502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 943, 3, 0, "いい座り心地です～\nこれならお嬢さまも\nゆったりできますね♪", 102502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 944, 3, 0, "かわいい♪\nあとでお洗濯\nしてあげますね", 102502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 945, 3, 0, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです", 102502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 946, 3, 0, "暖かいですけど\n…私が火に近づいて\n大丈夫でしょうか！？", 102502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 947, 3, 0, "運動しようとすると\nいつも怪我するん\nですよね…", 102502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 948, 3, 0, "こんなに美味しいもの\nメイドの私には\nもったいないです～！", 102502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 949, 3, 0, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 102502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 951, 1, 0, "あの方と私だけの\n部屋にしたいですわ", 102701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 952, 1, 0, "あの方は私のもの…\n眼も耳も鼻も口も髪も\n首も胸も腰も手も脚も\nぬくもりも心も…！", 102701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 953, 1, 0, "運命とは決して\n抗えないもの…\nクスクス…クスクス", 102701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 954, 1, 0, "部屋からあの方の\n香りが…\nああ、至福ですわ", 102701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 955, 1, 0, "毒にも薬にもならない\n存在なんて嫌ですわ\nどうせならとびきりの\n毒でありたい…", 102701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 956, 1, 0, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…", 102701, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 957, 1, 0, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…", 102701, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 958, 2, 0, "運命を信じますか？\nもちろん絶対当然\n信じますわよね？", 102701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 959, 2, 0, "この薬は起床時就寝前\n食前食後食間に、毎日\n服用してください。\n惚れ薬ですわ", 102701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 960, 2, 0, "お願いがあるのです。\n新しい研究の実験台に\n…", 102701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 961, 2, 0, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…", 102701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 962, 2, 0, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…", 102701, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 963, 2, 0, "私の身も心も、全てが\nあなた様のもの。\nあなた様の全ても…", 102701, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 964, 2, 0, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ", 102701, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 965, 2, 0, "お昼を用意しましたの\n何も入れていませんわ\nクスクス…", 102701, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 966, 2, 0, "あなた様が眠るまで\nお傍で見守りますわ。\n…あの、どうして\n眠らないのですか？", 102701, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 967, 3, 0, "いっそ鏡の中に\n閉じ込めてしまえたら\n私だけのものに…", 102701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 968, 3, 0, "はっ！\nリラックスしたら\n閃きましたわ！", 102701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 969, 3, 0, "あら、キメラの素体に\nぴったりですわ", 102701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 970, 3, 0, "このお部屋は\n良い香りがしますわ。\nはっ、あなた様の匂い\nなのでは…！？", 102701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 971, 3, 0, "あたたかい…\n加熱調合にも\n使えそうですわね", 102701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 972, 3, 0, "この運動エネルギー\n何かにうまく\n利用できないかしら？", 102701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 973, 3, 0, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…", 102701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 974, 3, 0, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ", 102701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 976, 1, 0, "あの方と私だけの\n部屋にしたいですわ", 102702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 977, 1, 0, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…", 102702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 978, 1, 0, "新しい薬\nどうやってあの方に\n飲ませようかしら", 102702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 979, 1, 0, "部屋からあの方の\n香りが…\nああ、至福ですわ", 102702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 980, 1, 0, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…", 102702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 981, 1, 0, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…", 102702, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 982, 1, 0, "魔法も科学も錬金術も\n全て駆使して\nあの方のために…", 102702, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 983, 2, 0, "この部屋には雌犬…\n失礼、女の方がとても\n沢山いますのね", 102702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 984, 2, 0, "あなた様には私の伴侶\nとして高い教養を…\nあ、逃げられてしまい\nましたわ…！", 102702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 985, 2, 0, "お願いがあるのです\n新しい研究の\n実験台に…", 102702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 986, 2, 0, "あなた様が寝ている時\n私は…クスクス\nこれ以上は言えません", 102702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 987, 2, 0, "想像の中のあなた様は\nすっかり私の虜なのに\n現実は…\nもどかしいですわね", 102702, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 988, 2, 0, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…", 102702, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 989, 2, 0, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ", 102702, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 990, 2, 0, "さあ、お食事ですよ\n味は保証しますわ\n特製ソースで少しだけ\n体が痺れますけど…", 102702, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 991, 2, 0, "あなた様が眠るまで\nお傍で見守りますわ\n…あの、どうして\n眠らないのですか？", 102702, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 992, 3, 0, "私ももう少し\n外見に気を配る\nべきでしょうか…", 102702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 993, 3, 0, "はっ！\nリラックスしたら\n閃きましたわ！", 102702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 994, 3, 0, "この私を差し置いて\nあなた様に媚びるなど\n言語道断…っ！", 102702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 995, 3, 0, "このお部屋は\n良い香りがしますわ\nはっ、あなた様の匂い\nなのでは…！？", 102702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 996, 3, 0, "素敵なぬくもり…\nまるであの方に\n抱きしめられたような", 102702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 997, 3, 0, "この運動エネルギー\n何かにうまく\n利用できないかしら？", 102702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 998, 3, 0, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…", 102702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 999, 3, 0, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ", 102702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1001, 1, 0, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね", 102801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1002, 1, 0, "使えるものは\n再利用しないと\n…あ、これとか\nまだ使えるわね", 102801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1003, 1, 0, "贅沢は敵！　とはいえ\n気持ちに余裕のない\n暮らしはダメよね", 102801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1004, 1, 0, "お腹空いたわね、\n残り物で\n何か作ろうかしら", 102801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1005, 1, 0, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの", 102801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1006, 1, 0, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪", 102801, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1007, 1, 0, "焼きそばパンが\n食べたいな～\nあいつと…", 102801, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1008, 2, 0, "ねぇあんた、どこか\n安いお店知らない？", 102801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1009, 2, 0, "あたし、エレガントに\n振る舞えてる？\n…そう？　嬉しい♪", 102801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1010, 2, 0, "いっぱい働いて遊んで\nよく休んでまた働く。\nそれが健全な生活よね", 102801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1011, 2, 0, "一緒に部屋の\n整頓でもする？\nハマると楽しいわよ♪", 102801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1012, 2, 0, "あんたがいると\n子供たちも喜ぶわ♪\nもちろんあたしもね！", 102801, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1013, 2, 0, "ね、あんた今、幸せ？\n何びっくりしてるのよ\n大事なことじゃないの", 102801, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1014, 2, 0, "面倒な用事は午前中に\n片づけてしまうのが、\n時間の使い方のコツよ", 102801, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1015, 2, 0, "午後のティータイム\nとかどう？\n良いカフェがあるの♪", 102801, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1016, 2, 0, "さ、エレガントな夜を\n楽しみましょ。\nモダンな音楽でも\nかけましょうか？", 102801, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1017, 3, 0, "レディたるもの\n身嗜みはしっかりとね\n見本にならないと♪", 102801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1018, 3, 0, "これで仕事は一段落ね\nちょっと休憩…", 102801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1019, 3, 0, "うふふ、\nかわいいものを\n見るのは幸せね～", 102801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1020, 3, 0, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪", 102801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1021, 3, 0, "風邪を引かないように\n身体を温めなくちゃ", 102801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1022, 3, 0, "さっ！　家の中でも\n効率的に\nトレーニングよ！", 102801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1023, 3, 0, "もぐもぐ…はっ、\n口の周り、\n汚れてないわよね？", 102801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1024, 3, 0, "知識から過去を学んで\n未来に活かすわよ", 102801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1026, 1, 0, "安売りのお店の情報\n整理しないと…", 102802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1027, 1, 0, "どんなに苦労してても\nそう見せないのが真の\nエレガントなレディよ", 102802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1028, 1, 0, "安く済んであいつが\n喜ぶ料理とか…\nないかしら", 102802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1029, 1, 0, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね", 102802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1030, 1, 0, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの", 102802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1031, 1, 0, "焼きそばパンが\n食べたいな～\nあいつと…", 102802, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1032, 1, 0, "あいつと一緒に…\n色々なところに\nいきたいな", 102802, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1033, 2, 0, "今度買い物に\n付き合いなさい\nセールがあるのよ", 102802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1034, 2, 0, "あんたがいると子供\nたちも喜ぶわ♪\nもちろんあたしもね♪", 102802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1035, 2, 0, "いっぱい働いて遊んで\nよく休んでまた働く\nそれが健全な生活よね", 102802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1036, 2, 0, "あんたとこうして\nのんびりするのも\nいいものね", 102802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1037, 2, 0, "ねえ、今度遊びに\n行きたいな\nもちろん一緒に、よ", 102802, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1038, 2, 0, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪", 102802, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1039, 2, 0, "面倒な用事は午前中に\n片づけてしまうのが\n時間の使い方のコツよ", 102802, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1040, 2, 0, "ランチにして午後の\n仕事に備えましょ！\nやることは一杯よ♪", 102802, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1041, 2, 0, "ふわあ～\nもうこんな時間\n子供たちは\nもう寝たかしら", 102802, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1042, 3, 0, "忙しい時こそ\n身だしなみは隙なく\nぴしっとね！", 102802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1043, 3, 0, "これで仕事は一段落ね\nちょっと休憩…", 102802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1044, 3, 0, "ふふふ\nずっと眺めてたく\nなっちゃうわね", 102802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1045, 3, 0, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪", 102802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1046, 3, 0, "風邪を引かないように\n身体を温めなくちゃ", 102802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1047, 3, 0, "さっ！　家の中でも\n効率的に\nトレーニングよ！", 102802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1048, 3, 0, "しっかり食べて\n栄養補給、栄養補給！", 102802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1049, 3, 0, "知識から過去を学んで\n未来に活かすわよ", 102802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1051, 1, 0, "可愛い子が\nいっぱいいるなぁ…\n誰か私とユニット\n組んでくれないかな？", 102901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1052, 1, 0, "今度のライブ会場、\nいつもより大きいから\n楽しみなんだ～！", 102901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1053, 1, 0, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～", 102901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1054, 1, 0, "彼とデュエット\nしてみたいな～♪", 102901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1055, 1, 0, "キラキラ無病息災、\nドキドキ一日一善…\n作詞って難しい～", 102901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1056, 1, 0, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 102901, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1057, 1, 0, "写真集見なくても、\n本物がここに\nいるのにな～", 102901, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1058, 2, 0, "握手会の練習、\n付き合ってくれない？", 102901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1059, 2, 0, "写真集のポーズ\n練習中～！\nど、どうかな？", 102901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1060, 2, 0, "何て書いてあるか\n読めないって？\nもー！\nこれは私のサイン！", 102901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1061, 2, 0, "ね、次のライブ\n観に来てくれる？\nいい席とっとくから！", 102901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1062, 2, 0, "ねぇ、台本読むの\nつきあってくれる？\n恋人役に\nなってほしいんだ", 102901, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1063, 2, 0, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ。\nどこかは秘密だけど♪", 102901, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1064, 2, 0, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 102901, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1065, 2, 0, "この時間は\nお腹すくけど…\n水着撮影があるから\nお昼抜いてるんだ", 102901, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1066, 2, 0, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ。", 102901, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1067, 3, 0, "身だしなみオッケー！\nアイドルはいつでも\n完璧でいなくちゃ！", 102901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1068, 3, 0, "ふぅ…\nこんな姿、ファンには\n見せられないな～", 102901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1069, 3, 0, "かわいい～♪\nこれは強力な\nライバル登場かな！", 102901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1070, 3, 0, "ステキな香り～♪\n歌詞のイメージが\n浮かんできそう！", 102901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1071, 3, 0, "体も暖まったし！\nダンスレッスンに\n行こうかな♪", 102901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1072, 3, 0, "ベストタイム更新！\nライブに向けて\n体力つけないと…", 102901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1073, 3, 0, "太っちゃったら\nどうしよう…？\nでも手が止まらない～", 102901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1074, 3, 0, "ステキなお話…\n新曲の作詞の\n参考になりそう♪", 102901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1076, 1, 0, "このお部屋すてき！\nファンに追われた時の\n隠れ家にピッタリね！", 102902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1077, 1, 0, "そろそろイメチェン\nしてみようかな…\nそれともこの路線で\nもう少し…？", 102902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1078, 1, 0, "みんながくれた\nファンレター…\n私の大事な宝物…", 102902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1079, 1, 0, "彼とデュエット\nしてみたいな～♪", 102902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1080, 1, 0, "疾風怒濤、威風堂々\n獅子奮迅、唯我独尊…\nラブソングの作詞って\n難しいな～", 102902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1081, 1, 0, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 102902, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1082, 1, 0, "トップアイドルの\nさらに先…\n彼とならその景色も\n見られるかな", 102902, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1083, 2, 0, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～", 102902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1084, 2, 0, "アイドルノゾミだけ\nじゃなくて、私の事も\nちゃんと見てね？", 102902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1085, 2, 0, "キミに響かないなら\nファンにも響くわけ\nないよ！　練習練習！", 102902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1086, 2, 0, "衣装のスカートは\n鉄壁なんだよ！\n残念でした～", 102902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1087, 2, 0, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ\nどこかは秘密だけど♪", 102902, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1088, 2, 0, "ねぇ、私がトップ\nアイドルになっても\nちゃんと隣にいてね\n遠くに行かないでね？", 102902, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1089, 2, 0, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 102902, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1090, 2, 0, "お昼ごはん行かない？\n誰にも知られてない\nお店があるんだけど！", 102902, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1091, 2, 0, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ", 102902, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1092, 3, 0, "身だしなみオッケー！\nアイドルはいつでも\n完璧でいなくちゃ！", 102902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1093, 3, 0, "ふぅ…\nこんな姿、ファンには\n見せられないな～", 102902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1094, 3, 0, "かわいい～♪\nこれは強力な\nライバル登場かな！", 102902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1095, 3, 0, "みんなのイメージする\nアイドルの香りは\nこんな感じかな～", 102902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1096, 3, 0, "体も暖まったし！\nダンスレッスンに\n行こうかな♪", 102902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1097, 3, 0, "ベストタイム更新！\nライブに向けて\n体力つけないと…", 102902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1098, 3, 0, "太っちゃったら\nどうしよう…？\nでも手が止まらない～", 102902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1099, 3, 0, "この恋愛小説ステキ！\n私もアイドルじゃ\nなかったら…", 102902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1101, 1, 0, "手裏剣の数が\n合わないデス！\nどこかに落とした\nデスか…？", 103001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1102, 1, 0, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 103001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1103, 1, 0, "金は天下の\nマワリモノ！\nところで、\nどういう意味デス？", 103001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1104, 1, 0, "むむ、アクダイカンの\n気配がするデース", 103001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1105, 1, 0, "クニトリ合戦、\n楽しみデース！", 103001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1106, 1, 0, "ショーグンと\n出会えて、\nワタシは果報者デス！", 103001, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1107, 1, 0, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 103001, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1108, 2, 0, "なんでも\n命令するデス！\nショーグンの命は\n絶対デス！", 103001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1109, 2, 0, "あっ、ショーグン、\n出陣はいつで\nゴザルデス！？", 103001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1110, 2, 0, "さ、さすが\nショーグンデス…\nいつの間に背後を！？", 103001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1111, 2, 0, "出陣の合図に\n備えるデス！\nほら貝は\nどこデスか！？", 103001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1112, 2, 0, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 103001, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1113, 2, 0, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 103001, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1114, 2, 0, "おはようデス！\n今日も一日\n忍法日和デス！", 103001, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1115, 2, 0, "そろそろお腹\n空いてきたデス。\n昼ごはんにするデス！", 103001, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1116, 2, 0, "明日もきっと\nいい日デスよ、\nショーグン！", 103001, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1117, 3, 0, "ちょんまげに\nしてみるのも\n面白いかもデス！", 103001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1118, 3, 0, "心地よくて\n眠くなってきマス…\nもしやテキコクの\n罠デスか！？", 103001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1119, 3, 0, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 103001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1120, 3, 0, "ワビサビというもの\nデスね！\n素晴らしいデス！", 103001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1121, 3, 0, "身体を暖めて\nイクサに向かうデス！", 103001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1122, 3, 0, "アクロバットな忍法を\n使うには、体を\n鍛えないと危険デス！", 103001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1123, 3, 0, "うーん、\n美味しいデス…\n幸せデス～", 103001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1124, 3, 0, "これは…\nＢＵＳＨＩＤＯ…\nデスか？", 103001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1126, 1, 0, "ふんふんふ～ん\n武器の手入れは\n念入りにしマスよ～", 103002, 0, 1);
