INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1131, 1, 0, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 103002, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1132, 1, 0, "ショーグンの身は\n何があってもワタシが\n守るデス！", 103002, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1133, 2, 0, "あっ、ショーグン\n出陣はいつで\nゴザルデス！？", 103002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1134, 2, 0, "イクサの前に\nホネ休めも大事デスよ\nゆっくりするデース！", 103002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1135, 2, 0, "さ、さすがショーグン\nデス…いつの間に\n背後を！？", 103002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1136, 2, 0, "一緒に忍法考える\nデス！\nショーグン用の\n忍法も必要デスね！", 103002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1137, 2, 0, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 103002, 0, 8);
