INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1142, 3, 0, "服の乱れは心の乱れ\nデス！　きっちり\nしないといけないデス", 103002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1143, 3, 0, "心地よくて眠く\nなってきマス…\nもしやテキコクの\n罠デスか！？", 103002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1144, 3, 0, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 103002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1145, 3, 0, "ワビサビというもの\nデスね！\n素晴らしいデス！", 103002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1146, 3, 0, "身体を暖めてイクサに\n向かうデス！", 103002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1147, 3, 0, "ショーグンのお役に\n立つため、修行\nあるのみデス！", 103002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1148, 3, 0, "美味しいデス…\n酢昆布を和えて\nみたいデス", 103002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1149, 3, 0, "物事を知って\n来たるべきイクサに\n活かすデス", 103002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1151, 1, 0, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 103101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1152, 1, 0, "父は、いつも男性に\nきびしいですね", 103101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1153, 1, 0, "占ってほしい人は、\nいつでも言って\nください", 103101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1154, 1, 0, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 103101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1155, 1, 0, "この部屋、風水的にも\n運気が上がりそうです", 103101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1156, 1, 0, "あの人とのこと占って\nみようかな…\n好き…嫌い…\n好き♪…き、嫌い", 103101, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1157, 1, 0, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 103101, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1158, 2, 0, "女難の相が\n出ていますね…\n気をつけてください", 103101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1159, 2, 0, "今日のあなたの運勢は\n大きょ…\n大吉です", 103101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1160, 2, 0, "父と散歩中です。\n毎日決まったコースを\n散歩させないと、\nストレスが溜まるので", 103101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1161, 2, 0, "小僧！\nシノブに触るとは\nいい度胸じゃねぇか！", 103101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1162, 2, 0, "あなたに触られると、\n動悸が早くなります\nもしかして心霊現象？", 103101, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1163, 2, 0, "今日もたくさんの幽霊\nに慕われていますね。\n素敵です", 103101, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1164, 2, 0, "おはようございます\n今日の運勢を\n占いましょうか？", 103101, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1165, 2, 0, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 103101, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1166, 2, 0, "今日のあなたの運気は\n最高潮でした。\nもうすぐ今日は\n終わりますが", 103101, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1167, 3, 0, "お父さん。\nいつも、身だしなみに\n気を使ってるけど、\nあまり変わってないよ", 103101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1168, 3, 0, "一休みしましょう。\n憑かれて疲れました", 103101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1169, 3, 0, "かわいい…\n付喪神が宿らないかな", 103101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1170, 3, 0, "お父さん、\nリラックスし過ぎると\n成仏しちゃうよ？", 103101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1171, 3, 0, "気持ちいいです…\n極楽浄土みたい…", 103101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1172, 3, 0, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 103101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1173, 3, 0, "お父さんも\n食べたいの？\n死んでるのに？", 103101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1174, 3, 0, "この本…\nもしかして予言書？", 103101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1176, 1, 0, "ここにいる幽霊は\nみんな幸せそうです", 103102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1177, 1, 0, "お父さんが\n女の子ばかりに\n声をかけるので\n困っています", 103102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1178, 1, 0, "成仏はうれしいこと\nです…でも友達が\n減るのは寂しいですね", 103102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1179, 1, 0, "幽霊に好かれる人は\n素敵な方です\nもちろん、あなたも", 103102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1180, 1, 0, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 103102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1181, 1, 0, "生きている人に\n好かれるには、\nどうすればいい\nのでしょう？", 103102, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1182, 1, 0, "お父さん、どうしたら\nあの人のことを\n認めてくれますか？", 103102, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1183, 2, 0, "この部屋、風水的にも\n運気が上がりそうです", 103102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1184, 2, 0, "シノブには指一本\n触れさせん！\nガブッ！！", 103102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1185, 2, 0, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 103102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1186, 2, 0, "ラップ音？\nいえ、父のいびきです", 103102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1187, 2, 0, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 103102, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1188, 2, 0, "前世って信じますか？\n私とあなたは\nもしかすると…", 103102, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1189, 2, 0, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 103102, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1190, 2, 0, "明るい時間は\nあまり得意では\nないんです…", 103102, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1191, 2, 0, "夜のお散歩に\n行きませんか？\n知り合いの幽霊に\n挨拶して回るんです", 103102, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1192, 3, 0, "お父さん\nいつも、身だしなみに\n気を使ってるけど\nあまり変わってないよ", 103102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1193, 3, 0, "うとうと…\nあれ…\n走馬灯が見える…", 103102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1194, 3, 0, "かわいい…\nお父さんみたい…", 103102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1195, 3, 0, "お父さん\nリラックスし過ぎると\n成仏しちゃうよ？", 103102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1196, 3, 0, "快適です…\n幽霊もどんどん\n集まってきますね…", 103102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1197, 3, 0, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 103102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1198, 3, 0, "お父さんの分も、\n私が食べるね", 103102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1199, 3, 0, "この本の占い\n私の占いと違う…", 103102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1201, 1, 0, "私だって、一人で\n買い物ぐらい\nできますわ…きっと", 103201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1202, 1, 0, "そろそろ新しい\n案件を始めないと\nいけませんわね", 103201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1203, 1, 0, "一日も早く、\n一人前の商人に\nなりたいものです", 103201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1204, 1, 0, "平和を買うのには\nどれほどのお金が\n必要なのかしら？", 103201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1205, 1, 0, "ここももう少し広いと\n落ち着くのですけど。\n改築しませんこと？", 103201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1206, 1, 0, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 103201, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1207, 1, 0, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 103201, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1208, 2, 0, "私と、庶民向けの\nお店へ一緒に\n参りませんか？", 103201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1209, 2, 0, "資産運用？\nお幾らほど\n増やしたいのです？", 103201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1210, 2, 0, "どんぶり勘定だとよく\n言われるのですが…\n例えが小さすぎます！", 103201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1211, 2, 0, "お金で解決するのは\n悪いことでは\nありませんわよね？", 103201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1212, 2, 0, "その、よろしければ\n私とお散歩など\nいかがです？", 103201, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1213, 2, 0, "もしあなたさまが\n道に迷われるなら、\n私もお供いたします！", 103201, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1214, 2, 0, "早起きは三ルピの徳と\n申しますが…\nもっとお得ですわね", 103201, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1215, 2, 0, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 103201, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1216, 2, 0, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 103201, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1217, 3, 0, "だらしない身なりでは\n取引相手に信用して\nいただけませんものね", 103201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1218, 3, 0, "この感触と安らぎ…\n素晴らしい\n費用対効果ですわ…", 103201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1219, 3, 0, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 103201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1220, 3, 0, "目に見えないものに\nお金をかけるのが\n真の贅沢ですわ！", 103201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1221, 3, 0, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 103201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1222, 3, 0, "健康はお金では\n買えませんが\n運動器具は別ですわ！", 103201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1223, 3, 0, "なかなか\n美味ですわ\nシェフはどちらに？", 103201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1224, 3, 0, "経済の仕組みを\nしっかりと\n学ばなくては…", 103201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1226, 1, 0, "そろそろ新しい\n案件を始めないと\nいけませんわね", 103202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1227, 1, 0, "ええと、これは\nおいくらで買ったん\nでしたっけ？", 103202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1228, 1, 0, "お金を見せたら\n態度が変わる人は\n信用できませんわ", 103202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1229, 1, 0, "地図が読めずとも\n道に迷わない方って\nいらっしゃるのですね", 103202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1230, 1, 0, "この狭さも\n慣れれば\n快適ですわ♪", 103202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1231, 1, 0, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 103202, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1232, 1, 0, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 103202, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1233, 2, 0, "一人前の商人に\n相応しい居振る舞いを\nしたいものですわ", 103202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1234, 2, 0, "幼い頃\nお店屋さんごっこを\nなさいましたか？", 103202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1235, 2, 0, "資産運用？\nお幾らほど\n増やしたいのです？", 103202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1236, 2, 0, "今度一緒に…\nパーティにでも\n行きませんこと？", 103202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1237, 2, 0, "その、よろしければ\n私とお散歩など\nいかがです？", 103202, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1238, 2, 0, "あなたさまの優しさは\nお金には\n代えられませんわね", 103202, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1239, 2, 0, "なぜだか\n眠り足りないような\n感じがしますわ…", 103202, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1240, 2, 0, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 103202, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1241, 2, 0, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 103202, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1242, 3, 0, "ドレスは汚れていない\nようですが…やだ\n髪がほつれてますわ！", 103202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1243, 3, 0, "こういったものに\n腰を沈めると…\nき、気が緩んで…", 103202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1244, 3, 0, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 103202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1245, 3, 0, "香りは質のいいものと\nそうでないものが\nはっきり分かりますわ", 103202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1246, 3, 0, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 103202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1247, 3, 0, "目標は遠くに\n置いた方が励みに\nなりますわ！", 103202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1248, 3, 0, "なかなか\n美味ですわ\nシェフはどちらに？", 103202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1249, 3, 0, "商人って、物語では\n悪役にされやすいの\nですね…", 103202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1251, 1, 0, "家の中のものを使って\n物ボケ、考えるべ！", 103301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1252, 1, 0, "毎日の日課…それは\n炊事、洗濯、大喜利\nだべ！", 103301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1253, 1, 0, "うちに塀ができたら、\nへー、って言いたい！", 103301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1254, 1, 0, "オラが作る料理は\nこの後スタッフで\n美味しくいただくべよ", 103301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1255, 1, 0, "エリザベスも\n連れてきてぇ～", 103301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1256, 1, 0, "あんちゃんも、\nエリザベスと同じ位\nめんこいべ♪", 103301, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1257, 1, 0, "オラとあんちゃん、\n息がぴったりに\nなってきたべ…！", 103301, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1258, 2, 0, "おぉ！　そんな急に\nツッコまれたら、\nびっくらこくべやー！", 103301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1259, 2, 0, "ネタはオラが書くけど\nあんちゃんが\n書きたかったら\n書いてもいいんだぞ？", 103301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1260, 2, 0, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 103301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1261, 2, 0, "押すなよ、\n絶対押すなよ\n…なんて\n言ってないべさ？", 103301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1262, 2, 0, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 103301, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1263, 2, 0, "将来的には夫婦漫才\nってのも…あ、ひ、\nひとりごとだべさ～！", 103301, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1264, 2, 0, "おはよう！　一緒に\n牛の乳しぼりいくべ？", 103301, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1265, 2, 0, "お昼だ～腹が減っては\n笑いはできぬ～！", 103301, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1266, 2, 0, "今日もよく働いたべ！\nさ、今からは\nお笑いの練習だ～！", 103301, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1267, 3, 0, "舞台に上がる前に\n身なりは整えねぇと\nだめだべ～！", 103301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1268, 3, 0, "ナイスなイスに\n座りたい、\nなんちゃって！", 103301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1269, 3, 0, "めんこいのは、\nスベっても許される\nからずるいべな～？", 103301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1270, 3, 0, "必要以上に\n癒されちまうべや～", 103301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1271, 3, 0, "ぽかぽかだ…\nウケた会場みてぇだ", 103301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1272, 3, 0, "お笑い芸人は、\n体が資本だべさ～！", 103301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1273, 3, 0, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 103301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1274, 3, 0, "ネタ作りのヒントが\n載ってねぇか探すべ", 103301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1276, 1, 0, "さぁ今日も張り切って\n漫才の練習するべさ！", 103302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1277, 1, 0, "この部屋でも、商品\n売っていいんだべか？", 103302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1278, 1, 0, "眼鏡を頭の上に乗せて\n「メガネメガネ～」\nって言う練習した方が\nいいべか？", 103302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1279, 1, 0, "漫才を持ち時間に\n収めるには、何度も\n練習するしかねぇべ", 103302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1280, 1, 0, "エリザベスも\n連れてきてぇ～", 103302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1281, 1, 0, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 103302, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1282, 1, 0, "あんちゃんに\n採れたての食材を\n食わしてやりてぇ～", 103302, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1283, 2, 0, "押すなよ\n絶対押すなよ\n…なんて\n言ってないべさ？", 103302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1284, 2, 0, "あんちゃん、百連\nツッコミ特訓だ～！", 103302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1285, 2, 0, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 103302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1286, 2, 0, "「なんでだべ～！」\nって言いながら\nツッコむといいべ！", 103302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1287, 2, 0, "あはは～あんちゃんの\nツッコミはやっぱり\n気持ちいいなぁ～", 103302, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1288, 2, 0, "将来的には夫婦漫才\nってのも…あ、ひ\nひとりごとだべさ～！", 103302, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1289, 2, 0, "おはよう！\n今日もいい\nネタ作り日和だ！", 103302, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1290, 2, 0, "お昼だな\nご飯を食べたら、\nネタの練習するべ！", 103302, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1291, 2, 0, "もうこんな時間だべ\nネタ作りは\n明日にするべ…", 103302, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1292, 3, 0, "うーん…衣装、\nずれてねぇべか？", 103302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1293, 3, 0, "ナイスなイスに\n座りたい\nなんちゃって！", 103302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1294, 3, 0, "はう～\nなまらめんこいべや～", 103302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1295, 3, 0, "必要以上に\n癒されちまうべや～", 103302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1296, 3, 0, "笑いをとることを\n忘れるほど\nあったけぇべ～", 103302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1297, 3, 0, "たくさん運動して\n動きでも笑いがとれる\nように頑張るべ～！", 103302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1298, 3, 0, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 103302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1299, 3, 0, "幅広く教養を\n身につけて\nいいネタ作るべ！", 103302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1301, 1, 0, "通りにいる\nカップル…爆発\nすればいいのに…", 103401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1302, 1, 0, "う～ん、仕事なんて\nしないで、\n飲んでたいな～", 103401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1303, 1, 0, "今日は一日、\n外に出ない！\nもう決めたわ！", 103401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1304, 1, 0, "お腹空いた…\nけど、外に出るのは\n面倒だなぁ", 103401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1305, 1, 0, "うぅ…頭がガンガン\nする…昨日、\n飲み過ぎたかな…", 103401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1306, 1, 0, "デートとか…\n誘っても嫌がられ\nないかな…？", 103401, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1307, 1, 0, "もしかして運命の\n相手だったり…\nするのかな", 103401, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1308, 2, 0, "やっぱり一杯目が\n一番おいしいわ～！\n…え、見てた！？", 103401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1309, 2, 0, "頼れるお姉さんだって\nところ、もっと見せて\nあげるからね", 103401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1310, 2, 0, "ねえ、一緒に買い物に\n行かない？　その…\nの、飲み物とか…", 103401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1311, 2, 0, "ひっく、うふふ…\nもっとお姉さんと\nお話しようよ～", 103401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1312, 2, 0, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 103401, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1313, 2, 0, "いつか…\nキミと一緒に飲めたら\n楽しいだろうな", 103401, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1314, 2, 0, "おはよう…朝は\nちょっとつらいな…\n低血圧なのよ…", 103401, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1315, 2, 0, "おっひるー♪\nなに食べよっかな～♪", 103401, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1316, 2, 0, "ふわあ…\n眠る前にちょっと\n一杯…冗談よ", 103401, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1317, 3, 0, "うう…\n髪がボサボサ…\nちゃんとしなきゃ…", 103401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1318, 3, 0, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 103401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1319, 3, 0, "こういうの男の子に\nプレゼントして\nもらいたいな…", 103401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1320, 3, 0, "うわあ、\nこれ、私の好きな\n香りだー！", 103401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1321, 3, 0, "ぽっかぽか…\nあとは熱い一杯が\nあれば…！", 103401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1322, 3, 0, "家でゴロゴロして\n付いちゃったお肉を\n落とさないと…！", 103401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1323, 3, 0, "もぐもぐ…\nおーいしー！\n…太らないよね？", 103401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1324, 3, 0, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 103401, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1326, 1, 0, "仕事終わりの一杯の\nために、今日も\n頑張らないと", 103402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1327, 1, 0, "今日は\nのんびり\n過ごそうかな～", 103402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1328, 1, 0, "あ…\nお皿、洗って\nなかった…", 103402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1329, 1, 0, "もう少し、大人の\n魅力が出せるように\nなりたいな", 103402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1330, 1, 0, "素敵な彼氏…\n欲しいなぁ…", 103402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1331, 1, 0, "彼と私\n釣り合うかな…\n大丈夫よね？", 103402, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1332, 1, 0, "今後のために\nちょっとは花嫁修業\nしてみようかな…？", 103402, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1333, 2, 0, "何事も\nお姉さんと一緒に\n楽しく頑張りましょ？", 103402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1334, 2, 0, "ね、ねえ！\nキミは年上と年下\nどっちが好きかな？", 103402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1335, 2, 0, "もう…\n構ってくれないと\n泣いちゃうぞ～？", 103402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1336, 2, 0, "お姉さんが～\n可愛がって\nあげようか～？", 103402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1337, 2, 0, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 103402, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1338, 2, 0, "お姉さんとチュー\nする？　ふふふ～\n冗談で～す！", 103402, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1339, 2, 0, "さーて、今日も\nいっちょやるぞー！", 103402, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1340, 2, 0, "後でのんびり\nストレッチでも\nしようっと♪", 103402, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1341, 2, 0, "一日よく働いた～！\n自分にごほうび\nあげたいなー♪", 103402, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1342, 3, 0, "仕事の前に、髪とか\n整えないと…", 103402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1343, 3, 0, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 103402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1344, 3, 0, "もう大人だし…\nあんまり可愛いのは\n似合わないかな…？", 103402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1345, 3, 0, "うわあ\nこれ、私の好きな\n香りだー！", 103402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1346, 3, 0, "あったかーい…\nだ、だらけ\nちゃいそう…", 103402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1347, 3, 0, "地道な努力が\nリア充への\n第一歩！", 103402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1348, 3, 0, "おーいしっ♪\nこのために\n生きてるわねえ", 103402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1349, 3, 0, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 103402, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1351, 1, 0, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 103801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1352, 1, 0, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？", 103801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1353, 1, 0, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 103801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1354, 1, 0, "ちょっと、休憩…\n本でも読もうかな", 103801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1355, 1, 0, "もっとたくさんの本に\n囲まれて生活したいな", 103801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1356, 1, 0, "むかしむかしある所に\nシオリと\nあの人が…", 103801, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1357, 1, 0, "この本の主人公、\nあの人に\n似ているかも…\nかっこいいな", 103801, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1358, 2, 0, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 103801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1359, 2, 0, "あ…お姉ちゃんと\n約束があったんだ…", 103801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1360, 2, 0, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 103801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1361, 2, 0, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 103801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1362, 2, 0, "読みたい本も\nあなたと行きたい所も\n増えるばっかりです", 103801, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1363, 2, 0, "私の病気、\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 103801, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1364, 2, 0, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 103801, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1365, 2, 0, "ふぅ…午前中\n動いたので体力が…\nすこし休みますね", 103801, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1366, 2, 0, "早く寝ないと\n体に良くないですよね\nでも気になっている\n本があって…", 103801, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1367, 3, 0, "寝てばかりなので、\n髪が乱れてないかな\n…？", 103801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1368, 3, 0, "少し休もう\n…やっぱり\nたくさん休もう", 103801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1369, 3, 0, "かわいいものが\n枕元にあると\n安心できるんですよ", 103801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1370, 3, 0, "いい香り…\n病は気からと言うので\n雰囲気も大事ですね", 103801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1371, 3, 0, "こうして温まって\nいると、病気も\n治る気がします", 103801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1372, 3, 0, "運動…イメージ\nトレーニングだけじゃ\nだめでしょうか…", 103801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1373, 3, 0, "少食ですけど\nこれならたくさん\n食べられそうです", 103801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1374, 3, 0, "続きが気になって\n読むのを止める\nタイミングが…", 103801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1376, 1, 0, "しっかり休まないと\n早く回復するように", 103802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1377, 1, 0, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？　", 103802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1378, 1, 0, "病室よりも…\nずっとこの部屋に\nいたいですね…　", 103802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1379, 1, 0, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 103802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1380, 1, 0, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 103802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1381, 1, 0, "むかしむかしある所に\nシオリと\nあの人が…", 103802, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1382, 1, 0, "あの人の\nためにも早く\n病気を治さないと…", 103802, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1383, 2, 0, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 103802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1384, 2, 0, "新記録です！\n一週間連続、\n平熱を保ちました！", 103802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1385, 2, 0, "あ…お薬の時間…\n苦いけど我慢しなきゃ　", 103802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1386, 2, 0, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 103802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1387, 2, 0, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 103802, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1388, 2, 0, "私の病気\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 103802, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1389, 2, 0, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 103802, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1390, 2, 0, "…お昼の薬？\nあ、飲み忘れてました\nありがとうございます", 103802, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1391, 2, 0, "この薬を飲むと\n眠くなっちゃうんです\nでもまだ、あなたと\nお話したいし～", 103802, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1392, 3, 0, "寝てばかりなので、\n髪が乱れてないかな\n…？　", 103802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1393, 3, 0, "少し休もう\n…やっぱり\nたくさん休もう", 103802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1394, 3, 0, "この子がいれば\n病室に一人でも\n寂しくないですね", 103802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1395, 3, 0, "いろんな効能が？\n健康になる香りも\nあるのでしょうか？", 103802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1396, 3, 0, "こうして温まって\nいると、病気も\n治る気がします", 103802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1397, 3, 0, "私も、少しは\n体力をつけないと\nいけませんよね　", 103802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1398, 3, 0, "いっぱい食べて\n健康な体にならないと", 103802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1399, 3, 0, "続きが気になって\n読むのを止める\nタイミングが…", 103802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1401, 1, 0, "友達ができたら、\n山の上でお弁当…\n夢が広がります…", 104001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1402, 1, 0, "あ、忘れずに\nお花に水を\nあげなきゃ…", 104001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1403, 1, 0, "お菓子を作って、\nみなさんに振る舞って\n友達作戦！　とか…", 104001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1404, 1, 0, "そろそろ新しい\n草人形を作って\n友達作りの特訓を…", 104001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1405, 1, 0, "植物のお世話って、\nぼっちにとっては、\n救い…なんです…", 104001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1406, 1, 0, "ＢＢ団の活動日…\nまだかな…\nうふふふっ", 104001, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1407, 1, 0, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 104001, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1408, 2, 0, "あ、こんにちは！\n今日もＢＢ団で\nお世話になります！", 104001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1409, 2, 0, "あの…私の友達作りの\n練習方法、間違って\nませんよね…？", 104001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1410, 2, 0, "そうだ、今度、\n森に一緒に木の苗を\n植えに行きませんか？", 104001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1411, 2, 0, "人とお話するのって、\n私、大好きです！\n友達ふやすぞ～！", 104001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1412, 2, 0, "あなたと話してると、\nまるで私がぼっちじゃ\nないみたいです！", 104001, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1413, 2, 0, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 104001, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1414, 2, 0, "おはようございます！\n今日もＢＢ団、\nがんばるぞー！", 104001, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1415, 2, 0, "なんだか、お腹が\n空いてきました。\nお昼ごはんー♪", 104001, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1416, 2, 0, "もう眠いかもです…\n明日は友達、\nできるといいなあ…", 104001, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1417, 3, 0, "私がもっと\n可愛ければ、\n友達できるかなあ？", 104001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1418, 3, 0, "ふわあ…のんびり\nしすぎないように\n気を付けなきゃです", 104001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1419, 3, 0, "かわいいなあ。\nこの子とも\n友達になりたい…", 104001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1420, 3, 0, "いい香りです…\n精神統一して\n友達のイメトレ…", 104001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1421, 3, 0, "灯りを見てると、\n心がなごみます～", 104001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1422, 3, 0, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 104001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1423, 3, 0, "おいしいです…\n私に友達がいたら、\n分けてあげたいな", 104001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1424, 3, 0, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 104001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1426, 1, 0, "友達作りは、焦らず\n慌てず、じっくりと…", 104002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1427, 1, 0, "ひとつ屋根の下\nって感じ…\nいいですねえ…", 104002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1428, 1, 0, "煮汁、煮汁が\n噴きこぼれて\nいませんか～", 104002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1429, 1, 0, "今度、みなさんと\nピクニックとか…\nいってみたいな", 104002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1430, 1, 0, "ぼっちでもいい\nたくましく育てば\n…なんて", 104002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1431, 1, 0, "最近、毎日あの人の\nこと考えちゃいます\nどうしてだろう…", 104002, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1432, 1, 0, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 104002, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1433, 2, 0, "あ！　ＢＢ団の活動\nですか？　嬉しくて\n飛び跳ねちゃいます！", 104002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1434, 2, 0, "あなたとのお話で友達\n作りの練習になるなら\n一石二鳥です！", 104002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1435, 2, 0, "私少しは人と話しても\nテンパらなくなって\nきましたかね…？", 104002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1436, 2, 0, "あの、友達作りの\n新しい作戦\n考えてみたんですっ！", 104002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1437, 2, 0, "あなたとお話する時の\nこの気持ち、私の宝物\nなんです…えへへ", 104002, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1438, 2, 0, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 104002, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1439, 2, 0, "朝ですね！\nこんないい朝は\n友達作りのチャンス！", 104002, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1440, 2, 0, "あの…そろそろお腹\nすきませんか？\nお昼ごはんは…", 104002, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1441, 2, 0, "もう眠いかもです…\n明日は友達\nできるといいなあ…", 104002, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1442, 3, 0, "私…見た目\nそこまで変じゃ\nないですよね…？", 104002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1443, 3, 0, "ほわあ～…\nふかふかって\nいいものですね～", 104002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1444, 3, 0, "この子みたいな\nあいくるしさを\n目指したいです！", 104002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1445, 3, 0, "灯りを見てると\n心がなごみます～", 104002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1446, 3, 0, "ぬくぬくで、なんだか\n眠くなっちゃいますね", 104002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1447, 3, 0, "身体を鍛えて\nみなさんを\n守ります！", 104002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1448, 3, 0, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 104002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1449, 3, 0, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 104002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1451, 1, 0, "唱喚の歌は、みだりに\n歌っていいものでは\nないんです", 104201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1452, 1, 0, "ここでアイドルの\n練習は、さすがに\n迷惑ですよね？", 104201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1453, 1, 0, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 104201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1454, 1, 0, "人々の平和を守る旅に\n同行してくれる人を\n探していて…", 104201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1455, 1, 0, "あとで曲を復習して\nおかないと…", 104201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1456, 1, 0, "踊りを見られるのは\nまだ恥ずかしいけど\nあの人になら…", 104201, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1457, 1, 0, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 104201, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1458, 2, 0, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 104201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1459, 2, 0, "え、もう練習の時間？\nごめんなさい\nすぐ準備します！", 104201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1460, 2, 0, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 104201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1461, 2, 0, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 104201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1462, 2, 0, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 104201, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1463, 2, 0, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 104201, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1464, 2, 0, "おはようございます♪\n少し歌の練習を\nしてきますね", 104201, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1465, 2, 0, "もうお昼なんですか？\n練習に夢中で\n気づきませんでした", 104201, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1466, 2, 0, "明日の予定は…\nアイドルはやることが\n多いみたいで…", 104201, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1467, 3, 0, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 104201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1468, 3, 0, "歌の調子をよくする\nためには休息も\n必要ですよね…", 104201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1469, 3, 0, "この子たちを\n相手に練習して\nみようかな…♪", 104201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1470, 3, 0, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 104201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1471, 3, 0, "ここにいると\n穏やかな旋律が\n浮かんできます…", 104201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1472, 3, 0, "歌うためには\n体力づくりは\n欠かせません", 104201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1473, 3, 0, "歌った後は\nお腹が空いて\nしまうんです", 104201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1474, 3, 0, "音楽について\n理論も勉強\nしないと…", 104201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1476, 1, 0, "歌うことの楽しさ\n分かってきました\nもっと歌いたいな…", 104202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1477, 1, 0, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 104202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1478, 1, 0, "精霊も強くなったし\nもっとたくさんの人を\n守りたいです", 104202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1479, 1, 0, "最近、よく頭の中に\n歌が浮かんできます\n不思議な感覚…", 104202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1480, 1, 0, "楽譜を買いました\n思いついた歌を\n書き留めておくために", 104202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1481, 1, 0, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 104202, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1482, 1, 0, "私の中にある\n私だけの歌…\n作ってみようかな？", 104202, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1483, 2, 0, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 104202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1484, 2, 0, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 104202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1485, 2, 0, "魔物退治ですか！？\n…違います？\n良かった…", 104202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1486, 2, 0, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 104202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1487, 2, 0, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 104202, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1488, 2, 0, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 104202, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1489, 2, 0, "呼吸を深く、大きく…\nこれでちゃんと\n目が覚めますよ", 104202, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1490, 2, 0, "少し休んだら\n午後の練習に\n行ってきますね", 104202, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1491, 2, 0, "曲を作っていたら\nもうこんな時間…\nおやすみなさい", 104202, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1492, 3, 0, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 104202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1493, 3, 0, "あ～…♪\nよく体がほぐれます…\nいい歌が歌えそうです", 104202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1494, 3, 0, "かわいいと思う\n気持ちを、歌にしても\nいいかも知れませんね", 104202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1495, 3, 0, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 104202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1496, 3, 0, "ここにいると\n穏やかな旋律が\n浮かんできます…", 104202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1497, 3, 0, "もっと体力をつけて\n最高のライブを\nファンに届けます！", 104202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1498, 3, 0, "これでも一応\n体形には気をつけて\n食べてるんですよ？", 104202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1499, 3, 0, "読書は歌に\n新しい風を\n吹き込んでくれます", 104202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1501, 1, 0, "店の新メニューでも\n考えるかな", 104301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1502, 1, 0, "あたしの店から、\n出前を届けようか？", 104301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1503, 1, 0, "パトロール、\nパトロール…\nここは異常なしかな？", 104301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1504, 1, 0, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 104301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1505, 1, 0, "のんびりし過ぎるのも\n…性に合わねぇな", 104301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1506, 1, 0, "そろそろあいつ、\n腹すかしてるん\nじゃないかな？", 104301, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1507, 1, 0, "今日の献立は…\nあいつの好きなものに\nしてやるか", 104301, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1508, 2, 0, "強くなりたいなら、\nあたしがビシバシ\n鍛えてやるよ", 104301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1509, 2, 0, "天気がいい日は\n外で運動したく\nならねぇか？", 104301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1510, 2, 0, "腹減ったか？　なんか\n作ってやろうか？", 104301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1511, 2, 0, "ん？　なんだよ、\n相談事か？\nなんでも言ってくれ", 104301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1512, 2, 0, "お前が好きな飯って\nなんだ？\n今度、作ってやるよ", 104301, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1513, 2, 0, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 104301, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1514, 2, 0, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか", 104301, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1515, 2, 0, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 104301, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1516, 2, 0, "日中たっぷり動いたら\nそのぶん夜はしっかり\n休んどけよな", 104301, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1517, 3, 0, "た、たまにはその…\nかわいい格好とか\nしても…", 104301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1518, 3, 0, "ふわぁ、\n一休みすっかなぁ～", 104301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1519, 3, 0, "うっ！　か、かわいい\nじゃねぇか", 104301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1520, 3, 0, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 104301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1521, 3, 0, "ふわぁ〜！\n温かいと、つい\nウトウトしちまうな", 104301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1522, 3, 0, "オラオラオラーーー！\n燃えるぜーーー！", 104301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1523, 3, 0, "いい味してるぜ！\nうちの店でも\n出してみたいな", 104301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1524, 3, 0, "文字を読んでると\nどうしても眠く\nなっちまうんだよなぁ", 104301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1526, 1, 0, "パトロール終了～\n本日も異常なしっと！", 104302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1527, 1, 0, "あたしの店から\n出前を届けようか？", 104302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1528, 1, 0, "あいつ、早く\n帰ってこないかな", 104302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1529, 1, 0, "あいつ\n腹すかせてないと\nいいんだけど…", 104302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1530, 1, 0, "のんびりし過ぎるのも\n…性に合わねぇな", 104302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1531, 1, 0, "あいつの事を思うと\n胸があったかくなる？\nなんだコレ…？", 104302, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1532, 1, 0, "今日の献立は…\nあいつの好きなものに\nしてやるか", 104302, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1533, 2, 0, "ん？　なんだよ\n相談事か？\nなんでも言ってくれ", 104302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1534, 2, 0, "天気がいい日は\n外で運動したく\nならねぇか？", 104302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1535, 2, 0, "腹減ったか？　なんか\n作ってやろうか？", 104302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1536, 2, 0, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 104302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1537, 2, 0, "あのさ…できれば\nもっとそばに…いや\nなんでもねぇ忘れろ！", 104302, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1538, 2, 0, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 104302, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1539, 2, 0, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか？", 104302, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1540, 2, 0, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 104302, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1541, 2, 0, "あんま夜更しすんなよ\n明日に響いたら\n大変だろ？", 104302, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1542, 3, 0, "た、たまにはその…\nかわいい格好とか\nしても…", 104302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1543, 3, 0, "ふぅ～♪\n疲れがとれるな～", 104302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1544, 3, 0, "うっ！　か、かわいい\nじゃねぇか", 104302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1545, 3, 0, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 104302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1546, 3, 0, "ふわぁ～！\n温かいと、つい\nウトウトしちまうな", 104302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1547, 3, 0, "オラオラオラーーー！\n燃えるぜーーー！", 104302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1548, 3, 0, "いい味してるぜ！\nうちの店でも\n出してみたいな", 104302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1549, 3, 0, "料理だけじゃなく\n経営術とかも\n学んどかねぇと…", 104302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1551, 1, 0, "最近なにも\nしてくれません…\n焦らしてるんですね", 104501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1552, 1, 0, "ぐふふ、じゅるり…\nい、いけない、また\nいつの間にかよだれが", 104501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1553, 1, 0, "好きな武器といえば\n剣より槍より\nやっぱり鞭…ぐふふ", 104501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1554, 1, 0, "「無数の触手が\nクウカの柔肌に\n食い込み」…ぐふふ", 104501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1555, 1, 0, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 104501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1556, 1, 0, "ドＳさんを登場\nさせるだけで、妄想に\n無限の広がりが！", 104501, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1557, 1, 0, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 104501, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1558, 2, 0, "視線を感じると\n思ったら…やはり\nドＳさんでしたか", 104501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1559, 2, 0, "今日は\nどんなヒドイことを\nしてくれるんですか？\nぐふふふ", 104501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1560, 2, 0, "いつの間にそこに！？\nまさかクウカを監視\nしていたのですか！？", 104501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1561, 2, 0, "クウカに触るつもり\nですか？　触るつもり\nなのですねっ！？", 104501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1562, 2, 0, "クウカにナニをする\nつもりですか…\n期待してしまいます！", 104501, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1563, 2, 0, "ドＳさんの視界に入る\nだけでドキドキが…\nこれが恋！？", 104501, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1564, 2, 0, "おはようございます。\n朝からお盛んですね？\nぐふふ…", 104501, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1565, 2, 0, "こんなにも日の高い\nうちからドＳさんは…\nじゅるり", 104501, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1566, 2, 0, "夜はこれから、\nですよ…\nぐふ、ぐふふふ…", 104501, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1567, 3, 0, "キレイにおめかし\nしたのに力ずくで…\nぐふふふ", 104501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1568, 3, 0, "ソファの背にクウカを\n押し付けて…ぐふふ\nいいですね\nソファドン", 104501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1569, 3, 0, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 104501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1570, 3, 0, "香りを吸ったクウカの\n頬は紅潮し目はうるみ\nもはや抗う力も…！", 104501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1571, 3, 0, "ああ、身体が\n火照って…\nぐふ、ぐふふ…", 104501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1572, 3, 0, "はぁ、はぁ、こうして\nクウカの体力を\n奪ってから…ぐふふ", 104501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1573, 3, 0, "食欲が満たされたら\nつ、次はやはり…！\nそうなのですね！？", 104501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1574, 3, 0, "この本の下に、\nそういう本を\nカムフラージュ？", 104501, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1576, 1, 0, "「ドＳさんと踊り子\n乱舞の一夜」…\n次の日記はこれで…", 104502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1577, 1, 0, "最近なにも\nしてくれません…\n焦らしてるんですね", 104502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1578, 1, 0, "も、妄想が\n足りません…\n禁断症状がっ…！", 104502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1579, 1, 0, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 104502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1580, 1, 0, "次はどんなドＳを\nされるのか、想像する\nだけで…ぐふふふ", 104502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1581, 1, 0, "じっとしてると\nドＳさんのことばかり\n考えてしまいますね", 104502, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1582, 1, 0, "妄想が止まらなくて\nクウカどうにかなって\nしまいそうです！", 104502, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1583, 2, 0, "ドＳさんといると体が\n火照って…クウカは\nいやらしい子です！", 104502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1584, 2, 0, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 104502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1585, 2, 0, "ドＳさんのおかげで\n新しい扉が開けそう\nです…じゅるり", 104502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1586, 2, 0, "いやー！　ドＳさんに\n襲われ…襲って\nくれないんですか？", 104502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1587, 2, 0, "クウカはドＳさんに\n身も心も堕とされて\nしまったのですね", 104502, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1588, 2, 0, "ドＳさんがお望みなら\nクウカは\nどんなことだって！", 104502, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1589, 2, 0, "朝だからこそ\nできる妄想が\nあります…ぐふふ", 104502, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1590, 2, 0, "こんな日の高いうち\nからドＳさんは…\nじゅるり", 104502, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1591, 2, 0, "今夜は一晩中…\nなんですね？\nぐふふ…", 104502, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1592, 3, 0, "意外と難しいんですよ\n正しい亀甲縛りと\nいうものは…", 104502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1593, 3, 0, "くつろいで油断\nしているスキに何を\nされるのでしょう！？", 104502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1594, 3, 0, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 104502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1595, 3, 0, "これは禁断の香り…\nああっ一段階上の\n妄想が開花するうぅ！", 104502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1596, 3, 0, "ああっ、暖気がまるで\nロウソクのように\nクウカを攻めますっ！", 104502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1597, 3, 0, "激しい攻めを受けきる\nためには、鍛えないと\nいけないのですね", 104502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1598, 3, 0, "こんなにいっぱい\n入りません…\nお腹にっ！", 104502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1599, 3, 0, "どんな本も\nよく見ると\nいやらしいです…！", 104502, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1601, 1, 0, "この前遊んだ猫、\nモッフモフ\nだったにゃあ…♪", 104601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1602, 1, 0, "ほっぺやお腹が、\n肉球ぐらいぷにぷに…\nこれはまずいにゃ…", 104601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1603, 1, 0, "大きな仕事が\n決まったにゃ！\n腕が鳴るにゃ～♪", 104601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1604, 1, 0, "おやつのたい焼き、\nつぶあんとこしあん、\nどっちに\nしようかにゃ？", 104601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1605, 1, 0, "うにゃー…\nもうちょっとお昼寝\nしたかったにゃ…\nむにゃむにゃ…", 104601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1606, 1, 0, "キミに呼ばれると、\n猫まっしぐらな気分に\nなっちゃうにゃ♪", 104601, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1607, 1, 0, "キミのおかげで\nギルドの仕事も\n大成功だにゃ♪\nありがとにゃ♪", 104601, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1608, 2, 0, "うずうず…その指、\nじゃれついても\nいいかにゃ？", 104601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1609, 2, 0, "にゃ？　あたしに何か\nご入り用かにゃ？\nお安くしとくにゃ～♪", 104601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1610, 2, 0, "うーん、なんだか\n退屈にゃ～\n遊んで、遊んで～？", 104601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1611, 2, 0, "素早い動きのコツ？\nタダじゃ教えて\nあげられないにゃ～♪", 104601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1612, 2, 0, "そんなにあたしを\nなでたいにゃ？\nキミならなでさせて\nあげてもいいかにゃー", 104601, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1613, 2, 0, "ふにゃあ、ねえねえ、\n一緒にゴロゴロ\nしようにゃあ？", 104601, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1614, 2, 0, "いい朝にゃ！\n顔を洗ってくるにゃ！", 104601, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1615, 2, 0, "むーん、そろそろ\nお昼…猫たちにも\n猫缶を…", 104601, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1616, 2, 0, "ふわあ…眠いにゃ…\n猫は布団で\n丸くなるにゃ", 104601, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1617, 3, 0, "客商売では、\n外見のチェックも\n欠かせないのにゃ！", 104601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1618, 3, 0, "うにゃ～！\nお昼寝に\nぴったりにゃ", 104601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1619, 3, 0, "これ…量産したら\n子どもにバカ売れ\nしそうだにゃ！", 104601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1620, 3, 0, "この灯り、まるで\n猫みたいな\n癒し効果にゃ！", 104601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1621, 3, 0, "ふにゃあ、\nあったかいと猫は丸く\nなっちゃうのにゃあ…", 104601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1622, 3, 0, "本当は外で\n運動したいん\nだけどにゃ", 104601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1623, 3, 0, "むーん、\n美味しいにゃー！\nたい焼きに\n匹敵するにゃ！", 104601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1624, 3, 0, "ふむふむ…猫の\nしつけ方は、と…", 104601, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1626, 1, 0, "かわいいネコの置物を\nゲットしたにゃ！\nどこに飾ろうか\nにゃ～♪", 104602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1627, 1, 0, "仕事の交渉がうまく\n行ったにゃ～♪\nいい気分だにゃ～♪", 104602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1628, 1, 0, "ねこじゃらしには\n食いついちゃうにゃ\nびしっ！　びしっ！", 104602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1629, 1, 0, "ほっぺやお腹が、肉球\nぐらいぷにぷに…\nこれはまずいにゃ…", 104602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1630, 1, 0, "さっきのたいやき\nおいしかったにゃ♪\nごろごろごろ…", 104602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1631, 1, 0, "手作りのたい焼き\nあいつは食べて\nくれるかにゃ…？", 104602, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1632, 1, 0, "あいつには、いつか\nたっぷり恩返しが\nしたいにゃ…", 104602, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1633, 2, 0, "にゃーに？　あたしの\nことがそんなに気に\nなるのかにゃ？", 104602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1634, 2, 0, "あたしのこと…\nしっかり捕まえ\nといてにゃ？", 104602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1635, 2, 0, "あたしの力が\n必要かにゃ？　猫の手\n貸してあげるにゃ♪", 104602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1636, 2, 0, "たいやきとクレープを\n合わせれば…\n新しい商売のニオイが\nするにゃ…！", 104602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1637, 2, 0, "ふにゃあ、ねえねえ\n一緒にゴロゴロ\nしようにゃあ？", 104602, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1638, 2, 0, "キミと一緒にいると\n楽しくなってつい\nじゃれちゃうにゃ！", 104602, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1639, 2, 0, "いい朝にゃ！\n顔を洗ってくるにゃ！", 104602, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1640, 2, 0, "そろそろおっひる～\nにゃーんか\n食べたい…", 104602, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1641, 2, 0, "うーん、眠くなって\nきたにゃ。あたしは\n夜行性じゃないのにゃ", 104602, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1642, 3, 0, "にゃにゃ…\n髪が乱れてるにゃ", 104602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1643, 3, 0, "ふっかふかにゃ～\n猫がねころん\nじゃうにゃ", 104602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1644, 3, 0, "これ、猫と匹敵する\nぐらいかわいいにゃ！", 104602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1645, 3, 0, "猫でもこわくない\n優しい灯りにゃ！", 104602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1646, 3, 0, "ぽっかぽか\nしあわせな気分にゃ～", 104602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1647, 3, 0, "雨の日でも運動\nできるのは、濡れ\nなくて便利だにゃ！", 104602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1648, 3, 0, "美味しいにゃ～！\nこれ、朝昼晩\n食べたいにゃ！", 104602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1649, 3, 0, "本を読んで\n猫の気持ちを\n勉強するのにゃ！", 104602, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1651, 1, 0, "内職手伝える人\nいないかしら？", 104801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1652, 1, 0, "次の依頼は、\nこういう計画で…\nうん、完璧ね！", 104801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1653, 1, 0, "次の特売セールに\n向けて、しっかり\n準備しないと！", 104801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1654, 1, 0, "配達の後に売り子\n次に採取のバイト…\nうん、効率的ね", 104801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1655, 1, 0, "一日に受ける\nバイトの数、もう少し\n増やしたいわね", 104801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1656, 1, 0, "師匠として\n頼りにされたいし\nもっと頑張らなきゃ！", 104801, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1657, 1, 0, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 104801, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1658, 2, 0, "あら、\nちょうどよかった\n今から一緒に\nバイトでもどう？", 104801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1659, 2, 0, "相談事かしら？\nそれくらいなら\n無料でいいわよ", 104801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1660, 2, 0, "背後の気配なら\nすぐ分かるわ\n試してみる？", 104801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1661, 2, 0, "道具やお金の管理は\n大丈夫？　手伝うから\nいつでも言って", 104801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1662, 2, 0, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 104801, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1663, 2, 0, "ねえ、あの…\n私はキミにとって…\nいや、何でもないわ", 104801, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1664, 2, 0, "おはよう！\n今日も朝から\nバイトよ！", 104801, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1665, 2, 0, "お昼時ね。\n少し休憩したら、\nまた働くわよ！", 104801, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1666, 2, 0, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 104801, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1667, 3, 0, "接客業の依頼が\nある時は、外見も\nキレイにしないとね", 104801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1668, 3, 0, "はぁ～バイトで\n疲れたから癒される～", 104801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1669, 3, 0, "これを内職で\n作ったら\n儲かるかしら？", 104801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1670, 3, 0, "リフレッシュできたわ\nこれでまた、バリバリ\n働けるわね！", 104801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1671, 3, 0, "風邪を引いたら効率が\n落ちちゃうから、\n暖めないと！", 104801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1672, 3, 0, "働き続けるには\n健康第一！　それには\n運動が一番よ", 104801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1673, 3, 0, "もぐもぐ…\n腹が減っては\n戦ができぬ、よ！", 104801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1674, 3, 0, "日々絶えず学んで、\n生活に活かすのよ！", 104801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1676, 1, 0, "家計簿をつけなきゃ！\n覚えている時に\nやっておかないとね", 104802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1677, 1, 0, "次の依頼は\nこういう計画で…\nうん、完璧ね！", 104802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1678, 1, 0, "ギルドの経費\nあとちょっとだけ\n削れそうね…", 104802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1679, 1, 0, "少しでも時給のいい\nバイトがしたいけど\nなかなか\n出会えないのよ", 104802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1680, 1, 0, "今度のバイトも\nなかなか大変そう\nね…腕が鳴るわ", 104802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1681, 1, 0, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 104802, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1682, 1, 0, "私、どうしたんだろ？\n仕事中もずっと\n彼のこと考えてる…", 104802, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1683, 2, 0, "この間はヘルプに\n来てくれてありがとう\n頼んで正解だったわ", 104802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1684, 2, 0, "見つけた！　ちょうど\n頼みたいことが\nあるんだけど、いい？", 104802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1685, 2, 0, "今から\n休憩するところなの\n一緒にどう？", 104802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1686, 2, 0, "ん？　どうしたの？\n私のことが\n気になるの？", 104802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1687, 2, 0, "…たまには甘えても\nいい？　私だって\n弱る時はあるのよ", 104802, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1688, 2, 0, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 104802, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1689, 2, 0, "おはよう！\nいい朝ね。一緒に\nランニングでもいく？", 104802, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1690, 2, 0, "お昼時ね\n少し休憩したら\nまた働くわよ！", 104802, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1691, 2, 0, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 104802, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1692, 3, 0, "女の子らしく…\nどうも忘れちゃう\nのよね", 104802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1693, 3, 0, "ここで少し\n休んだら\nまたバイトよ！", 104802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1694, 3, 0, "これを内職で\n作ったら\n儲かるかしら？", 104802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1695, 3, 0, "仕事から帰ってきて\nこういうのがあると\n素敵ね", 104802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1696, 3, 0, "しっかり身体を\n温めて、血流を\nよくしなくっちゃ", 104802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1697, 3, 0, "これで休憩中も\n無駄なく\n鍛えられるわ♪", 104802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1698, 3, 0, "もぐもぐ…よく\n食べて、よく働く！\nそれが私のモットーよ", 104802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1699, 3, 0, "日々絶えず学んで\n生活に活かすのよ！", 104802, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1701, 1, 0, "暇だし、お料理と\nお掃除とお洗濯と\n買い物しようかな？", 104901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1702, 1, 0, "こうして歩いていれば\n弟くんが声をかけて\nくれるはず…", 104901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1703, 1, 0, "弟くんがどんな生活を\nしてるのか調べるのも\nお姉ちゃんの務め！", 104901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1704, 1, 0, "くんくん、\n弟くんのにおい…\n近くにいるね？", 104901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1705, 1, 0, "お姉ちゃんの痕跡を\n部屋中に\n残しちゃおっと♪", 104901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1706, 1, 0, "この部屋を、\n私と弟くんの\n愛の巣に…", 104901, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1707, 1, 0, "はぁ～…弟くん\n禁断症状だよ～", 104901, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1708, 2, 0, "弟くんの部屋は\nお姉ちゃんの\n部屋だよね～", 104901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1709, 2, 0, "いいこいいこ～♪\nどう、お姉ちゃんの\nナデナデは？", 104901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1710, 2, 0, "姉と弟の外出は\nデートって\nいうのかな？", 104901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1711, 2, 0, "もう！\nイタズラする子には\nお姉ちゃんチョップ！", 104901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1712, 2, 0, "お姉ちゃんが\nぎゅ～ってして\nあげるね♪", 104901, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1713, 2, 0, "あ、あのね…\nたまにはお姉ちゃんも\n甘えていい？", 104901, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1714, 2, 0, "着替えは？　荷物は？\nお出かけの準備は\nお姉ちゃんが手伝うね", 104901, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1715, 2, 0, "出かけるなら\n明るいうちに\n帰ってくるんだよ！", 104901, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1716, 2, 0, "まだ起きてるの？\nそれじゃあ、\nお姉ちゃんも\n夜更かししちゃお☆", 104901, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1717, 3, 0, "きれいなお姉ちゃん\n好きだよね♪", 104901, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1718, 3, 0, "ここにお姉ちゃんの\n匂いを、た～っぷり\n残しておこうっと♪", 104901, 0, 1);
