INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1731, 1, 0, "はぁ～…弟くん、\n禁断症状だよ～", 104902, 0, 8);
