INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1746, 3, 0, "これ、弟くんみたいに\nあったかいね～", 104902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1747, 3, 0, "ダイエットダイエット\nきれいなお姉ちゃんに\nならなくちゃ", 104902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1748, 3, 0, "弟くんの手作りなら\nもっとおいしくなると\n思うんだけどなぁ～", 104902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1749, 3, 0, "弟くんをきちんと\n教育するために\nお姉ちゃんは勉強を\nかかさないのだ☆", 104902, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1751, 1, 0, "ん～、エレガントで\n大人なお部屋ね！\nアタシにぴったり！", 105001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1752, 1, 0, "身長とバスト以外は\n完璧な大人\nなのになぁ～", 105001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1753, 1, 0, "アタシの大人コーデで\nあいつも夢中ね♪", 105001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1754, 1, 0, "朝顔の観察したら\n理系女子になれる\nかしら？", 105001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1755, 1, 0, "レディは、どんな時も\nおしとやかに…って\nアタシも騒ぎたいー！", 105001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1756, 1, 0, "大人っぽい子が\n多いわね…アタシも\n負けてられないわ！", 105001, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1757, 1, 0, "アタシが待ってる\nなんて、アイツは\n幸せものよねー", 105001, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1758, 2, 0, "わーい、アメだー♪\n食べる食べるー♪", 105001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1759, 2, 0, "熱いハニーラテが\n飲みたーい！\n淹れてー淹れてー！", 105001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1760, 2, 0, "なによ？　もしかして\n好きな子にイタズラ\nしたくなるってやつ？", 105001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1761, 2, 0, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ、\nデザインとかの話よ！", 105001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1762, 2, 0, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？", 105001, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1763, 2, 0, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！", 105001, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1764, 2, 0, "朝のスキンケアは\n大事なの！", 105001, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1765, 2, 0, "お腹すいたー\nダイエット\nやめちゃおうかなー", 105001, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1766, 2, 0, "エイジングケアを\nしないとねー", 105001, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1767, 3, 0, "この角度のほうが\n大人っぽく見えるかな\nそれともこっちかしら", 105001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1768, 3, 0, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！", 105001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1769, 3, 0, "か、かわいい♪　けど\n大人はこんなもの…\nでも、かわいいし～！", 105001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1770, 3, 0, "オシャレ～♪\n大人なレディって\n感じよね♪", 105001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1771, 3, 0, "あったかーい♪\nここで宿題\nしようかしら？", 105001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1772, 3, 0, "運動したら成長\nしないかしら？\n背とか胸とか…", 105001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1773, 3, 0, "食べすぎると\n太っちゃうな…\nでも、おいしいし～っ", 105001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1774, 3, 0, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？", 105001, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1776, 1, 0, "レディは、どんな時も\nおしとやかに…って\nアタシも騒ぎたいー！", 105002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1777, 1, 0, "恋人にするなら\nイケメンか\nただイケよね～", 105002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1778, 1, 0, "もう少し背が欲しいな\n…もっと牛乳を\n飲まなくちゃ！", 105002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1779, 1, 0, "朝顔の観察したら\n理系女子になれる\nかしら？", 105002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1780, 1, 0, "こ、これは補習の\nお知らせプリント！\nアイツに見つかる前に\n隠さなくちゃ！", 105002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1781, 1, 0, "アイツおっそいなー\n別に会いたいわけじゃ\nないけど…\n来ないと気になる…", 105002, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1782, 1, 0, "アタシが待ってる\nなんて、アイツは\n幸せものよねー ", 105002, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1783, 2, 0, "なーに？\nアタシに甘えたいの？", 105002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1784, 2, 0, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ\nデザインとかの話よ！", 105002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1785, 2, 0, "頭撫でないで！\nお子様みたいに\nあつかわないでって\n言ってるのに～！", 105002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1786, 2, 0, "壁ドンの練習…\nあれ？　壁ドンって\n男がするんだっけ？", 105002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1787, 2, 0, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？", 105002, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1788, 2, 0, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！", 105002, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1789, 2, 0, "レディの朝は\n忙しいのよ", 105002, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1790, 2, 0, "お腹すいたー\nダイエット\nやめちゃおうかなー", 105002, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1791, 2, 0, "エイジングケアを\nしないとねー", 105002, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1792, 3, 0, "身だしなみチェック\nオッケー！　これで\nいつアイツが来ても\n大丈夫ね！", 105002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1793, 3, 0, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！", 105002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1794, 3, 0, "あたしがもっと\nオシャレにかわいく\nしてあげようか？", 105002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1795, 3, 0, "オシャレ～♪\n大人なレディって\n感じよね♪", 105002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1796, 3, 0, "あったかーい♪\nここで宿題\nしようかしら？", 105002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1797, 3, 0, "運動したら成長\nしないかしら？\n背とか胸とか…", 105002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1798, 3, 0, "レディは、食べ物にも\n気を使うのよ", 105002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1799, 3, 0, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？", 105002, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1801, 1, 0, "魔法と科学\nどっちも極めて\nみせるわ", 105101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1802, 1, 0, "仕事も片付いたし\n部屋で\n実験実験…♪", 105101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1803, 1, 0, "たまった治療代の\n請求書を\nまとめないと…", 105101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1804, 1, 0, "新薬が出来たし\nすぐに試したいけど…\n実験台がいないわ", 105101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1805, 1, 0, "部屋にこもりすぎると\n曜日の感覚が\nなくなるわね", 105101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1806, 1, 0, "あの子の記憶を\n取り戻せる薬…\n作れないものかしら", 105101, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1807, 1, 0, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 105101, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1808, 2, 0, "あら、ヒマなら\n薬草の整理を\n手伝って", 105101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1809, 2, 0, "もしかして実験に\n付き合ってくれるの？\nえ？　違う？", 105101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1810, 2, 0, "具合が悪い？\nなら診てあげる\nまずは触診から…", 105101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1811, 2, 0, "植物に興味あるの？\nなら教えてあげるわ\n色々とね…", 105101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1812, 2, 0, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 105101, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1813, 2, 0, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 105101, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1814, 2, 0, "お昼までに調合用の\n花を集めたいの\n手伝ってくれる？", 105101, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1815, 2, 0, "あら、もうお昼？\nそろそろ一息\n入れましょうか", 105101, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1816, 2, 0, "夜も更けて来たわね\n私は朝まで実験だけど\n見学していく？", 105101, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1817, 3, 0, "医者たるもの\nいつだって\n清潔に…♪", 105101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1818, 3, 0, "いいわねこれ\n仮眠の時に\n使わせてもらうわ", 105101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1819, 3, 0, "ぬいぐるみなら\n動物も…", 105101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1820, 3, 0, "いい香り…\n仕事場にも\n置いてみようかしら", 105101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1821, 3, 0, "ここにいると\nついウトウト\nしちゃうわ…", 105101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1822, 3, 0, "座ってる時間が多いと\n運動不足になるし\nたまにはいいわね", 105101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1823, 3, 0, "研究していると\nつい食べるのを\n忘れちゃうのよね", 105101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1824, 3, 0, "実験もいいけど\nいつか成果を\n本にしないと…", 105101, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1826, 1, 0, "徹夜はツラいけど\n集中すると時間の\n感覚がなくなって…", 105102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1827, 1, 0, "痛みに悶える顔って\nどうしてあんなに\n興奮するのかしら？", 105102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1828, 1, 0, "彼に罵られたら\nどんな気分かしら\n意外と快感かも？", 105102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1829, 1, 0, "毒を薄めると\n薬になるのって\n今でも不思議だわ", 105102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1830, 1, 0, "綺麗なバラには\nトゲがあるけど\n魅力の一つよね", 105102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1831, 1, 0, "カレをさりげなく\n惚れさせる方法を\n研究するわよ…♪", 105102, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1832, 1, 0, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 105102, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1833, 2, 0, "構って欲しいの？\nじゃあ、君の体を\n調べさせて？", 105102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1834, 2, 0, "実験に付き合って\n大丈夫よ、試すのは\nただの栄養剤だから", 105102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1835, 2, 0, "欲しい薬が\nあったら言ってね\n調合してみるから", 105102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1836, 2, 0, "おしおきされたいの？\nキミにだったら\nいつでもしてあげる", 105102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1837, 2, 0, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 105102, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1838, 2, 0, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 105102, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1839, 2, 0, "朝しか取れない\n植物があるの\n採取しに行くわよ", 105102, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1840, 2, 0, "お昼時ってやけに\n眠たくなるわよね\n…調べたくなるわ", 105102, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1841, 2, 0, "今日くらいは\nゆっくり眠りたいわ\n…キミと一緒にね", 105102, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1842, 3, 0, "身なりについて\n研究するのも\n楽しそうね", 105102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1843, 3, 0, "休息に必要な道具は\n妥協せずに選ぶ！\nこれだけは譲れないわ", 105102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1844, 3, 0, "たまにはこういう\n可愛らしいものに\n触れないとね♪", 105102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1845, 3, 0, "いい香り…\n仕事場にも\n置いてみようかしら", 105102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1846, 3, 0, "仕事がなかったら\nこのまますーっと\n眠りたいのに…", 105102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1847, 3, 0, "運動しないと\n体型の維持が…", 105102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1848, 3, 0, "適度な食事も\n大切よね", 105102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1849, 3, 0, "まさか私が\n恋愛読本を読む\n日が来るなんて…", 105102, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1851, 1, 0, "ぺっぺっ！\n…い、いけないっ！", 105201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1852, 1, 0, "いっけな～い！\nひづめで床に傷が！", 105201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1853, 1, 0, "太っちゃったかも～！\n伐採でダイエット\nしなくっちゃ！", 105201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1854, 1, 0, "きちんと生活すると\n毛づやが良くなる\nみたいなのよね～", 105201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1855, 1, 0, "みんな、体重\nどれくらいなのかな？", 105201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1856, 1, 0, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 105201, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1857, 1, 0, "いつか人間の女の子\nみたいなデート、\nしてみたいな～♪", 105201, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1858, 2, 0, "私ね「どこにでもいる\n普通の女の子」って\n言葉が憧れなんだ～♪", 105201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1859, 2, 0, "チャームポイント？\n自分では分からない\nけど…まつげかなぁ？", 105201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1860, 2, 0, "リマリマ～♪\nはっ！？\nわ、私ったら今何を？", 105201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1861, 2, 0, "力仕事ならまかせて！", 105201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1862, 2, 0, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 105201, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1863, 2, 0, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 105201, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1864, 2, 0, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 105201, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1865, 2, 0, "ランチは\nおしゃれなカフェで\nしてみたいな～♪", 105201, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1866, 2, 0, "お肌のケアに\nパックしながら\n寝ようかな～", 105201, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1867, 3, 0, "やだっ、\nまた寝ぐせついてる！\n直さないと！", 105201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1868, 3, 0, "ミシッっていった！\n私、重すぎるかも！？", 105201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1869, 3, 0, "あ！　あなたも\n美人さんね～♪", 105201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1870, 3, 0, "はぁ、まったり…\n四足歩行しちゃい\nそうだわ…", 105201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1871, 3, 0, "あったか～い♪\n…私、毛皮だから\n暑すぎるかも", 105201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1872, 3, 0, "な、なんだか野生に\n帰っちゃいそう！", 105201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1873, 3, 0, "ぱくぱく、もぐもぐ、\nぺっぺっ…\nやだ、はしたない！", 105201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1874, 3, 0, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 105201, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1876, 1, 0, "素敵ね～、居るだけで\n女子力があがりそうな\nお部屋だわ～", 105202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1877, 1, 0, "私、大丈夫かしら？\n他の子に怖がられたり\nしないかな…？", 105202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1878, 1, 0, "動物苑…って\n嫌なギルド名ね…", 105202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1879, 1, 0, "好き、嫌い、好き…\nあっ！　うっかりして\n花占いのお花\n食べちゃった！", 105202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1880, 1, 0, "ここ、女の子らしい\nものでいっぱいね～！\nときめいちゃうわ～♪", 105202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1881, 1, 0, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 105202, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1882, 1, 0, "あの人が\n来てくれるまで薪割り\nでもしてよっかな？", 105202, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1883, 2, 0, "一緒にお出かけ？\nうれしいけど…\n心の準備が！\nはわわわ！", 105202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1884, 2, 0, "どうしたの？\n急に抱きついてきて…\n癒されたいの？", 105202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1885, 2, 0, "ぺっぺっ！\n…い、いけないっ！", 105202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1886, 2, 0, "メタモルアップルを\nここで食べたら、\nみんな驚くわよね", 105202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1887, 2, 0, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 105202, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1888, 2, 0, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 105202, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1889, 2, 0, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 105202, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1890, 2, 0, "ダイエット中だから\nお昼は抜いてるの～", 105202, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1891, 2, 0, "お肌のケアに\nパックしながら\n寝ようかな～", 105202, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1892, 3, 0, "ちゃんとかわいく\nしてないと刈られ…\n狩られちゃうもんね！", 105202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1893, 3, 0, "ミシッっていった！\n私、重すぎるかも！？", 105202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1894, 3, 0, "あ！　あなたも\n美人さんね～♪", 105202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1895, 3, 0, "いい香り～\n森の中にいるみたいに\nとっても落ち着く～", 105202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1896, 3, 0, "素敵～♪　…\nきゃっ！　私の毛が\nたいへんなことに～！", 105202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1897, 3, 0, "な、なんだか野生に\n帰っちゃいそう！", 105202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1898, 3, 0, "何を食べるかより\n誰と食べるかが\n大事なのよね", 105202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1899, 3, 0, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 105202, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1901, 1, 0, "ヴァイスフリューゲル\nを盛り上げる方法、\n共に語らおう！", 105301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1902, 1, 0, "私は子供ではない…！\nが、お菓子は\nいただこう！", 105301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1903, 1, 0, "部屋はきちんと\n片づけておかなくては\n部屋の汚れは\n心の汚れだ", 105301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1904, 1, 0, "むむむ…明日\n目が覚めたら、身長が\n伸びていないものか…", 105301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1905, 1, 0, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 105301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1906, 1, 0, "あいつとの新たな\n鍛錬が\n待ち遠しいな…！", 105301, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1907, 1, 0, "最近、あいつと\n過ごしていて、毎日が\n充実しているな…", 105301, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1908, 2, 0, "軍人の本分は日々の\n研鑽にあるからな。\n今日も鍛錬に\n行かないか？", 105301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1909, 2, 0, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 105301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1910, 2, 0, "貴公にも、飴をくれて\nやろうか？\nあまいぞ～！\n美味しいぞ～！", 105301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1911, 2, 0, "私ともっと高く、\n共に飛ぼう！", 105301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1912, 2, 0, "貴公は、本当に頼りに\nなる男だな…いや、\nふとそう思っただけだ", 105301, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1913, 2, 0, "貴公と共にいられる\nことは私の喜びだ。\nこれからも\nよろしく頼むぞ", 105301, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1914, 2, 0, "うむ、いい朝だ。\n本国の夜明けを\n思い出してしまうな！", 105301, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1915, 2, 0, "そろそろ昼食に\nするか。腹が減っては\n戦はできないからな", 105301, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1916, 2, 0, "ふわああ…私は子供\nではないが…眠い時は\n眠いのだ…", 105301, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1917, 3, 0, "身だしなみは、心の\n在りようを映すから\n気をつけなくてはな", 105301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1918, 3, 0, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 105301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1919, 3, 0, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 105301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1920, 3, 0, "このともし火、戦いに\n明け暮れる我らの心を\n癒すものだな…", 105301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1921, 3, 0, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 105301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1922, 3, 0, "うおおおおおっ！\n鍛錬、鍛錬、\n鍛錬っ！！", 105301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1923, 3, 0, "いつ戦いになっても\n困らぬように、\n腹八分目に\nしておくのだ！", 105301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1924, 3, 0, "これを読んで、新たな\n戦術を編み出すぞっ！", 105301, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1926, 1, 0, "たまには掃除でも\nするか。部屋が\n綺麗になるのは\n喜ばしいことだ", 105302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1927, 1, 0, "部屋の中では\n剣を振り回せないのが\n玉に瑕だな…", 105302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1928, 1, 0, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 105302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1929, 1, 0, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 105302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1930, 1, 0, "家庭を守れない者が\n国を守れるはずが\nない。この部屋も\n私が守るぞ！", 105302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1931, 1, 0, "あいつは今頃、何を\nしているのだろうな…\nふふっ", 105302, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1932, 1, 0, "あいつと会える日が\nこんなにも待ち遠しく\nなるとはな…", 105302, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1933, 2, 0, "軍人の本分は日々の\n研鑽にあるからな\n今日も鍛錬に\n行かないか？", 105302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1934, 2, 0, "おお、今日も鍛錬の\n時間だったか！\nすまないな\nすぐ行く！", 105302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1935, 2, 0, "私は子供ではない！\nが…たまには童心に\n帰りたい時もある…", 105302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1936, 2, 0, "貴公にも、飴を\nくれてやろうか？\nあまいぞ～！\n美味しいぞ～！", 105302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1937, 2, 0, "私と歩きたいのか…？\nそうだな。二人で\n散歩でも行こうか", 105302, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1938, 2, 0, "貴公と共にいられる\nことは私の喜びだ\nこれからも\nよろしく頼むぞ", 105302, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1939, 2, 0, "うむ、いい朝だ\n本国の夜明けを\n思い出してしまうな！", 105302, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1940, 2, 0, "そろそろ昼時だな\n鍛錬の手を緩めて\n昼食にするか", 105302, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1941, 2, 0, "寝る前に…あと少し\nだけ、鍛錬を…", 105302, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1942, 3, 0, "ヴァイス\nフリューゲルの一員と\nして恥ずかしくないよう\n身なりを整えなくては", 105302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1943, 3, 0, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 105302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1944, 3, 0, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 105302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1945, 3, 0, "このともし火\n戦いに明け暮れる\n我らの心を\n癒すものだな…", 105302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1946, 3, 0, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 105302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1947, 3, 0, "うおおおおおっ！\n鍛錬、鍛錬\n鍛錬っ！！", 105302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1948, 3, 0, "いつ戦いになっても\n困らぬように\n腹八分目に\nしておくのだ！", 105302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1949, 3, 0, "うむ…脳細胞が\n活性化してきたぞ\n読書は大事だな", 105302, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1951, 1, 0, "私たちの騎空艇\nどんなデザインに\nしよう…？", 105701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1952, 1, 0, "団員募集の\nポスター\n作ろうっと♪", 105701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1953, 1, 0, "父さん…\n今頃どこで\n何してるんだろ？", 105701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1954, 1, 0, "騎空艇に乗って、\n誰もいったことがない\n場所を見つけたいな", 105701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1955, 1, 0, "父さん以上の\n世界一の\n騎空士になりたい！", 105701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1956, 1, 0, "仲間と行く空の旅…\n何度想像しても\nドキドキするなぁ", 105701, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1957, 1, 0, "父さん…\n私にもね、大切な\n仲間ができたよ", 105701, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1958, 2, 0, "仕事の依頼？\nすぐに支度\nしてくるね！", 105701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1959, 2, 0, "ねえねえ！\n団員募集のポスター\n一緒に作ろうよ！", 105701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1960, 2, 0, "なーにー？\nどうしたの？\n今呼んだよね？", 105701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1961, 2, 0, "今は休憩中だよ？\nだからあなたも\nゆっくりしてて", 105701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1962, 2, 0, "私にとってあなたは\nとっても頼りになる、\n自慢の団員だよ！", 105701, 0, 4);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1963, 2, 0, "お父さんに会えたら、\n大切な仲間として\nあなたを紹介するね！", 105701, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1964, 2, 0, "おはよう！\n体の調子はどう？\n今日は頑張れそう？", 105701, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1965, 2, 0, "ねえねえ、お昼\n食べに行こうよ！\nお腹空いちゃった", 105701, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1966, 2, 0, "…あれ？\nおしゃべりしてたら\nもうこんな時間？", 105701, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1967, 3, 0, "どうせならオシャレな\n騎空士って\n呼ばれたいな", 105701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1968, 3, 0, "ふぅ、疲れた～…\nちょっとだけ\n休んじゃおっと", 105701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1969, 3, 0, "よし！　キミは\n今から団長代理だ！\n…なんてね♪", 105701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1970, 3, 0, "いい香り～♪\n気持ちが\n落ち着く～…", 105701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1971, 3, 0, "これって\n騎空艇に\n置けるかな…？", 105701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1972, 3, 0, "どんな仕事でも\nこなせるような\n体力がないとね…！", 105701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1973, 3, 0, "あむっ…うん！\n美味しい～！\n今度作ってみよう♪", 105701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1974, 3, 0, "空を冒険するには\n色んなことを\n知っておかないと…", 105701, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1976, 1, 0, "…戻らなくなったよ\nどうすれば元に\n戻る…？", 105702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1977, 1, 0, "暇だよ…\nどこかに強い人は\nいないか？", 105702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1978, 1, 0, "ジョブチェンジ！\n…叫んでも\n戻らないよ", 105702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1979, 1, 0, "今のうちに、\n魔物退治の\n仕事に行くね！", 105702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1980, 1, 0, "ハァ～…\nうん、気の巡りは\n問題ないね！", 105702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1981, 1, 0, "私の力の源は\n仲間との\n絆だよ！", 105702, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1982, 1, 0, "いつかこの拳を\n空を越えた、星まで\n届かせたいね", 105702, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1983, 2, 0, "功夫が足りない人は\nどうすればいいか？\nもちろん修行だよ！", 105702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1984, 2, 0, "私はいつも通りに\n喋ってるつもり\nだよ？", 105702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1985, 2, 0, "どうした？\nもしかして手合せ\nしたいか？", 105702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1986, 2, 0, "騎空士としても\n拳法家としても\n目指すは世界一ね！", 105702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 1987, 2, 0, "背中はあなたに\n預けたよ！\nよろしくね！", 105702, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 102, 1988, 2, 0, "あなたと一緒なら\nきっと、今よりもっと\n強くなれるよ！", 105702, 0, 8);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1989, 2, 0, "ちゃんと起きてるか？\n寝ぼけてたら\nダメよ？", 105702, 1, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1990, 2, 0, "腹減ったね…\nなにか食べに\n行くよ！", 105702, 2, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1991, 2, 0, "修行のしすぎは\n良くないよ？\n気をつけるね", 105702, 3, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1992, 3, 0, "美しい技は\n美しい体が\nあってこそ！", 105702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 1993, 3, 0, "よいしょっと…\nちょっとのんびり\nさせてもらうよ", 105702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 1994, 3, 0, "…練習に\n使うのはさすがに\n可哀そうね", 105702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 1995, 3, 0, "これは…\n瞑想の時にも\n使ってみたいね", 105702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 1996, 3, 0, "出来ればもっと\n熱いほうが\n嬉しいよ", 105702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 6, 1997, 3, 0, "ここでも\n修行出来るなんて\n最高だよ！", 105702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 7, 1998, 3, 0, "気力の源は体力！\n体力の源は\n健康とご飯！", 105702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1999, 3, 0, "んー…\n難しいことは\nわかんないよ！", 105702, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 2001, 1, 0, "わたしたちの\nギルドハウスへ\nようこそ～！", 105801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 2, 2002, 1, 0, "ぐるるる～…\nあ、お腹が\n鳴っちゃいました…", 105801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 3, 2003, 1, 0, "ん？\n食べ物のにおい？", 105801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 4, 2004, 1, 0, "みんなと仲良く食卓を\n囲みたいですね～", 105801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 5, 2005, 1, 0, "この部屋が、わたしの\n帰る場所です♪", 105801, 0, 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 101, 2006, 1, 0, "あの人を見ると\n胸が満たされます～\nお腹は空いてますが！", 105801, 0, 4);
