INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 1, 100101, "ここに困ってる人は\nいないよね…？\nよかった♪", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 2, 100101, "冒険の準備準備…\nうん、忘れ物はなし！\n…あっ！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 3, 100101, "リズムを意識して…\nわんつーわんつー！\nうん、調子はバッチリ", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 4, 100101, "ここで特訓していい？\n物は壊さないように\n気をつけるね！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 5, 100101, "なんか落ち着かないや\nもしかして、\n困ってる人が\n近くにいるのかな…？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 4, 101, 0, 1, 6, 100101, "えへへ…いつも\n一緒にいてくれて\nありがとねっ！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 7, 100101, "ちゃんと覚えてるよ\n騎士くんの声…\nあの時はありがとね！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 8, 100101, "あははっ！\nやったなー！\nお返しだー！\nこちょこちょこちょ…", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 9, 100101, "どうしたの？\nもしかして困ってる？\nあたしにまかせて！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 10, 100101, "えっとね…しっぽは\n触っちゃダメだよ？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 11, 100101, "反応しない特訓…\nだったけど無理～！\n悔しいよぉ～！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 4, 101, 0, 1, 12, 100101, "ふぁふぃ？\nやられた～！　ほっぺ\nぷにぷにしないでぇ～", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 13, 100101, "えへへ…\n騎士くんの手だ…\nあったかいな…♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1, 0, 1, 14, 100101, "おはよー！\n今日も元気に\nいっちゃうよー！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (2, 1, 2, 0, 1, 15, 100101, "ユイちゃんに\nおやつ作って\nもらおうかな～♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (3, 1, 3, 0, 1, 16, 100101, "寝るのも修行だって\nこないだレイさんが\n言ってたんだ…", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 17, 100101, "ちゃんとケアして\n傷が残らないように\nしないと…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 18, 100101, "一回休むと\nこのままずるずる\nいきそう…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 19, 100101, "新しい練習相手に…\nって冗談だよ冗談！\n本気じゃないから！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 20, 100101, "落ち着くなぁ～\n心だけじゃなくて\n体もほぐれそう♪", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 21, 100101, "あったかい所に\nいると体が丸く\nなっちゃう…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 6, 0, 1, 22, 100101, "いつ何が起きるか\n分からないし、\nもっと力をつけないと", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 7, 0, 1, 23, 100101, "美味しいけど、\n食べ過ぎないように\nしないと…！！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 8, 0, 1, 24, 100101, "文字が多いと\n眠くなっちゃう…\n危ない危ない！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 26, 100102, "リズムを意識して…\nわんつーわんつー！\nうん、調子はバッチリ", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 27, 100102, "のんびりするのって\n退屈かも…\n何かして遊ばない？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 28, 100102, "留守番はちょっと\n苦手かも…\nなんか寂しいんだもん", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 29, 100102, "あたしのお友達も\n呼んできていい？\nきっと楽しいよ！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 30, 100102, "誰も困ってないよね？\n…何をすれば\nいいのか分かんないや", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 101, 0, 1, 31, 100102, "ちゃんと覚えてるよ\n騎士くんの声…\nあの時はありがとね！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 32, 100102, "…なんであたし\n騎士くんばっかり\n見てるんだろ…", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 33, 100102, "反応しない特訓…\nだったけど無理～！\n悔しいよぉ～！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 34, 100102, "触るのって楽しい？\n…そっか\n少しだけだよ…？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 35, 100102, "あたしの元気を\nわけてあげるね！\nフレー！　フレー！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 36, 100102, "何かムズムズする！？\nって騎士くんかぁー\nどしたの？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 101, 0, 1, 37, 100102, "ひゃうっ！　もう…\nしっぽはだめだってば\n次やったらデコピン！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 38, 100102, "えへへ…\n騎士くんの手だ…\nあったかいな…♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1, 0, 1, 39, 100102, "朝ごはんと運動！\nこれがあたしの\n元気の素だよ！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (2, 1, 2, 0, 1, 40, 100102, "ユイちゃんに\nおやつ作って\nもらおうかな～♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (3, 1, 3, 0, 1, 41, 100102, "寝るのも修行だって\nこないだレイさんが\n言ってたんだ…", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 42, 100102, "これでよし♪\n騎士くん\n気づくかな…？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 43, 100102, "一回休むと\nこのままずるずる\nいきそう…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 44, 100102, "新しい練習相手に…\nって冗談だよ冗談！\n本気じゃないから！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 45, 100102, "こういうの\nなんか気になって\nじーって見ちゃうんだ", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 46, 100102, "あったかい所に\nいると体が丸く\nなっちゃう…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 6, 0, 1, 47, 100102, "いつ何が起きるか\n分からないし\nもっと力をつけないと", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 7, 0, 1, 48, 100102, "お腹が減ると\nなんか力が\n出ないんだよね…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 8, 0, 1, 49, 100102, "色んなことを\n知っておくのは\n大事だよね！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 51, 100201, "お片付けも済んだし\n今度は洗濯ものを\nたたもうかな", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 52, 100201, "この気持ち\nもっとちゃんと\n伝えなくっちゃ…", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 53, 100201, "お茶をいれようと\n思うんだけど\n他に飲みたい人は\nいるかな？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 54, 100201, "戦いは\n得意じゃないけど\nお料理なら…！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 55, 100201, "こういう平和な\n時間が、もっと\n続くといいな", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 4, 101, 0, 1, 56, 100201, "わたし…\nもうちょっとだけ勇気\n出してみようかな", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 57, 100201, "みんなのことが大好き\nも、もちろん、\n騎士クンのことも…\nな、仲間として！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 58, 100201, "ひゃうっ！\nな、なに？\nびっくりしたぁ…", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 59, 100201, "男の子って、\nやっぱりおっきいね。\nその…手とか\n他にもいろいろ…", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 60, 100201, "困ったことが\nあったら\nいつでも話してね", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 61, 100201, "え！　む、虫っ！？\n取って取って！\nはやく～！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 4, 101, 0, 1, 62, 100201, "あのね…\n一緒におでかけ\nしたいんだけど…\nダメかな？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 63, 100201, "騎士クンに\n伝えたいことがあるの\nとっても大切な…\nこ、今度、話すね！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1, 0, 1, 64, 100201, "お、おはよう！\nあの…朝ごはんは\nもう食べちゃった？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (2, 1, 2, 0, 1, 65, 100201, "お昼休みだね\nあの…良かったら\nお弁当…食べない？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (3, 1, 3, 0, 1, 66, 100201, "遅くまでお疲れ様\n明日も頑張ろうね\nふわぁ…", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 67, 100201, "よし…\nこれなら\n褒めてもらえるかな？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 68, 100201, "がんばることも\n必要だけど、\nお休みも大切だよね", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 69, 100201, "かわいいなぁ…\nわたしも今度\n作ってみよう。\nえっと、材料は…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 70, 100201, "戦う前にもこうやって\n落ち着けたら\nいいのに", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 71, 100201, "…はへっ？\nも、もしかして今\n寝ちゃってた…？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 6, 0, 1, 72, 100201, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 7, 0, 1, 73, 100201, "おいしいものって\n食べ過ぎちゃうけど…\nしょうがない…よね？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 8, 0, 1, 74, 100201, "…そっか\nこうすれば魔法が\nもっと強くなるんだ", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 76, 100202, "洗濯と掃除\nどっちから\nはじめよう？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 77, 100202, "新しい魔道書を\n買いに行かなくちゃ\nもっとたくさん\n魔法を覚えたいな", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 78, 100202, "お茶をいれようと\n思うんだけど\n他に飲みたい人は\nいるかな？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 79, 100202, "今日はどんな料理に\n挑戦しよう…♪", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 80, 100202, "この気持ち\nもっとちゃんと\n伝えなくっちゃ…", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 101, 0, 1, 81, 100202, "みんなのことが大好き\nも、もちろん\n騎士クンのことも…\nな、仲間として！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 82, 100202, "騎士クンのそばに\nいられるだけで\n幸せだよ", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 83, 100202, "えっと、その…\nこんな所で\nこういうことされると\n恥ずかしいよ…", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 84, 100202, "え！　む、虫っ！？\n取って取って！\nはやく～！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 85, 100202, "あ、あの…実は\n騎士クンのこと…\nううん、やっぱり\nなんでもない", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 86, 100202, "男の子って\nやっぱりおっきいね\nその…手とか\n他にもいろいろ…", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 101, 0, 1, 87, 100202, "騎士クンに\n伝えたいことがあるの\nとっても大切な…\nこ、今度、話すね！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 88, 100202, "ふふふっ…そんなとこ\nツンツンしないで\nくすぐったいよ～", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1, 0, 1, 89, 100202, "おはよう\nよく眠れた？　今日も\n一日、頑張ろうね♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (2, 1, 2, 0, 1, 90, 100202, "お腹空いちゃった…\nそうだ。騎士クンも\n一緒にお昼食べない？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (3, 1, 3, 0, 1, 91, 100202, "もうこんな時間…\nそろそろ寝なくちゃ\nおやすみなさい", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 92, 100202, "よし…\nこれなら\n褒めてもらえるかな？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 93, 100202, "がんばることも\n必要だけど\nお休みも大切だよね", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 94, 100202, "かわいい～♪\n騎士クンは\nこういうのって好き？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 95, 100202, "戦う前にもこうやって\n落ち着けたら\nいいのに", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 96, 100202, "あの…！\nよ、良かったら\n一緒に…！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 6, 0, 1, 97, 100202, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 7, 0, 1, 98, 100202, "最近服のサイズが\n合わないんだけど…\n食べ過ぎなのかな？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 8, 0, 1, 99, 100202, "…やっぱり\n月刊恋愛指南は\nためになる…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 101, 100301, "装備の手入れでも\nしようかな…", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 102, 100301, "次に鍛えるべきは\n肉体面か精神面か\nどちらにしよう…？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 103, 100301, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 104, 100301, "仲間なんていらない\nそう思ってた頃も\nあったな…", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 105, 100301, "考えごとをしていると\nつい部屋中を\n歩いてしまうな", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 4, 101, 0, 1, 106, 100301, "彼は\nどこまで強くなるのか\n私も負けられないな", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 107, 100301, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 108, 100301, "ああ、キミだったのか\n人の気配がしたから\n誰かと思ったよ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 109, 100301, "斬りかかるつもりなら\nちゃんとかかってきて\n…呼んだだけ？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 110, 100301, "なに？　私に相談？\n戦いについてなら\n喜んで", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 111, 100301, "今のが突き？\n軽い！　もっと鋭く！\nさあ、もう一回！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 4, 101, 0, 1, 112, 100301, "ひゃっ！　ど、どこを\n触ってるの！？\n…油断した", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 113, 100301, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1, 0, 1, 114, 100301, "おはよう\n今日も充実した\n一日にしていこう", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (2, 1, 2, 0, 1, 115, 100301, "そろそろ\nお茶の時間か\nキミも一緒にどう？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (3, 1, 3, 0, 1, 116, 100301, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 117, 100301, "戦いを経てもなお\n美しくあるように…\nこれでよし♪", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 118, 100301, "戦ってばかりで\n休まないと、また\nユイに怒られるな", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 119, 100301, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 120, 100301, "これはいい…\n戦いの前の精神統一に\nもってこいじゃないか", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 121, 100301, "時々は落ち着いた\n時間を過ごすのも\n悪くない…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 6, 0, 1, 122, 100301, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 7, 0, 1, 123, 100301, "食事は大切だけど\n食べ過ぎには\n注意しておこう…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 8, 0, 1, 124, 100301, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 126, 100302, "海だけじゃなくて\n川でも釣りがしたいな\n道具も新調しよう", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 127, 100302, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 128, 100302, "彼は\nどこまで強くなるのか\n私も負けられないな", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 129, 100302, "そろそろ修行の\n内容を新しくしよう\nより厳しいものにね", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 130, 100302, "強くなって…誰にも\n妨げられない自由な\n生き方がしたい", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 101, 0, 1, 131, 100302, "まさか風邪を引くとは\n彼の看病のおかげで\nすっかり良く\nなったけど", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 132, 100302, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 133, 100302, "どうしたの？\nなんでもない？\nそれなら良かった", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 134, 100302, "あんまり突っつくと\nお返しするよ？\n覚悟は出来てる？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 135, 100302, "ねぇ、今度また\n釣りに行かない？\n大きいのを狙おう", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 136, 100302, "修行がしたいの？\nそれを早く言ってよ\nすぐに出発しよう！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 101, 0, 1, 137, 100302, "ひょっとして\n今のも一本だなんて\n言わないよね？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 138, 100302, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1, 0, 1, 139, 100302, "今日は仕事？\nそれとも修行？\n良ければ付き合うよ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (2, 1, 2, 0, 1, 140, 100302, "休息も時には\n必要だと、仲間から\n教わったよ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (3, 1, 3, 0, 1, 141, 100302, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 142, 100302, "髪の長さを少し\n変えてみようかな…\nわ、私は何を…！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 143, 100302, "休息があってこそ\n万全な状態で\n戦えるんだ", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 144, 100302, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 145, 100302, "体と同様に\n心もほぐれるね…\nはぁ…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 146, 100302, "休む時は休む\nヒヨリもそろそろ\n覚えて欲しいな…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 6, 0, 1, 147, 100302, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 7, 0, 1, 148, 100302, "修行のせいか\n最近食べる量が\n増えてきたような…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 8, 0, 1, 149, 100302, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 151, 100401, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 152, 100401, "落とし穴…どこに\n作ったんだっけ？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 153, 100401, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 154, 100401, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 155, 100401, "かくれんぼする人\nこの指、と～まれ！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 4, 101, 0, 1, 156, 100401, "ミソギ以外の人に\nイタズラされちゃ\nダメだよ！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 157, 100401, "ミソギと遊ぶ？\nミソギに遊ばれる？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 158, 100401, "いいこと思いついた！\nい、イタズラとかじゃ\nないよ～", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 159, 100401, "うわっ！？\n　にいちゃんが、\nミソギにイタズラ？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 160, 100401, "にいちゃんの\n通りそうなところに\nバナナの皮を…", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 161, 100401, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 4, 101, 0, 1, 162, 100401, "ここ、ミソギ探検隊の\n基地にしようよ！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 163, 100401, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1, 0, 1, 164, 100401, "にいちゃん、おはよ！\n目覚まし時計を\n止めておいたのに、\nよく起きられたね～♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (2, 1, 2, 0, 1, 165, 100401, "にいちゃんが\n昼寝したら、\nイタズラし放題♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (3, 1, 3, 0, 1, 166, 100401, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 167, 100401, "イタズラには\nお色気も必要…\nうん、完璧！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 168, 100401, "座ったら、ブーッって\nいう音が出たら、\nおもしろいな～♪", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 169, 100401, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 170, 100401, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 171, 100401, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 6, 0, 1, 172, 100401, "探検家は\n体力勝負だよー！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 7, 0, 1, 173, 100401, "みんなに分けて\nあげようかな～\n激辛ソースも\n入れちゃうけどね！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 8, 0, 1, 174, 100401, "こういう所に\n宝の地図が\n隠れてるんだよね～", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 176, 100402, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 177, 100402, "うぅ、探検に出たい！\nじっとしてなんか\nいられないよ～！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 178, 100402, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 179, 100402, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 180, 100402, "かくれんぼする人\nこの指、と～まれ！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 101, 0, 1, 181, 100402, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 182, 100402, "にいちゃんが喜ぶ\nイタズラ、何かない\nかな～？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 183, 100402, "ミソギがしかけた\nイタズラ\n見つけられるかな？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 184, 100402, "探検する？\n冒険する？\nそれとも…\nイタズラされる？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 185, 100402, "将来の夢は探検家！\n探したい物はないけど", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 186, 100402, "にいちゃんに宿題！\n落とし穴を10個\n掘ること！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 101, 0, 1, 187, 100402, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 188, 100402, "ここ、ミソギ探検隊の\n基地にしようよ！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1, 0, 1, 189, 100402, "にいちゃん\n探検に行こうよ！\n今からなら遭難しても\n夜には帰れるよ！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (2, 1, 2, 0, 1, 190, 100402, "にいちゃんが昼寝したら\nイタズラし放題♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (3, 1, 3, 0, 1, 191, 100402, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 192, 100402, "イタズラには\nお色気も必要…\nうん、完璧！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 193, 100402, "ゆっくりまったりして\n次のイタズラを\n考えよ～！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 194, 100402, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 195, 100402, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 196, 100402, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 6, 0, 1, 197, 100402, "探検家は\n体力勝負だよー！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 7, 0, 1, 198, 100402, "探検のおやつに\nぴったりかも～！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 8, 0, 1, 199, 100402, "こういう所に\n宝の地図が\n隠れてるんだよね～", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 201, 100601, "音楽は全部好きだけど\n一番好きなのは\nやっぱりジャズかな", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 202, 100601, "はぁ、はぁ…\n一人でもいっぱい\nしないと…\n特訓…", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 203, 100601, "お姉ちゃんには\nもっと自信を\n持ってほしいなぁ", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 204, 100601, "お姉ちゃんのマネ\nしちゃおうかな？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 205, 100601, "双子って、\n好みのタイプも\n似ちゃうのかな？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 4, 101, 0, 1, 206, 100601, "お兄ちゃんと私は\n相性抜群です♪", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 207, 100601, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 208, 100601, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 209, 100601, "うふふ、お兄ちゃん！\n今日は何を\nするんですか？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 210, 100601, "そんなところ\n触っちゃダメ♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 211, 100601, "お兄ちゃん、イジワル\nしないでください…", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 4, 101, 0, 1, 212, 100601, "お兄ちゃん、もっと\n上級編のテクニック、\n教えてくださいね！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 213, 100601, "コーチ、\n今日も特訓\nお願いします！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1, 0, 1, 214, 100601, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (2, 1, 2, 0, 1, 215, 100601, "こんな明るい時間から\nす、するんですか…\n二人っきりの特訓…", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (3, 1, 3, 0, 1, 216, 100601, "夜はこれからですよ\nお兄ちゃん♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 217, 100601, "世界一、かわいく\nなるんだから♪", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 218, 100601, "気持ちいい♪\nもう立てないよ～", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 219, 100601, "かわいい♪\nアカリのライバル\nだね", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 220, 100601, "はぁ…気持ちいい、\n私、このままダメに\nなっちゃいそう…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 221, 100601, "はぁ…アカリ、\nとろけちゃう…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 6, 0, 1, 222, 100601, "はぁ…はぁはぁ、\n私…もう…ダメ\n限界です…！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 7, 0, 1, 223, 100601, "わぁ、おいしい～\nけど食べ過ぎは美容に\nよくないですよね", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 8, 0, 1, 224, 100601, "この楽譜、\nサックスで吹いて\nみたいなぁ♪", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 226, 100602, "このごろ一人でいると\nお兄ちゃんのこと\nばかり考えちゃう…", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 227, 100602, "はぁ、はぁ…もっと\nいっぱいしなくちゃ\n…練習", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 228, 100602, "このお部屋、なんだか\n落ち着きます…\nお兄ちゃんが\nいるから？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 229, 100602, "みんなかわいいな～\n世界一かわいくなる\nのは、難しいな…", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 230, 100602, "双子って、\n好みのタイプも\n似ちゃうのかな？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 101, 0, 1, 231, 100602, "いつか世界でいちばん\nかわいくなって\nお兄ちゃんと…", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 232, 100602, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 233, 100602, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 234, 100602, "二人きりの秘密の特訓\n今度はいつですか？\n待ち遠しいです…", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 235, 100602, "え？　ここでするの？\nみんなの前で恥ずか\nしいな…筋トレ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 236, 100602, "お兄ちゃん、イジワル\nしないでください…", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 101, 0, 1, 237, 100602, "お兄ちゃんといると\nわ、私…ドキドキ\nしてきちゃう…", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 238, 100602, "コーチ、\n今日も特訓\nお願いします！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1, 0, 1, 239, 100602, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (2, 1, 2, 0, 1, 240, 100602, "お兄ちゃんに\n食べてもらいたいなぁ\n手作りのお昼ごはん", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (3, 1, 3, 0, 1, 241, 100602, "お風呂入ってきますね\nそのあとは…\n二人でい～っぱい\n遊びましょうね", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 242, 100602, "お姉ちゃんのマネ\nしちゃおうかな？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 243, 100602, "気持ちいい♪\nもう立てないよ～", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 244, 100602, "かわいい♪\nアカリのライバル\nだね", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 245, 100602, "はぁ…気持ちいい、\n私、このままダメに\nなっちゃいそう…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 246, 100602, "あったか～い…\nまるでお兄ちゃんが\n隣にいる時みたい", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 6, 0, 1, 247, 100602, "汗をかくと\n気持ちいいですよね", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 7, 0, 1, 248, 100602, "お兄ちゃんの特訓に\n付き合うために\nたっぷり\n食べておかないと♪", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 8, 0, 1, 249, 100602, "この楽譜\nサックスで吹いて\nみたいなぁ♪", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 251, 100701, "プリン、プリン、\nプリン、プリン、\nプリン、プリン～♪", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 252, 100701, "う～ら～め～し～や～\nなの～♪", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 253, 100701, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 254, 100701, "幽霊の正体見たり、\nミヤコなの～", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 255, 100701, "ミヤコがいきなり\n消えたら、みんな\n驚くかもなの～♪", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 4, 101, 0, 1, 256, 100701, "アイツがいて、\nプリンもある。\nきっとここが天国なの", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 257, 100701, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 258, 100701, "姿を消して、オマエを\n見張ってやるの～\nむふふ～♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 259, 100701, "もし成仏した先に\nプリンがなかったら\n地獄なの～…", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 260, 100701, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 261, 100701, "死んだ人には\nお供え物をするの～\nミヤコにも、プリンを\n毎日お供えするの～", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 4, 101, 0, 1, 262, 100701, "たくさんオマエと\n遊べて、\n成仏しそうなくらい\nうれしいの～！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 263, 100701, "オマエのこと\n気に入ったからずっと\nとり憑いてやるの～", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1, 0, 1, 264, 100701, "う～　眠いの…\n二度目の二度寝を\nするの…", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (2, 1, 2, 0, 1, 265, 100701, "お昼ご飯はプリンなの\n３時のおやつも、\nプリンなの～", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (3, 1, 3, 0, 1, 266, 100701, "ディナーは豪華に、\nプリンのフルコース\nなの～♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 267, 100701, "体が透けちゃうから\nうまく使えないの～", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 268, 100701, "まったりなの～\nあとはプリンがあれば\n完璧なの～", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 269, 100701, "む～…かわいいけど\nプリンは分けて\nあげないの～", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 270, 100701, "は～　いい気分なの…\n成仏しそうなの…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 271, 100701, "も、もしかして\nミヤコ、ここで\n火葬されちゃうの？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 6, 0, 1, 272, 100701, "幽霊には、体力も\n筋力も関係ないの～！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 7, 0, 1, 273, 100701, "ぱくぱくモグモグ…\nミヤコ、幽霊だけど、\nお腹はすくの～", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 8, 0, 1, 274, 100701, "プリンの写真集が\nあればいいのに\nなの～", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 276, 100702, "うらめしや～…\n裏の飯より\nプリンがいいの～", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 277, 100702, "プリン♪　プリン♪\nプリン♪　なの～♪　", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 278, 100702, "世界中のケーキ屋が\nプリンのおいしさを\n競い合えばいいの～！\n審査員はミヤコなの～", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 279, 100702, "買ってきたプリンには\n全部ミヤコの名前を\n書いておくの～", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 280, 100702, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～ ", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 101, 0, 1, 281, 100702, "アイツがいて\nプリンもある\nきっとここが天国なの", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 282, 100702, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 283, 100702, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 284, 100702, "もし成仏した先に\nプリンがなかったら\n地獄なの～…", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 285, 100702, "今日はオマエと\nプリンを買いに行くの\n強制なの～　", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 286, 100702, "死んだ人には\nお供え物をするの～\nミヤコにも、プリンを\n毎日お供えするの～", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 101, 0, 1, 287, 100702, "たくさんオマエと\n遊べて\n成仏しそうなくらい\nうれしいの～！ ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 288, 100702, "ミヤコのプリン\nオマエにだったら\n分けてあげるの～ ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1, 0, 1, 289, 100702, "う～　眠いの…\n二度目の二度寝を\nするの… ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (2, 1, 2, 0, 1, 290, 100702, "う～ら～め～し～や～\n…明るい時間だと\n驚かせないの～ ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (3, 1, 3, 0, 1, 291, 100702, "ひゅ～どろどろ～ん☆\n真っ暗なうちに\nいたずらしまくるの～♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 292, 100702, "体が透けちゃうから\nうまく使えないの～ ", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 293, 100702, "落ち着くの～\nいまプリン食べたら\nきっといつもより\nおいしいの～♪ ", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 294, 100702, "む～…かわいいけど\nプリンは分けて\nあげないの～　", 3);
