INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 296, 100702, "も、もしかして\nミヤコ、ここで\n火葬されちゃうの？ ", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 6, 0, 1, 297, 100702, "幽霊には、体力も\n筋力も関係ないの～！　", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 7, 0, 1, 298, 100702, "おいしいの～\nプリン味だったら\nもっとおいしいの～ ", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 8, 0, 1, 299, 100702, "プリンの写真集が\nあればいいのに\nなの～ ", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 301, 100801, "もし太陽が滅んでも\nボクの美しさで大地を\n照らしてあげる！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 302, 100801, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 303, 100801, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 304, 100801, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 305, 100801, "うわっ昨日より可愛く\nなってる。明日は\nどうなっちゃう\nのかな？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 4, 101, 0, 1, 306, 100801, "このボクがいるだけで\nあの人への\nおもてなしだよね", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 307, 100801, "ボクの可愛さは、\nふふ…あの人の\nためのものさ", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 308, 100801, "ねぇ～、ボクって\n可愛い？\nふふ、知ってる☆", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 309, 100801, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 310, 100801, "ふふ、ボクに触ると\nキミも美しく\nなっちゃうかもよ？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 311, 100801, "どうかな、\n今日のボク。\n美しい？　可愛い？\nねえ、どっち？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 4, 101, 0, 1, 312, 100801, "あの人も今、\n可愛いボクのこと\n考えてるかな…", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 313, 100801, "美しい…\nキミの瞳に写るボクは\nより一層きれいだ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1, 0, 1, 314, 100801, "美しいボクの\n美しい一日が\n始まるよ！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (2, 1, 2, 0, 1, 315, 100801, "さーて、午後からも\n可愛く美しく\n華麗に優雅に♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (3, 1, 3, 0, 1, 316, 100801, "バラの花びら\nいっぱい浮かべた\nお風呂に入ってから\n寝ようっと♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 317, 100801, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 318, 100801, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 319, 100801, "わあ、可愛い！\n…まあ、ボクほど\nじゃないけどさ！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 320, 100801, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 321, 100801, "気安く触ったら\n火傷する…\n炎はボクに似ているね", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 6, 0, 1, 322, 100801, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 7, 0, 1, 323, 100801, "ボクが食べると、\n食べ物まで\n可愛いなあ…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 8, 0, 1, 324, 100801, "ボクが触った瞬間に、\nこの本は美を\n携えてしまうんだ", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 326, 100802, "ボクの美しさは\nみんなのために\nあるんだよ！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 327, 100802, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 328, 100802, "これ以上美しくなると\nボクをめぐって\n大陸中で戦争が\n始まっちゃう！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 329, 100802, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 330, 100802, "ボクの可愛さについて\nみんな話し合わない\nのかな…？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 101, 0, 1, 331, 100802, "このボクがいるだけで\nあの人への\nおもてなしだよね", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 332, 100802, "あの人も今\n可愛いボクのこと\n考えてるかな…", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 333, 100802, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 334, 100802, "ボクの美しさに嫉妬\nしたって意味ないよね\n次元が違うんだから", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 335, 100802, "ボクの化粧品使う？\n一緒に可愛くなろうよ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 336, 100802, "ボクがいるだけで\nこの部屋は美の神殿♪\nランドソルの新たな\n観光名所になるね！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 101, 0, 1, 337, 100802, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 338, 100802, "美しい…\nキミの瞳に写るボクは\nより一層綺麗だ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1, 0, 1, 339, 100802, "朝日と共に\nボクの美しさも\n周りを照らすよ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (2, 1, 2, 0, 1, 340, 100802, "ボクの美しさを\n維持するために\nお昼ご飯食べなくちゃ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (3, 1, 3, 0, 1, 341, 100802, "バラの花びらいっぱい\n浮かべたお風呂に\n入ってから\n寝ようっと♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 342, 100802, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 343, 100802, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 344, 100802, "可愛いものを\n可愛いって言える\nボクって可愛いよね！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 345, 100802, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 346, 100802, "冷えは美容の大敵\nなんだから！\nここ、ボクの場所ね！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 6, 0, 1, 347, 100802, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 7, 0, 1, 348, 100802, "ボクが食べると\n食べ物まで\n可愛いなあ…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 8, 0, 1, 349, 100802, "ボクが触った瞬間に\nこの本は美を\n携えてしまうんだ", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 351, 100901, "「第１０部２０章\n終焉戦争、開幕」\n…これでよし！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 352, 100901, "今度シグルドに\n私の魔法を一つ\n伝授してやるか", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 353, 100901, "ここでは風が\n吹いてこないな…\n換気したい！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 354, 100901, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 355, 100901, "まずは包帯をとって\n手から龍の形の\n炎を出して…", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 4, 101, 0, 1, 356, 100901, "シグルド…\n今頃何処で何を\nしているのだろう…", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 357, 100901, "最近、彼の姿が\nまぶしく見える…\nどうして…？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 358, 100901, "辞書を知らないか？\n…探している\n理由は聞くな", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 359, 100901, "そうだ！\n貴様の技にも\n名前をつけてやろう！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 360, 100901, "私に触れても力を\n奪われないのは\n貴様くらいだぞ？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 361, 100901, "何事だ？\nまさか我が瞳に\n魅入られたのか？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 4, 101, 0, 1, 362, 100901, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 363, 100901, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1, 0, 1, 364, 100901, "「覚醒」めたか…\n行くぞ、悪を討つ\n戦いに出発だ！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (2, 1, 2, 0, 1, 365, 100901, "昼間は「ヤツら」も\n襲ってはこない…\n束の間の休息だ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (3, 1, 3, 0, 1, 366, 100901, "どうした？　夜はまだ\nこれから…ふぁ…\nね、眠くなどないぞ！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 367, 100901, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 368, 100901, "くっ…！\n私を眠りの国へ\n誘うつもりか！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 369, 100901, "このようなものに\n懐柔されるものか！\n…されるものか！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 370, 100901, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 371, 100901, "これが我らの求めた\n安寧か…\n悪くない…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 6, 0, 1, 372, 100901, "この程度\n前世での激闘に\n比べれば…！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 7, 0, 1, 373, 100901, "最近金欠でな…\nいやぁありがたい…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 8, 0, 1, 374, 100901, "ここにも\n前世の記憶が\n眠っていたとは…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 376, 100902, "「第１３部１章\n閃光のシグルド」\n…ついに登場だな", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 377, 100902, "何故だ…何故\nこうもあっさり金欠に\n逆戻りするのだ！？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 378, 100902, "やはり転生者という\n設定はカッコイイ…\n天才だな、私！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 379, 100902, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 380, 100902, "そろそろ今日の分の\n魔力を溜めないと\nまた魔力切れが…", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 101, 0, 1, 381, 100902, "最近奴がそばに\nいるだけで、鼓動が\n早くなるのは何故だ？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 382, 100902, "シ、シグルド…！\nだめだ…妄想でも\n上手く言えない…", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 1, 0, 1, 383, 100902, "なんだ？　新たな\n賞金の情報を\n持ってきたのか？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 384, 100902, "名乗りやポーズは\nその強度で相手を\n圧倒する技術だ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 385, 100902, "いいか、魔法の\n詠唱中は絶対に\n声をかけるなよ？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 386, 100902, "おお、ちょうど今\n貴様専用の設定集を\n作成中だぞ！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 101, 0, 1, 387, 100902, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 388, 100902, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 8, 1, 0, 1, 389, 100902, "夜更かしのせいか\nまだ眠気が…いや！\nこれは組織の罠だ！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (2, 1, 2, 0, 1, 390, 100902, "平穏な時に\n出来るだけ魔力を\n溜めておかねば…", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (3, 1, 3, 0, 1, 391, 100902, "夜になると、力が\n増した気分になるな\n…なりませんか？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 392, 100902, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 393, 100902, "お、おのれ\nヒュプノス…\n私は負けないぞ…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 394, 100902, "くっくっくっ…\n貴様は特別団員に\nしてやろう", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 395, 100902, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 396, 100902, "これが我らの求めた\n安寧か…\n悪くない…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 6, 0, 1, 397, 100902, "新たな戦いに備えて\n魔力だけでなく\n体力も鍛えねば！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 7, 0, 1, 398, 100902, "食事は魔力に\n変換する体力の\n元になるのだ！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 8, 0, 1, 399, 100902, "フフフ…\nこれを覚えれば\nもっと強く…！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 401, 101001, "王子はんほんまに\nはんなりしてて、\n王子はんって感じやわ", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 402, 101001, "この絵本の王子はん、\n王子はんみたいで\nええなぁ♪", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 403, 101001, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 404, 101001, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 405, 101001, "王子はんのために\n糸を紡いで、\n編み物でもしまひょか", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 4, 101, 0, 1, 406, 101001, "マホ姫と王子はん、\n絵本の世界より\nベストカップルや", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 407, 101001, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 408, 101001, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 409, 101001, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 410, 101001, "王子はんは、\n絵本から出てきた人\nみたいやね～", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 411, 101001, "うちの話し方\nのんびりしてます\nやろか？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 4, 101, 0, 1, 412, 101001, "マホ姫の手は\nつべたいから、\n王子はんが温めて？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 413, 101001, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1, 0, 1, 414, 101001, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (2, 1, 2, 0, 1, 415, 101001, "お茶請けはお大福が\nよろしおすけど\nクッキーもええなあ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (3, 1, 3, 0, 1, 416, 101001, "うち、普段から\n夢見がちやけど…\nほんに眠い時間やわぁ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 417, 101001, "王子はんに\n会いに行くなら、\nおめかしせな\nあかんなぁ", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 418, 101001, "はんなりした\nええお椅子やわぁ\nほっこりしますなあ", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 419, 101001, "かいらしもんが\nいっぱいで、\nマホマホ王国みたい\nやわぁ", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 420, 101001, "心地ええどす。まるで\nお花畑にいるようやね", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 421, 101001, "ぽかぽかどす。\n心まであったかく\nなりますわぁ", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 6, 0, 1, 422, 101001, "はぁはぁ、お姫はんは\n激しい運動は\nしぃひんもんどす～", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 7, 0, 1, 423, 101001, "もぐもぐ…あんまり\n速う食べられへんけど\n美味しいわぁ", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 8, 0, 1, 424, 101001, "絵本、絵本～\n幸せやわぁ♪", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 426, 101002, "王子はんにとって\n自慢の姫になるえ～", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 427, 101002, "カボチャの馬車に\nガラスの靴…\n欲しいなぁ", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 428, 101002, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 429, 101002, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 430, 101002, "ほんまなぁ\nこの絵本が面白いんよ\n王子はんに\n読んであげよ", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 101, 0, 1, 431, 101002, "マホ姫と王子はん\n絵本の世界より\nベストカップルや", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 432, 101002, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 433, 101002, "たまには、王子はんの\n好きな絵本も\n知りたいな～", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 434, 101002, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 435, 101002, "そのうち、王子はんの\nお国にも連れてって\nもらえますやろか？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 436, 101002, "絵本読むんも\nええなぁ…", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 101, 0, 1, 437, 101002, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 438, 101002, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1, 0, 1, 439, 101002, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (2, 1, 2, 0, 1, 440, 101002, "マホマホ王国では\nこの時分は\nみんなお昼寝\nしてたんよ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (3, 1, 3, 0, 1, 441, 101002, "夜に飛んだり鳴いたり\nする鳥はんもおるんよ\n昼は寝てはるんかねぇ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 442, 101002, "王子はんに\n会いに行くなら\nおめかしせな\nあかんなぁ", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 443, 101002, "王子はんも一緒に\nゆっくりしていっても\nええんよ？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 444, 101002, "かいらしい子やねぇ\nうちとお友だちに\nなってくれるん？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 445, 101002, "癒しアイテムって\nほんま、好っきゃわぁ", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 446, 101002, "うちのお城にも\nほしいわぁ。帰ったら\nさっそく準備せな\nあかんなぁ", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 6, 0, 1, 447, 101002, "部屋の中で運動\nできるのも、なかなか\n面白いもんやわぁ", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 7, 0, 1, 448, 101002, "おいひいわ～\nマホマホ王国には\nない味やわぁ", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 8, 0, 1, 449, 101002, "しっかり知識を\n身につけんと\n魔法も使えへん\nからなぁ", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 451, 101101, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 452, 101101, "おやつのケーキは\n苺かチョコ…\nどっちも食べよう！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 453, 101101, "い、いけない！\n縫い針落としちゃった", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 454, 101101, "ケーキをクレープで\n巻いたら…\n大ヒット商品の予感！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 455, 101101, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 4, 101, 0, 1, 456, 101101, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 457, 101101, "ここがお兄ちゃんの\n部屋…\nドキドキしちゃう～", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 458, 101101, "ねぇ、おにいちゃん\n一緒に、スイーツでも\n食べにいかない？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 459, 101101, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 460, 101101, "勉強、見てくれるん\nですか？　やった～♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 461, 101101, "服がほつれてますよ？\n直してあげます！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 4, 101, 0, 1, 462, 101101, "私をお嫁さんに\nしたいなら、予約は\nお早目に、ですよ♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 463, 101101, "ねぇねぇお兄ちゃん！\n…えへへ、\n呼んでみただけ♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1, 0, 1, 464, 101101, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (2, 1, 2, 0, 1, 465, 101101, "お昼休憩に\n甘いものが\n食べたいです～", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (3, 1, 3, 0, 1, 466, 101101, "ねむいです～\nでももうちょっと\n勉強しないと～…", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 467, 101101, "この服、かわいい♪\n今度作って\nみよっと～！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 468, 101101, "勉強で疲れたから\n休憩しよ～っと", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 469, 101101, "かわいい♪\n私が服を作って\nあげよっか？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 470, 101101, "リラックスできます～\nお兄ちゃんが隣に\nいるみたい！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 471, 101101, "のんびりしたら、\nまた勉強しよっと！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 6, 0, 1, 472, 101101, "運動すると勉強も\nはかどるよね！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 7, 0, 1, 473, 101101, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 8, 0, 1, 474, 101101, "お兄ちゃんの記憶を\n戻す方法、どこかに\n書いてないかな？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 476, 101102, "ケーキの食べ過ぎで\nお腹が…うぅ、\n因果横暴だよー…", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 477, 101102, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 478, 101102, "い、いけない！\n縫い針落としちゃった", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 479, 101102, "笑う時には服着ます！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 480, 101102, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 101, 0, 1, 481, 101102, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 482, 101102, "お兄ちゃんが帰って\nきたら、何して遊ぼう\nかな～？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 483, 101102, "勉強、見てくれるん\nですか？　やった～♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 484, 101102, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 485, 101102, "おにいちゃん！\nそろそろ、おやつに\nしませんか？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 486, 101102, "勉強は…\n明日にしようっと", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 101, 0, 1, 487, 101102, "妹だけど、大人な\nレディですからね！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 488, 101102, "な、ナデナデとか\n恥ずかしいな～", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1, 0, 1, 489, 101102, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (2, 1, 2, 0, 1, 490, 101102, "お昼休憩に\n甘いものが\n食べたいです～", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (3, 1, 3, 0, 1, 491, 101102, "お兄ちゃんは\n夜強いんですね\n私は眠くて…", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 492, 101102, "服装や髪形が変わると\n気分も変わるよね", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 493, 101102, "ここで寝ちゃったら\n受験勉強が～…\nぐーー", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 494, 101102, "かわいい♪\n私が服を作って\nあげよっか？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 495, 101102, "いまならどんな試験も\n合格できそうです！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 496, 101102, "のんびりしたら\nまた勉強しよっと！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 6, 0, 1, 497, 101102, "お兄ちゃんを\n守るためには\n毎日、鍛えなくちゃ！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 7, 0, 1, 498, 101102, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 8, 0, 1, 499, 101102, "この本の服\nかわいいなぁ♪\n私に作れるかな？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 501, 101201, "う～ん、今なら\nどこでだって\n眠れちゃいそう…", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 502, 101201, "たくさん寝すぎて、\n夢の中か現実か\nわかんなく\nなっちゃうんだよね～", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 503, 101201, "…はっ！？\nね、寝てないよ？\nホントだよっ", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 504, 101201, "ぐっすり眠って\nパワーを\nフルチャージっ☆", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 505, 101201, "もし、超能力のことが\nバレたら…ブルブル", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 4, 101, 0, 1, 506, 101201, "テレパシーで\nキミの心を覗けたら\n…なんてね！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 507, 101201, "キミを抱き枕に…\nじょ、冗談冗談！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 508, 101201, "ん～、なになに？\n一緒にお昼寝する？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 509, 101201, "忘れ物しちゃった。\nま、いっか！\nえ、ダメ？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 510, 101201, "おおっ、相談ごと？\nなにかな\nなにかな～？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 511, 101201, "う～ん、もしかして\nキミにも仲良しになる\n超能力があるのかも！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 4, 101, 0, 1, 512, 101201, "むむ～…どう？\nテレパシーで、私の\n気持ちを送ったよ！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 513, 101201, "えっ？　私とキミとの\n相性？　むむ～…\nうん、バッチリだよ☆", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1, 0, 1, 514, 101201, "まだこんな時間？\nベッドに行って、\n二度寝しなくちゃ～", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (2, 1, 2, 0, 1, 515, 101201, "適度なお昼寝は\n健康にいいんだって！\n８時間くらいかな？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (3, 1, 3, 0, 1, 516, 101201, "どんなにお昼寝しても\n夜は眠いんだ～", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 517, 101201, "ん～、また寝癖…\nいっつも\n寝てるからかなぁ？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 518, 101201, "一休み、一休み…\nどうせなら百休み\nくらいしようかな…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 519, 101201, "これ…可愛い！\nシオリンに\n届けてあげたい！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 520, 101201, "ん～…気分爽快！\nそれじゃ、おやすみ～\n…すやぁ…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 521, 101201, "気持ちいい…\nはっ！　ダメダメ！\nまた寝るとこだった！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 6, 0, 1, 522, 101201, "眠くならないように、\n運動しよっと！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 7, 0, 1, 523, 101201, "お腹いっぱいになると\n眠くなるよね～", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 8, 0, 1, 524, 101201, "睡眠学習って\n本を読みながら\n寝るんだっけ？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 526, 101202, "う～ん、今なら\nどこでだって\n眠れちゃいそう…", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 527, 101202, "もっといっぱい\n超能力使えたら\nいいのにな～", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 528, 101202, "ねえねえ、\n今から人助け、\n付き合ってほしいなー", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 529, 101202, "ぐっすり眠って\nパワーを\nフルチャージっ☆", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 530, 101202, "人助け、頑張ったら\nなんだか、眠く…\n…はっ、ダメダメ！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 101, 0, 1, 531, 101202, "キミと一緒なら、\nなんだって出来る！\n間違いない！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 532, 101202, "キミを抱き枕に…\nじょ、冗談冗談！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 533, 101202, "忘れ物しちゃった\nま、いっか！\nえ、ダメ？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 534, 101202, "なに？　困ってる？\nちょうど良かった！\n今は体力満タンだよ☆", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 535, 101202, "一緒に\nお出かけしたいなー\nデートしよっ！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 536, 101202, "うんうん、そうだねっ\n困ってる人がいたら\n見過ごせないよねっ！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 101, 0, 1, 537, 101202, "むむ～…どう？\nテレパシーで、私の\n気持ちを送ったよ！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 538, 101202, "う～だんだん眠く…\nねぇ一緒に寝よ？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1, 0, 1, 539, 101202, "キミって早起きだね！\n私は寝ようかな～", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (2, 1, 2, 0, 1, 540, 101202, "適度なお昼寝は\n健康にいいんだって！\n８時間くらいかな？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (3, 1, 3, 0, 1, 541, 101202, "どんなにお昼寝しても\n夜は眠いんだ～", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 542, 101202, "ん～、また寝癖…\nいっつも\n寝てるからかなぁ？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 543, 101202, "一休み、一休み…\nどうせなら百休み\nくらいしようかな…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 544, 101202, "これ…可愛い！\nシオリンに\n届けてあげたい！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 545, 101202, "ん～…気分爽快！\nそれじゃ、おやすみ～\n…すやぁ…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 546, 101202, "うう～ん、なんだか\nいつもより眠ーく\nなってきたよ…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 6, 0, 1, 547, 101202, "よーし\n困ってる人のために\nパワーアップ☆", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 7, 0, 1, 548, 101202, "食べてすぐ寝ると\n牛になるんだっけ？\nまぁ、いいかぁ～", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 8, 0, 1, 549, 101202, "睡眠学習って\n本を読みながら\n寝るんだっけ？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 551, 101601, "モデルは楽しいよ！\nファンの子みんな\n応援してくれるしー！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 552, 101601, "今日のおやつは\n駄菓子に決定～！\nいただきまーす！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 553, 101601, "んー、今日のおやつは\n何にしよう？\nやっぱアメ玉かな！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 554, 101601, "ここで撮影会、\nしようかな～♪", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 555, 101601, "７かける８はぁ\n７＋７＋７＋７＋…\nん、何個数えたっけ？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 4, 101, 0, 1, 556, 101601, "やば…\nこのままじゃ、うち\nキュン死にかも…", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 557, 101601, "好きなことして\nヒデサイもいて\nうちって超～\nリア充だよね♪", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 558, 101601, "うちの写真撮るの？\nどんなポーズ？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 559, 101601, "ネイルの勉強中～♪\nうまくなったら、\nヒデサイにしてあげる", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 560, 101601, "宿題見てくんない？\nひとりじゃムリ～", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 561, 101601, "みてこのネイル♪\n超エモくな～い？\nヒデサイもする～？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 4, 101, 0, 1, 562, 101601, "新しい美容液で\n肌まじスベスベ～\nヒデサイも\n触ってみる？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 563, 101601, "ヒデサイと会ってると\n顔熱くなるんだよね\n熱なんかないのに…", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1, 0, 1, 564, 101601, "早起きとか\nつらたんだよねー\n昼すぎまで寝てたい", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (2, 1, 2, 0, 1, 565, 101601, "撮影にむけて\nダイエットちうだから\nお昼ぬいてるんだー", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (3, 1, 3, 0, 1, 566, 101601, "お腹すいたー\nでもこの時間に\n食べたら絶対に\n太るやつだよねー", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 567, 101601, "どんなシーンにも合う\nきれいめわがまま\nちゃんとコーデだよ♪", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 568, 101601, "ちょっち疲れたかも？\nガン寝しちゃおっかな", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 569, 101601, "きゃわたーん☆\n君もおしゃれしよっか", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 570, 101601, "いいにおいじゃん！\nこれと同じにおいの\n香水ないかな？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 571, 101601, "あったまるー\nこれ、サイツヨに\n癒しじゃない？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 6, 0, 1, 572, 101601, "あはは、なんか\nウォーキングの練習\nしてるみたーい！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 7, 0, 1, 573, 101601, "体形維持しないと\nいけないけど\nついつい食べちゃう！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 8, 0, 1, 574, 101601, "全然読めない～\nそだ、ヒデサイに\n読んでもらおっと！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 576, 101602, "そろそろ\n仕事の時間だ～\n出かける準備\nしよっと！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 577, 101602, "今日のおやつは\n駄菓子に決定～！\nいただきまーす！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 578, 101602, "ヒデサイを見習って\nうちも少し勉強して\nみよっかな…　", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 579, 101602, "体形維持とか\n気にしてるんだよ～", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 580, 101602, "７かける８はぁ\n７＋７＋７＋７＋…\nん、何個数えたっけ？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 101, 0, 1, 581, 101602, "やば…\nこのままじゃ、うち\nキュン死にかも…　", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 582, 101602, "ヒデサイと会ってると\n顔熱くなるんだよね\n熱なんかないのに…", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 583, 101602, "うちの写真撮るの？\nどんなポーズ？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 584, 101602, "ネイルの勉強中～♪\nうまくなったら、\nヒデサイに\nしてあげる♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 585, 101602, "この雑誌の服\nガチめにほしぃ～", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 586, 101602, "うちのコーデで\nヒデサイをオシャンに\nしてあげるね♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 101, 0, 1, 587, 101602, "新しい美容液で\n肌まじスベスベ～\nヒデサイも\n触ってみる？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 588, 101602, "好きなことして\nヒデサイもいて\nうちって超～\nリア充だよね♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1, 0, 1, 589, 101602, "今日は早起き\nできたんだー\nメイクもばっち\nきまってるっしょ？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (2, 1, 2, 0, 1, 590, 101602, "撮影にむけて\nダイエットちうだから\nお昼ぬいてるんだー", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (3, 1, 3, 0, 1, 591, 101602, "お腹すいたー\nでもこの時間に\n食べたら絶対に\n太るやつだよねー", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 592, 101602, "この服ね～\n今年のトレンドなんだ♪\nかわたんでしょ！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 593, 101602, "ちょっち疲れたかも？\nガン寝しちゃおっかな", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 594, 101602, "うわー\nこのぬいぐるみ\nもきゅすぎでしょ～！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 595, 101602, "いいにおーい♪\nファンのみんなにも\n教えてあげよー♪", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 596, 101602, "あったまるー\nこれ、サイツヨに\n癒しじゃない？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 6, 0, 1, 597, 101602, "食べた分は\n消費しないとね～", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 7, 0, 1, 598, 101602, "体形維持しないと\nいけないけど\nついつい食べちゃう！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 8, 0, 1, 599, 101602, "全然読めない～\nそだ、ヒデサイに\n読んでもらおっと！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 601, 101701, "やっさー\n踊ってると、どんどん\n楽しくなるさー！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 602, 101701, "このお菓子、でーじ\nおいしいねー！\nキミも食べる？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 603, 101701, "ハイターイ！　元気な\nあいさつが一番さ～！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 604, 101701, "あまった食材があれば\nチャンプルーにして\n食べたいさー！ ", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 605, 101701, "さぁ、みんなで歌って\n踊るさ～♪", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 4, 101, 0, 1, 606, 101701, "毎日、最高に幸せさ～\nキミのおかげだねー", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 607, 101701, "キミへの気持ちを\n踊りで表現\nしてみるさ～", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 608, 101701, "海でひと泳ぎ\nしたいさ～\nキミもどう？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 609, 101701, "あぎひゃっ！\n急に声かけたら、\nビックリするさ～", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 610, 101701, "踊りなら、\n私が手とり足とり\n教えてあげるさ～！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 611, 101701, "空手の型の\n練習するさー\nキミもどう？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 4, 101, 0, 1, 612, 101701, "よ～し、手をつないで\n踊るさ～！\n恥ずかしがること\nないさ～", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 613, 101701, "キミと一緒だから、\n毎日楽しいこと\nばっかりさー", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1, 0, 1, 614, 101701, "ハイターイ！\n今日も一日、\nがんばるさー！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (2, 1, 2, 0, 1, 615, 101701, "午後も頑張るために\nお昼ご飯は\nてびちにするさー！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (3, 1, 3, 0, 1, 616, 101701, "故郷では見えない星が\n見られるんだねえ。\nなんか不思議さぁ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 617, 101701, "夢中で踊ってたら、\n髪の毛ぼさぼさに\nなってるさ～！？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 618, 101701, "よく食べてよく踊って\nよく休む。\nそれが元気の秘訣さ～", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 619, 101701, "ちゅらかーぎーねー！\nキミはどこから\n来たのー？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 620, 101701, "いい香り～、って\n私もカオリだったさ！\nなんだか照れるね☆", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 621, 101701, "故郷はあんまり暖房を\n使わなかったから、\n新鮮さー！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 6, 0, 1, 622, 101701, "汗をかくのは\n気持ちいいさー！\nどんどん行くよー！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 7, 0, 1, 623, 101701, "うーん、おいしいさ～\nゴーヤっぽいものを\n混ぜたいさ～", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 8, 0, 1, 624, 101701, "おおー、故郷の民話！\nこんなとこで見るとは\n懐かしいさぁ～！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 626, 101702, "焦らなくても\nなんくるないさ～\nなるようになるさ～", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 627, 101702, "さぁ、みんなで歌って\n踊るさ～♪", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 628, 101702, "元気がないときは\n私のところに来るさー", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 629, 101702, "あまった食材があれば\nチャンプルーにして\n食べたいさー！ ", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 630, 101702, "ノリノリな音楽と\n踊りで、疲れを\n吹き飛ばすさ～！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 101, 0, 1, 631, 101702, "毎日、最高に幸せさ～\nキミのおかげだねー", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 632, 101702, "幸せすぎて踊らずには\nいられないさ～", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 633, 101702, "海でひと泳ぎ\nしたいさ～\nキミもどう？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 634, 101702, "ん？　どうしたの～？\nヒマなら一緒に\n踊ろうさ～", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 635, 101702, "キミと一緒だから\n毎日楽しいこと\nばっかりさー", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 636, 101702, "ハイターイ！　元気な\nあいさつが一番さ～！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 101, 0, 1, 637, 101702, "キミと踊ってる時が\n一番幸せさ～\nキミもそう思ってたら\nうれしいさ～", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 638, 101702, "海に沈む夕日は\nとっても綺麗さ～！\n今度一緒に見ようね～ ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1, 0, 1, 639, 101702, "気持ちのいい朝さー！\nひとっ走り\nしてくるさー！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (2, 1, 2, 0, 1, 640, 101702, "午後も頑張るために\nお昼ご飯は\nてびちにするさー！ ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (3, 1, 3, 0, 1, 641, 101702, "故郷では見えない星が\n見られるんだねえ\nなんか不思議さぁ ", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 642, 101702, "夢中で踊ってたら\n髪の毛ぼさぼさに\nなってるさ～！？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 643, 101702, "よく食べてよく踊って\nよく休む\nそれが元気の秘訣さ～", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 644, 101702, "ちゅらかーぎーねー！\nキミはどこから\n来たのー？ ", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 645, 101702, "いい香り～、って\n私もカオリだったさ！\nなんだか照れるね☆ ", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 646, 101702, "故郷はあんまり暖房を\n使わなかったから\n新鮮さー！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 6, 0, 1, 647, 101702, "徹夜で踊り続ける\nためにも、まずは\n体を鍛えるさ～", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 7, 0, 1, 648, 101702, "もぐもぐ…もっと\nおかわりがほしいさー", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 8, 0, 1, 649, 101702, "おおー、故郷の民話！\nこんなとこで見るとは\n懐かしいさぁ～！ ", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 651, 101801, "はあ…\nすてきな小説だった～\nキュンキュンしちゃう", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 652, 101801, "このお部屋\n女の子ばかりね。\n魔族の女子校を\n思い出しちゃう♪", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 653, 101801, "次のテスト\n恋愛小説から\n出題したら\nダメかしら…？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 654, 101801, "キンコンカンコ～ン♪\n授業をはじめま～す", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 655, 101801, "はー…\nちょっと肩が\nこっちゃった…", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 4, 101, 0, 1, 656, 101801, "私、あの子のこと\n無意識に特別扱い\nしちゃってるかな…", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 657, 101801, "物語だと生徒が先生を\n好きになるのにな…\n逆の場合は…", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 658, 101801, "あん！　見ちゃダメよ\n明日のテストの問題を\n作ってるとこなの！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 659, 101801, "勉強勉強で\n疲れたよね？\n膝枕してあげる！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 660, 101801, "テストがんばったね～\nご褒美に先生が\n特別な授業\nしてあげるね？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 661, 101801, "学院の外でも\n私は君の先生よ\nなんでも聞いてね♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 4, 101, 0, 1, 662, 101801, "今読んでるのは\nヒロインが年下の子と\n恋に落ちる話…あ、\n深い意味はないのよ～", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 663, 101801, "生徒は全員可愛いけど\n君は少し特別…\nみんなには内緒よ？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1, 0, 1, 664, 101801, "学校に\n遅刻しないように\n早く準備しないとね", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (2, 1, 2, 0, 1, 665, 101801, "男の子といっしょに\nお弁当を食べるの、\n夢なんだ～", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (3, 1, 3, 0, 1, 666, 101801, "明日の予習はやった？\n先生も手伝うから\n寝る前にやりましょ♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 667, 101801, "もしかして…\n少し露出多いかしら？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 668, 101801, "リラックスすると\nα波が出て、勉強が\nはかどるんですって", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 669, 101801, "かわいい♪　あなたも\n私の生徒になる？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 670, 101801, "学院にもこういう\n安らぐ設備が\n必要かしらね？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 671, 101801, "まだ寒いわぁ\n誰か隣に\n来てくれたら…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 6, 0, 1, 672, 101801, "運動部の顧問も\nできるかしら？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 7, 0, 1, 673, 101801, "学校の給食でも\n食べたいわぁ～", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 8, 0, 1, 674, 101801, "いい参考書を\n見つけちゃった♪", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 676, 101802, "私の生徒に\n不良なんていないわ\nみーんないい子♪", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 677, 101802, "このお部屋\n女の子ばかりね\n魔族の女子校を\n思い出しちゃう♪", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 678, 101802, "あの子、まだかな…\n宿題も見てあげたいし\n早く来ないかしら？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 679, 101802, "はあ…\nすてきな小説だった～\nキュンキュンしちゃう", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 680, 101802, "学校の図書室に\n恋愛小説を\n置いて欲しいなあ", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 101, 0, 1, 681, 101802, "私、あの子のこと\n無意識に特別扱い\nしちゃってるかな…", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 682, 101802, "物語だと生徒が先生を\n好きになるのにな…\n逆の場合は…　", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 683, 101802, "あん！　見ちゃダメよ\n明日のテストの問題を\n作ってるとこなの！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 684, 101802, "勉強勉強で\n疲れたよね？\n膝枕してあげる！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 685, 101802, "宿題はもうやった？\n苦手なところは\n先生が見てあげる♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 686, 101802, "んん？　なんだか\n元気がないみたい\n先生がぎゅぅ～って\nしてあげる…　", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 101, 0, 1, 687, 101802, "君のお勉強も\nだいぶ進んできたね～\n補習卒業も近いかな？\n少し寂しいけど…", 2);
