INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (2, 1, 2, 0, 1, 690, 101802, "午後の授業って\n眠いでしょう？\n気持ちは分かるけど\nがんばってね♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (3, 1, 3, 0, 1, 691, 101802, "明日の予習はやった？\n先生も手伝うから\n寝る前にやりましょ♪", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 692, 101802, "もしかして…\n少し露出多いかしら？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 693, 101802, "私の膝枕と、どっちが\nフカフカかしら？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 694, 101802, "可愛いわね～\n教室に置いたら\n生徒が喜ぶかしら？　", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 695, 101802, "は～落ち着くなぁ～\n教師には\nこういうゆとりも\n必要よね♪　", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 696, 101802, "まだ寒いわぁ\n誰か隣に\n来てくれたら…　", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 6, 0, 1, 697, 101802, "運動部の顧問も\nできるかしら？　", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 7, 0, 1, 698, 101802, "あん！\nお胸にこぼしちゃった", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 8, 0, 1, 699, 101802, "あぁーん♪\nこういう物語みたいな\n恋愛あこがれる～　", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 701, 102001, "おにいちゃんは、\nにんじん食べられる？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 702, 102001, "えへへ、\nお絵かきしよ～♪", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 703, 102001, "あのね、ミミと\nあそんでほしいな～", 1);
