INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 7, 0, 1, 1098, 102902, "太っちゃったら\nどうしよう…？\nでも手が止まらない～", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 8, 0, 1, 1099, 102902, "この恋愛小説ステキ！\n私もアイドルじゃ\nなかったら…", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 1101, 103001, "手裏剣の数が\n合わないデス！\nどこかに落とした\nデスか…？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 1102, 103001, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 1103, 103001, "金は天下の\nマワリモノ！\nところで、\nどういう意味デス？", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 1104, 103001, "むむ、アクダイカンの\n気配がするデース", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 1105, 103001, "クニトリ合戦、\n楽しみデース！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 4, 101, 0, 1, 1106, 103001, "ショーグンと\n出会えて、\nワタシは果報者デス！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 1107, 103001, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 1108, 103001, "なんでも\n命令するデス！\nショーグンの命は\n絶対デス！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 1109, 103001, "あっ、ショーグン、\n出陣はいつで\nゴザルデス！？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 1110, 103001, "さ、さすが\nショーグンデス…\nいつの間に背後を！？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 1111, 103001, "出陣の合図に\n備えるデス！\nほら貝は\nどこデスか！？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 4, 101, 0, 1, 1112, 103001, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 1113, 103001, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (1, 1, 1, 0, 1, 1114, 103001, "おはようデス！\n今日も一日\n忍法日和デス！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (2, 1, 2, 0, 1, 1115, 103001, "そろそろお腹\n空いてきたデス。\n昼ごはんにするデス！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (3, 1, 3, 0, 1, 1116, 103001, "明日もきっと\nいい日デスよ、\nショーグン！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 1117, 103001, "ちょんまげに\nしてみるのも\n面白いかもデス！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 1118, 103001, "心地よくて\n眠くなってきマス…\nもしやテキコクの\n罠デスか！？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 1119, 103001, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 1120, 103001, "ワビサビというもの\nデスね！\n素晴らしいデス！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 5, 0, 1, 1121, 103001, "身体を暖めて\nイクサに向かうデス！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 6, 0, 1, 1122, 103001, "アクロバットな忍法を\n使うには、体を\n鍛えないと危険デス！", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 7, 0, 1, 1123, 103001, "うーん、\n美味しいデス…\n幸せデス～", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 8, 0, 1, 1124, 103001, "これは…\nＢＵＳＨＩＤＯ…\nデスか？", 3);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 1126, 103002, "ふんふんふ～ん\n武器の手入れは\n念入りにしマスよ～", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 1127, 103002, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 1);
