INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 101, 0, 1, 1131, 103002, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 1132, 103002, "ショーグンの身は\n何があってもワタシが\n守るデス！", 1);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 1, 0, 1, 1133, 103002, "あっ、ショーグン\n出陣はいつで\nゴザルデス！？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 2, 0, 1, 1134, 103002, "イクサの前に\nホネ休めも大事デスよ\nゆっくりするデース！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 3, 0, 1, 1135, 103002, "さ、さすがショーグン\nデス…いつの間に\n背後を！？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 1, 4, 0, 1, 1136, 103002, "一緒に忍法考える\nデス！\nショーグン用の\n忍法も必要デスね！", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 101, 0, 1, 1137, 103002, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 2);
INSERT INTO `v1_287cc17c54fbb205815d9411b2a083aadfcee607ab856147be76c9c9d5e42344` VALUES (0, 8, 102, 0, 1, 1138, 103002, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 2);
