INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 10.85, "2024/03/31 12:00:00", 100, 0.0, "2030/12/17 14:59:59", 100, 10142, 1, 129901, "エリコ", 0.0, 24, 7);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 11.15, "2024/03/31 12:00:00", 101, 0.0, "2030/12/17 14:59:59", 101, 10142, 1, 129901, "エリコ", 0.0, 24, 2);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 6.4, "2024/03/31 12:00:00", 102, 0.0, "2030/12/17 14:59:59", 102, 10142, 7, 129901, "エリコ", 0.0, 24, 5);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 0.0, "2024/03/31 12:00:00", 103, 0.0, "2030/12/17 14:59:59", 103, 10142, 3, 129901, "エリコ", 0.0, 24, 3);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 2.43, "2024/03/31 12:00:00", 104, 0.0, "2030/12/17 14:59:59", 104, 10142, 2, 129901, "エリコ", 0.0, 24, 8);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 9.08, "2024/03/31 12:00:00", 105, 0.0, "2030/12/17 14:59:59", 105, 10142, 4, 129901, "エリコ", 0.0, 24, 3);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 0.0, "2024/03/31 12:00:00", 106, 0.0, "2030/12/17 14:59:59", 106, 10142, 6, 129901, "エリコ", 0.0, 24, 6);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 10.6, "2024/03/31 12:00:00", 107, 0.0, "2030/12/17 14:59:59", 107, 10142, 3, 129901, "エリコ", 0.0, 24, 8);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 7.54, "2024/03/31 12:00:00", 108, 0.0, "2030/12/17 14:59:59", 108, 10142, 6, 129901, "エリコ", 0.0, 24, 4);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 11.5, "2024/03/31 12:00:00", 109, 0.0, "2030/12/17 14:59:59", 109, 10142, 2, 129901, "エリコ", 0.0, 24, 7);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 8.83, "2024/03/31 12:00:00", 110, 0.0, "2030/12/17 14:59:59", 110, 10142, 2, 129901, "エリコ", 0.0, 24, 1);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 6.68, "2024/03/31 12:00:00", 111, 0.0, "2030/12/17 14:59:59", 111, 10142, 1, 129901, "エリコ", 0.0, 24, 2);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 8.8, "2024/03/31 12:00:00", 112, 0.0, "2030/12/17 14:59:59", 112, 10142, 6, 129901, "エリコ", 0.0, 24, 2);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 8.9, "2024/03/31 12:00:00", 113, 0.0, "2030/12/17 14:59:59", 113, 10142, 2, 129901, "エリコ", 0.0, 24, 1);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 3.7, "2024/03/31 12:00:00", 114, 0.0, "2030/12/17 14:59:59", 114, 10142, 2, 129901, "エリコ", 0.0, 24, 5);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 7.6, "2024/03/31 12:00:00", 200, 0.0, "2030/12/17 14:59:59", 200, 10142, 1, 129801, "マコト", 0.0, 24, 2);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 2.25, "2024/03/31 12:00:00", 201, 0.0, "2030/12/17 14:59:59", 201, 10142, 2, 129801, "マコト", 0.0, 24, 1);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 3.28, "2024/03/31 12:00:00", 202, 0.0, "2030/12/17 14:59:59", 202, 10142, 2, 129801, "マコト", 0.0, 24, 1);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 2.98, "2024/03/31 12:00:00", 203, 0.0, "2030/12/17 14:59:59", 203, 10142, 4, 129801, "マコト", 0.0, 24, 1);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 3.0, "2024/03/31 12:00:00", 204, 0.0, "2030/12/17 14:59:59", 204, 10142, 4, 129801, "マコト", 0.0, 24, 1);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 10.3, "2024/03/31 12:00:00", 205, 0.0, "2030/12/17 14:59:59", 205, 10142, 1, 129801, "マコト", 0.0, 24, 2);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 2.93, "2024/03/31 12:00:00", 206, 0.0, "2030/12/17 14:59:59", 206, 10142, 6, 129801, "マコト", 0.0, 24, 1);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 9.3, "2024/03/31 12:00:00", 207, 0.0, "2030/12/17 14:59:59", 207, 10142, 3, 129801, "マコト", 0.0, 24, 1);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 6.9, "2024/03/31 12:00:00", 208, 0.0, "2030/12/17 14:59:59", 208, 10142, 4, 129801, "マコト", 0.0, 24, 1);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 0.0, "2024/03/31 12:00:00", 209, 0.0, "2030/12/17 14:59:59", 209, 10142, 2, 129801, "マコト", 0.0, 24, 2);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 5.95, "2024/03/31 12:00:00", 210, 0.0, "2030/12/17 14:59:59", 210, 10142, 1, 129801, "マコト", 0.0, 24, 2);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 6.73, "2024/03/31 12:00:00", 211, 0.0, "2030/12/17 14:59:59", 211, 10142, 2, 129801, "マコト", 0.0, 24, 1);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 0.0, "2024/03/31 12:00:00", 212, 0.0, "2030/12/17 14:59:59", 212, 10142, 2, 129801, "マコト", 0.0, 24, 2);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 6.65, "2024/03/31 12:00:00", 213, 0.0, "2030/12/17 14:59:59", 213, 10142, 1, 129801, "マコト", 0.0, 24, 2);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 3.05, "2024/03/31 12:00:00", 214, 0.0, "2030/12/17 14:59:59", 214, 10142, 6, 129801, "マコト", 0.0, 24, 1);
