INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, 100, 100, 24, 10.85, 129901, 1, "2024/03/31 12:00:00", 7, 10142, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 0.0, "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, 101, 101, 24, 11.15, 129901, 1, "2024/03/31 12:00:00", 2, 10142, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 0.0, "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, 102, 102, 24, 6.4, 129901, 7, "2024/03/31 12:00:00", 5, 10142, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 0.0, "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, 103, 103, 24, 0.0, 129901, 3, "2024/03/31 12:00:00", 3, 10142, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 0.0, "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, 104, 104, 24, 2.43, 129901, 2, "2024/03/31 12:00:00", 8, 10142, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 0.0, "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, 105, 105, 24, 9.08, 129901, 4, "2024/03/31 12:00:00", 3, 10142, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 0.0, "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, 106, 106, 24, 0.0, 129901, 6, "2024/03/31 12:00:00", 6, 10142, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 0.0, "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, 107, 107, 24, 10.6, 129901, 3, "2024/03/31 12:00:00", 8, 10142, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 0.0, "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, 108, 108, 24, 7.54, 129901, 6, "2024/03/31 12:00:00", 4, 10142, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 0.0, "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, 109, 109, 24, 11.5, 129901, 2, "2024/03/31 12:00:00", 7, 10142, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 0.0, "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, 110, 110, 24, 8.83, 129901, 2, "2024/03/31 12:00:00", 1, 10142, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 0.0, "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, 111, 111, 24, 6.68, 129901, 1, "2024/03/31 12:00:00", 2, 10142, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 0.0, "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, 112, 112, 24, 8.8, 129901, 6, "2024/03/31 12:00:00", 2, 10142, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 0.0, "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, 113, 113, 24, 8.9, 129901, 2, "2024/03/31 12:00:00", 1, 10142, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 0.0, "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, 114, 114, 24, 3.7, 129901, 2, "2024/03/31 12:00:00", 5, 10142, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 0.0, "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, 200, 200, 24, 7.6, 129801, 1, "2024/03/31 12:00:00", 2, 10142, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 0.0, "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, 201, 201, 24, 2.25, 129801, 2, "2024/03/31 12:00:00", 1, 10142, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 0.0, "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, 202, 202, 24, 3.28, 129801, 2, "2024/03/31 12:00:00", 1, 10142, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 0.0, "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, 203, 203, 24, 2.98, 129801, 4, "2024/03/31 12:00:00", 1, 10142, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 0.0, "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, 204, 204, 24, 3.0, 129801, 4, "2024/03/31 12:00:00", 1, 10142, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 0.0, "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, 205, 205, 24, 10.3, 129801, 1, "2024/03/31 12:00:00", 2, 10142, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 0.0, "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, 206, 206, 24, 2.93, 129801, 6, "2024/03/31 12:00:00", 1, 10142, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 0.0, "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, 207, 207, 24, 9.3, 129801, 3, "2024/03/31 12:00:00", 1, 10142, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 0.0, "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, 208, 208, 24, 6.9, 129801, 4, "2024/03/31 12:00:00", 1, 10142, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 0.0, "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, 209, 209, 24, 0.0, 129801, 2, "2024/03/31 12:00:00", 2, 10142, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 0.0, "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, 210, 210, 24, 5.95, 129801, 1, "2024/03/31 12:00:00", 2, 10142, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 0.0, "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, 211, 211, 24, 6.73, 129801, 2, "2024/03/31 12:00:00", 1, 10142, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 0.0, "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, 212, 212, 24, 0.0, 129801, 2, "2024/03/31 12:00:00", 2, 10142, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 0.0, "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, 213, 213, 24, 6.65, 129801, 1, "2024/03/31 12:00:00", 2, 10142, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 0.0, "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, 214, 214, 24, 3.05, 129801, 6, "2024/03/31 12:00:00", 1, 10142, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 0.0, "マコト");
