INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (24, 10.85, 1, 100, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 10142, 7, "エリコ", 0.0, 129901, "2030/12/17 14:59:59", 0.0, "2024/03/31 12:00:00", 100);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (24, 11.15, 1, 101, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 10142, 2, "エリコ", 0.0, 129901, "2030/12/17 14:59:59", 0.0, "2024/03/31 12:00:00", 101);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (24, 6.4, 7, 102, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 10142, 5, "エリコ", 0.0, 129901, "2030/12/17 14:59:59", 0.0, "2024/03/31 12:00:00", 102);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (24, 0.0, 3, 103, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 10142, 3, "エリコ", 0.0, 129901, "2030/12/17 14:59:59", 0.0, "2024/03/31 12:00:00", 103);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (24, 2.43, 2, 104, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 10142, 8, "エリコ", 0.0, 129901, "2030/12/17 14:59:59", 0.0, "2024/03/31 12:00:00", 104);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (24, 9.08, 4, 105, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 10142, 3, "エリコ", 0.0, 129901, "2030/12/17 14:59:59", 0.0, "2024/03/31 12:00:00", 105);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (24, 0.0, 6, 106, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 10142, 6, "エリコ", 0.0, 129901, "2030/12/17 14:59:59", 0.0, "2024/03/31 12:00:00", 106);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (24, 10.6, 3, 107, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 10142, 8, "エリコ", 0.0, 129901, "2030/12/17 14:59:59", 0.0, "2024/03/31 12:00:00", 107);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (24, 7.54, 6, 108, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 10142, 4, "エリコ", 0.0, 129901, "2030/12/17 14:59:59", 0.0, "2024/03/31 12:00:00", 108);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (24, 11.5, 2, 109, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 10142, 7, "エリコ", 0.0, 129901, "2030/12/17 14:59:59", 0.0, "2024/03/31 12:00:00", 109);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (24, 8.83, 2, 110, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 10142, 1, "エリコ", 0.0, 129901, "2030/12/17 14:59:59", 0.0, "2024/03/31 12:00:00", 110);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (24, 6.68, 1, 111, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 10142, 2, "エリコ", 0.0, 129901, "2030/12/17 14:59:59", 0.0, "2024/03/31 12:00:00", 111);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (24, 8.8, 6, 112, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 10142, 2, "エリコ", 0.0, 129901, "2030/12/17 14:59:59", 0.0, "2024/03/31 12:00:00", 112);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (24, 8.9, 2, 113, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 10142, 1, "エリコ", 0.0, 129901, "2030/12/17 14:59:59", 0.0, "2024/03/31 12:00:00", 113);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (24, 3.7, 2, 114, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 10142, 5, "エリコ", 0.0, 129901, "2030/12/17 14:59:59", 0.0, "2024/03/31 12:00:00", 114);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (24, 7.6, 1, 200, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 10142, 2, "マコト", 0.0, 129801, "2030/12/17 14:59:59", 0.0, "2024/03/31 12:00:00", 200);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (24, 2.25, 2, 201, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 10142, 1, "マコト", 0.0, 129801, "2030/12/17 14:59:59", 0.0, "2024/03/31 12:00:00", 201);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (24, 3.28, 2, 202, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 10142, 1, "マコト", 0.0, 129801, "2030/12/17 14:59:59", 0.0, "2024/03/31 12:00:00", 202);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (24, 2.98, 4, 203, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 10142, 1, "マコト", 0.0, 129801, "2030/12/17 14:59:59", 0.0, "2024/03/31 12:00:00", 203);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (24, 3.0, 4, 204, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 10142, 1, "マコト", 0.0, 129801, "2030/12/17 14:59:59", 0.0, "2024/03/31 12:00:00", 204);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (24, 10.3, 1, 205, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 10142, 2, "マコト", 0.0, 129801, "2030/12/17 14:59:59", 0.0, "2024/03/31 12:00:00", 205);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (24, 2.93, 6, 206, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 10142, 1, "マコト", 0.0, 129801, "2030/12/17 14:59:59", 0.0, "2024/03/31 12:00:00", 206);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (24, 9.3, 3, 207, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 10142, 1, "マコト", 0.0, 129801, "2030/12/17 14:59:59", 0.0, "2024/03/31 12:00:00", 207);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (24, 6.9, 4, 208, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 10142, 1, "マコト", 0.0, 129801, "2030/12/17 14:59:59", 0.0, "2024/03/31 12:00:00", 208);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (24, 0.0, 2, 209, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 10142, 2, "マコト", 0.0, 129801, "2030/12/17 14:59:59", 0.0, "2024/03/31 12:00:00", 209);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (24, 5.95, 1, 210, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 10142, 2, "マコト", 0.0, 129801, "2030/12/17 14:59:59", 0.0, "2024/03/31 12:00:00", 210);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (24, 6.73, 2, 211, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 10142, 1, "マコト", 0.0, 129801, "2030/12/17 14:59:59", 0.0, "2024/03/31 12:00:00", 211);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (24, 0.0, 2, 212, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 10142, 2, "マコト", 0.0, 129801, "2030/12/17 14:59:59", 0.0, "2024/03/31 12:00:00", 212);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (24, 6.65, 1, 213, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 10142, 2, "マコト", 0.0, 129801, "2030/12/17 14:59:59", 0.0, "2024/03/31 12:00:00", 213);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (24, 3.05, 6, 214, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 10142, 1, "マコト", 0.0, 129801, "2030/12/17 14:59:59", 0.0, "2024/03/31 12:00:00", 214);
