INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129901, 10142, "エリコ", "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 10.85, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 100, 7, 1, 24, 100, 0.0);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129901, 10142, "エリコ", "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 11.15, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 101, 2, 1, 24, 101, 0.0);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129901, 10142, "エリコ", "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 6.4, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 102, 5, 7, 24, 102, 0.0);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129901, 10142, "エリコ", "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 0.0, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 103, 3, 3, 24, 103, 0.0);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129901, 10142, "エリコ", "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 2.43, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 104, 8, 2, 24, 104, 0.0);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129901, 10142, "エリコ", "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 9.08, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 105, 3, 4, 24, 105, 0.0);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129901, 10142, "エリコ", "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 0.0, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 106, 6, 6, 24, 106, 0.0);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129901, 10142, "エリコ", "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 10.6, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 107, 8, 3, 24, 107, 0.0);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129901, 10142, "エリコ", "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 7.54, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 108, 4, 6, 24, 108, 0.0);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129901, 10142, "エリコ", "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 11.5, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 109, 7, 2, 24, 109, 0.0);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129901, 10142, "エリコ", "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 8.83, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 110, 1, 2, 24, 110, 0.0);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129901, 10142, "エリコ", "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 6.68, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 111, 2, 1, 24, 111, 0.0);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129901, 10142, "エリコ", "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 8.8, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 112, 2, 6, 24, 112, 0.0);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129901, 10142, "エリコ", "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 8.9, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 113, 1, 2, 24, 113, 0.0);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129901, 10142, "エリコ", "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 3.7, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 114, 5, 2, 24, 114, 0.0);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129801, 10142, "マコト", "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 7.6, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 200, 2, 1, 24, 200, 0.0);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129801, 10142, "マコト", "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 2.25, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 201, 1, 2, 24, 201, 0.0);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129801, 10142, "マコト", "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 3.28, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 202, 1, 2, 24, 202, 0.0);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129801, 10142, "マコト", "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 2.98, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 203, 1, 4, 24, 203, 0.0);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129801, 10142, "マコト", "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 3.0, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 204, 1, 4, 24, 204, 0.0);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129801, 10142, "マコト", "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 10.3, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 205, 2, 1, 24, 205, 0.0);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129801, 10142, "マコト", "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 2.93, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 206, 1, 6, 24, 206, 0.0);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129801, 10142, "マコト", "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 9.3, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 207, 1, 3, 24, 207, 0.0);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129801, 10142, "マコト", "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 6.9, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 208, 1, 4, 24, 208, 0.0);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129801, 10142, "マコト", "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 0.0, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 209, 2, 2, 24, 209, 0.0);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129801, 10142, "マコト", "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 5.95, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 210, 2, 1, 24, 210, 0.0);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129801, 10142, "マコト", "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 6.73, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 211, 1, 2, 24, 211, 0.0);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129801, 10142, "マコト", "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 0.0, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 212, 2, 2, 24, 212, 0.0);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129801, 10142, "マコト", "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 6.65, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 213, 2, 1, 24, 213, 0.0);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129801, 10142, "マコト", "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 3.05, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 214, 1, 6, 24, 214, 0.0);
