INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (7, 1, 100, 0.0, 129901, 100, 0.0, "2024/03/31 12:00:00", 24, "2030/12/17 14:59:59", 10.85, 10142, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (2, 1, 101, 0.0, 129901, 101, 0.0, "2024/03/31 12:00:00", 24, "2030/12/17 14:59:59", 11.15, 10142, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (5, 7, 102, 0.0, 129901, 102, 0.0, "2024/03/31 12:00:00", 24, "2030/12/17 14:59:59", 6.4, 10142, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (3, 3, 103, 0.0, 129901, 103, 0.0, "2024/03/31 12:00:00", 24, "2030/12/17 14:59:59", 0.0, 10142, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (8, 2, 104, 0.0, 129901, 104, 0.0, "2024/03/31 12:00:00", 24, "2030/12/17 14:59:59", 2.43, 10142, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (3, 4, 105, 0.0, 129901, 105, 0.0, "2024/03/31 12:00:00", 24, "2030/12/17 14:59:59", 9.08, 10142, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (6, 6, 106, 0.0, 129901, 106, 0.0, "2024/03/31 12:00:00", 24, "2030/12/17 14:59:59", 0.0, 10142, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (8, 3, 107, 0.0, 129901, 107, 0.0, "2024/03/31 12:00:00", 24, "2030/12/17 14:59:59", 10.6, 10142, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (4, 6, 108, 0.0, 129901, 108, 0.0, "2024/03/31 12:00:00", 24, "2030/12/17 14:59:59", 7.54, 10142, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (7, 2, 109, 0.0, 129901, 109, 0.0, "2024/03/31 12:00:00", 24, "2030/12/17 14:59:59", 11.5, 10142, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (1, 2, 110, 0.0, 129901, 110, 0.0, "2024/03/31 12:00:00", 24, "2030/12/17 14:59:59", 8.83, 10142, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (2, 1, 111, 0.0, 129901, 111, 0.0, "2024/03/31 12:00:00", 24, "2030/12/17 14:59:59", 6.68, 10142, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (2, 6, 112, 0.0, 129901, 112, 0.0, "2024/03/31 12:00:00", 24, "2030/12/17 14:59:59", 8.8, 10142, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (1, 2, 113, 0.0, 129901, 113, 0.0, "2024/03/31 12:00:00", 24, "2030/12/17 14:59:59", 8.9, 10142, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (5, 2, 114, 0.0, 129901, 114, 0.0, "2024/03/31 12:00:00", 24, "2030/12/17 14:59:59", 3.7, 10142, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (2, 1, 200, 0.0, 129801, 200, 0.0, "2024/03/31 12:00:00", 24, "2030/12/17 14:59:59", 7.6, 10142, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (1, 2, 201, 0.0, 129801, 201, 0.0, "2024/03/31 12:00:00", 24, "2030/12/17 14:59:59", 2.25, 10142, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (1, 2, 202, 0.0, 129801, 202, 0.0, "2024/03/31 12:00:00", 24, "2030/12/17 14:59:59", 3.28, 10142, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (1, 4, 203, 0.0, 129801, 203, 0.0, "2024/03/31 12:00:00", 24, "2030/12/17 14:59:59", 2.98, 10142, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (1, 4, 204, 0.0, 129801, 204, 0.0, "2024/03/31 12:00:00", 24, "2030/12/17 14:59:59", 3.0, 10142, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (2, 1, 205, 0.0, 129801, 205, 0.0, "2024/03/31 12:00:00", 24, "2030/12/17 14:59:59", 10.3, 10142, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (1, 6, 206, 0.0, 129801, 206, 0.0, "2024/03/31 12:00:00", 24, "2030/12/17 14:59:59", 2.93, 10142, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (1, 3, 207, 0.0, 129801, 207, 0.0, "2024/03/31 12:00:00", 24, "2030/12/17 14:59:59", 9.3, 10142, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (1, 4, 208, 0.0, 129801, 208, 0.0, "2024/03/31 12:00:00", 24, "2030/12/17 14:59:59", 6.9, 10142, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (2, 2, 209, 0.0, 129801, 209, 0.0, "2024/03/31 12:00:00", 24, "2030/12/17 14:59:59", 0.0, 10142, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (2, 1, 210, 0.0, 129801, 210, 0.0, "2024/03/31 12:00:00", 24, "2030/12/17 14:59:59", 5.95, 10142, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (1, 2, 211, 0.0, 129801, 211, 0.0, "2024/03/31 12:00:00", 24, "2030/12/17 14:59:59", 6.73, 10142, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (2, 2, 212, 0.0, 129801, 212, 0.0, "2024/03/31 12:00:00", 24, "2030/12/17 14:59:59", 0.0, 10142, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (2, 1, 213, 0.0, 129801, 213, 0.0, "2024/03/31 12:00:00", 24, "2030/12/17 14:59:59", 6.65, 10142, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (1, 6, 214, 0.0, 129801, 214, 0.0, "2024/03/31 12:00:00", 24, "2030/12/17 14:59:59", 3.05, 10142, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", "マコト");
