INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (0.0, 1, 10142, 10.85, 24, 100, "2024/03/31 12:00:00", "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 0.0, 129901, "2030/12/17 14:59:59", 100, 7, "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (0.0, 1, 10142, 11.15, 24, 101, "2024/03/31 12:00:00", "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 0.0, 129901, "2030/12/17 14:59:59", 101, 2, "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (0.0, 7, 10142, 6.4, 24, 102, "2024/03/31 12:00:00", "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 0.0, 129901, "2030/12/17 14:59:59", 102, 5, "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (0.0, 3, 10142, 0.0, 24, 103, "2024/03/31 12:00:00", "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 0.0, 129901, "2030/12/17 14:59:59", 103, 3, "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (0.0, 2, 10142, 2.43, 24, 104, "2024/03/31 12:00:00", "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 0.0, 129901, "2030/12/17 14:59:59", 104, 8, "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (0.0, 4, 10142, 9.08, 24, 105, "2024/03/31 12:00:00", "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 0.0, 129901, "2030/12/17 14:59:59", 105, 3, "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (0.0, 6, 10142, 0.0, 24, 106, "2024/03/31 12:00:00", "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 0.0, 129901, "2030/12/17 14:59:59", 106, 6, "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (0.0, 3, 10142, 10.6, 24, 107, "2024/03/31 12:00:00", "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 0.0, 129901, "2030/12/17 14:59:59", 107, 8, "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (0.0, 6, 10142, 7.54, 24, 108, "2024/03/31 12:00:00", "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 0.0, 129901, "2030/12/17 14:59:59", 108, 4, "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (0.0, 2, 10142, 11.5, 24, 109, "2024/03/31 12:00:00", "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 0.0, 129901, "2030/12/17 14:59:59", 109, 7, "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (0.0, 2, 10142, 8.83, 24, 110, "2024/03/31 12:00:00", "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 0.0, 129901, "2030/12/17 14:59:59", 110, 1, "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (0.0, 1, 10142, 6.68, 24, 111, "2024/03/31 12:00:00", "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 0.0, 129901, "2030/12/17 14:59:59", 111, 2, "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (0.0, 6, 10142, 8.8, 24, 112, "2024/03/31 12:00:00", "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 0.0, 129901, "2030/12/17 14:59:59", 112, 2, "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (0.0, 2, 10142, 8.9, 24, 113, "2024/03/31 12:00:00", "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 0.0, 129901, "2030/12/17 14:59:59", 113, 1, "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (0.0, 2, 10142, 3.7, 24, 114, "2024/03/31 12:00:00", "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 0.0, 129901, "2030/12/17 14:59:59", 114, 5, "エリコ");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (0.0, 1, 10142, 7.6, 24, 200, "2024/03/31 12:00:00", "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 0.0, 129801, "2030/12/17 14:59:59", 200, 2, "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (0.0, 2, 10142, 2.25, 24, 201, "2024/03/31 12:00:00", "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 0.0, 129801, "2030/12/17 14:59:59", 201, 1, "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (0.0, 2, 10142, 3.28, 24, 202, "2024/03/31 12:00:00", "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 0.0, 129801, "2030/12/17 14:59:59", 202, 1, "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (0.0, 4, 10142, 2.98, 24, 203, "2024/03/31 12:00:00", "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 0.0, 129801, "2030/12/17 14:59:59", 203, 1, "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (0.0, 4, 10142, 3.0, 24, 204, "2024/03/31 12:00:00", "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 0.0, 129801, "2030/12/17 14:59:59", 204, 1, "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (0.0, 1, 10142, 10.3, 24, 205, "2024/03/31 12:00:00", "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 0.0, 129801, "2030/12/17 14:59:59", 205, 2, "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (0.0, 6, 10142, 2.93, 24, 206, "2024/03/31 12:00:00", "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 0.0, 129801, "2030/12/17 14:59:59", 206, 1, "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (0.0, 3, 10142, 9.3, 24, 207, "2024/03/31 12:00:00", "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 0.0, 129801, "2030/12/17 14:59:59", 207, 1, "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (0.0, 4, 10142, 6.9, 24, 208, "2024/03/31 12:00:00", "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 0.0, 129801, "2030/12/17 14:59:59", 208, 1, "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (0.0, 2, 10142, 0.0, 24, 209, "2024/03/31 12:00:00", "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 0.0, 129801, "2030/12/17 14:59:59", 209, 2, "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (0.0, 1, 10142, 5.95, 24, 210, "2024/03/31 12:00:00", "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 0.0, 129801, "2030/12/17 14:59:59", 210, 2, "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (0.0, 2, 10142, 6.73, 24, 211, "2024/03/31 12:00:00", "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 0.0, 129801, "2030/12/17 14:59:59", 211, 1, "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (0.0, 2, 10142, 0.0, 24, 212, "2024/03/31 12:00:00", "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 0.0, 129801, "2030/12/17 14:59:59", 212, 2, "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (0.0, 1, 10142, 6.65, 24, 213, "2024/03/31 12:00:00", "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 0.0, 129801, "2030/12/17 14:59:59", 213, 2, "マコト");
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (0.0, 6, 10142, 3.05, 24, 214, "2024/03/31 12:00:00", "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 0.0, 129801, "2030/12/17 14:59:59", 214, 1, "マコト");
