INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129901, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", "エリコ", 1, 24, 0.0, 0.0, 100, "2024/03/31 12:00:00", 7, 100, "2030/12/17 14:59:59", 10.85, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129901, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", "エリコ", 1, 24, 0.0, 0.0, 101, "2024/03/31 12:00:00", 2, 101, "2030/12/17 14:59:59", 11.15, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129901, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", "エリコ", 7, 24, 0.0, 0.0, 102, "2024/03/31 12:00:00", 5, 102, "2030/12/17 14:59:59", 6.4, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129901, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", "エリコ", 3, 24, 0.0, 0.0, 103, "2024/03/31 12:00:00", 3, 103, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129901, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", "エリコ", 2, 24, 0.0, 0.0, 104, "2024/03/31 12:00:00", 8, 104, "2030/12/17 14:59:59", 2.43, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129901, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", "エリコ", 4, 24, 0.0, 0.0, 105, "2024/03/31 12:00:00", 3, 105, "2030/12/17 14:59:59", 9.08, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129901, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", "エリコ", 6, 24, 0.0, 0.0, 106, "2024/03/31 12:00:00", 6, 106, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129901, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", "エリコ", 3, 24, 0.0, 0.0, 107, "2024/03/31 12:00:00", 8, 107, "2030/12/17 14:59:59", 10.6, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129901, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", "エリコ", 6, 24, 0.0, 0.0, 108, "2024/03/31 12:00:00", 4, 108, "2030/12/17 14:59:59", 7.54, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129901, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", "エリコ", 2, 24, 0.0, 0.0, 109, "2024/03/31 12:00:00", 7, 109, "2030/12/17 14:59:59", 11.5, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129901, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", "エリコ", 2, 24, 0.0, 0.0, 110, "2024/03/31 12:00:00", 1, 110, "2030/12/17 14:59:59", 8.83, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129901, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", "エリコ", 1, 24, 0.0, 0.0, 111, "2024/03/31 12:00:00", 2, 111, "2030/12/17 14:59:59", 6.68, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129901, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", "エリコ", 6, 24, 0.0, 0.0, 112, "2024/03/31 12:00:00", 2, 112, "2030/12/17 14:59:59", 8.8, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129901, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", "エリコ", 2, 24, 0.0, 0.0, 113, "2024/03/31 12:00:00", 1, 113, "2030/12/17 14:59:59", 8.9, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129901, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", "エリコ", 2, 24, 0.0, 0.0, 114, "2024/03/31 12:00:00", 5, 114, "2030/12/17 14:59:59", 3.7, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129801, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", "マコト", 1, 24, 0.0, 0.0, 200, "2024/03/31 12:00:00", 2, 200, "2030/12/17 14:59:59", 7.6, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129801, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", "マコト", 2, 24, 0.0, 0.0, 201, "2024/03/31 12:00:00", 1, 201, "2030/12/17 14:59:59", 2.25, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129801, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", "マコト", 2, 24, 0.0, 0.0, 202, "2024/03/31 12:00:00", 1, 202, "2030/12/17 14:59:59", 3.28, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129801, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", "マコト", 4, 24, 0.0, 0.0, 203, "2024/03/31 12:00:00", 1, 203, "2030/12/17 14:59:59", 2.98, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129801, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", "マコト", 4, 24, 0.0, 0.0, 204, "2024/03/31 12:00:00", 1, 204, "2030/12/17 14:59:59", 3.0, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129801, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", "マコト", 1, 24, 0.0, 0.0, 205, "2024/03/31 12:00:00", 2, 205, "2030/12/17 14:59:59", 10.3, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129801, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", "マコト", 6, 24, 0.0, 0.0, 206, "2024/03/31 12:00:00", 1, 206, "2030/12/17 14:59:59", 2.93, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129801, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", "マコト", 3, 24, 0.0, 0.0, 207, "2024/03/31 12:00:00", 1, 207, "2030/12/17 14:59:59", 9.3, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129801, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", "マコト", 4, 24, 0.0, 0.0, 208, "2024/03/31 12:00:00", 1, 208, "2030/12/17 14:59:59", 6.9, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129801, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", "マコト", 2, 24, 0.0, 0.0, 209, "2024/03/31 12:00:00", 2, 209, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129801, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", "マコト", 1, 24, 0.0, 0.0, 210, "2024/03/31 12:00:00", 2, 210, "2030/12/17 14:59:59", 5.95, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129801, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", "マコト", 2, 24, 0.0, 0.0, 211, "2024/03/31 12:00:00", 1, 211, "2030/12/17 14:59:59", 6.73, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129801, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", "マコト", 2, 24, 0.0, 0.0, 212, "2024/03/31 12:00:00", 2, 212, "2030/12/17 14:59:59", 0.0, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129801, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", "マコト", 1, 24, 0.0, 0.0, 213, "2024/03/31 12:00:00", 2, 213, "2030/12/17 14:59:59", 6.65, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES (129801, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", "マコト", 6, 24, 0.0, 0.0, 214, "2024/03/31 12:00:00", 1, 214, "2030/12/17 14:59:59", 3.05, 10142);
