INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 10.85, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 0.0, 7, 129901, 100, 24, "エリコ", 0.0, "2024/03/31 12:00:00", 1, 100, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 11.15, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 0.0, 2, 129901, 101, 24, "エリコ", 0.0, "2024/03/31 12:00:00", 1, 101, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 6.4, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 0.0, 5, 129901, 102, 24, "エリコ", 0.0, "2024/03/31 12:00:00", 7, 102, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 0.0, 3, 129901, 103, 24, "エリコ", 0.0, "2024/03/31 12:00:00", 3, 103, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 2.43, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 0.0, 8, 129901, 104, 24, "エリコ", 0.0, "2024/03/31 12:00:00", 2, 104, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 9.08, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 0.0, 3, 129901, 105, 24, "エリコ", 0.0, "2024/03/31 12:00:00", 4, 105, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 0.0, 6, 129901, 106, 24, "エリコ", 0.0, "2024/03/31 12:00:00", 6, 106, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 10.6, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 0.0, 8, 129901, 107, 24, "エリコ", 0.0, "2024/03/31 12:00:00", 3, 107, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 7.54, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 0.0, 4, 129901, 108, 24, "エリコ", 0.0, "2024/03/31 12:00:00", 6, 108, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 11.5, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 0.0, 7, 129901, 109, 24, "エリコ", 0.0, "2024/03/31 12:00:00", 2, 109, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 8.83, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 0.0, 1, 129901, 110, 24, "エリコ", 0.0, "2024/03/31 12:00:00", 2, 110, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 6.68, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 0.0, 2, 129901, 111, 24, "エリコ", 0.0, "2024/03/31 12:00:00", 1, 111, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 8.8, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 0.0, 2, 129901, 112, 24, "エリコ", 0.0, "2024/03/31 12:00:00", 6, 112, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 8.9, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 0.0, 1, 129901, 113, 24, "エリコ", 0.0, "2024/03/31 12:00:00", 2, 113, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 3.7, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 0.0, 5, 129901, 114, 24, "エリコ", 0.0, "2024/03/31 12:00:00", 2, 114, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 7.6, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 0.0, 2, 129801, 200, 24, "マコト", 0.0, "2024/03/31 12:00:00", 1, 200, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 2.25, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 0.0, 1, 129801, 201, 24, "マコト", 0.0, "2024/03/31 12:00:00", 2, 201, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 3.28, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 0.0, 1, 129801, 202, 24, "マコト", 0.0, "2024/03/31 12:00:00", 2, 202, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 2.98, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 0.0, 1, 129801, 203, 24, "マコト", 0.0, "2024/03/31 12:00:00", 4, 203, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 3.0, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 0.0, 1, 129801, 204, 24, "マコト", 0.0, "2024/03/31 12:00:00", 4, 204, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 10.3, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 0.0, 2, 129801, 205, 24, "マコト", 0.0, "2024/03/31 12:00:00", 1, 205, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 2.93, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 0.0, 1, 129801, 206, 24, "マコト", 0.0, "2024/03/31 12:00:00", 6, 206, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 9.3, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 0.0, 1, 129801, 207, 24, "マコト", 0.0, "2024/03/31 12:00:00", 3, 207, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 6.9, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 0.0, 1, 129801, 208, 24, "マコト", 0.0, "2024/03/31 12:00:00", 4, 208, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 0.0, 2, 129801, 209, 24, "マコト", 0.0, "2024/03/31 12:00:00", 2, 209, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 5.95, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 0.0, 2, 129801, 210, 24, "マコト", 0.0, "2024/03/31 12:00:00", 1, 210, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 6.73, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 0.0, 1, 129801, 211, 24, "マコト", 0.0, "2024/03/31 12:00:00", 2, 211, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 0.0, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 0.0, 2, 129801, 212, 24, "マコト", 0.0, "2024/03/31 12:00:00", 2, 212, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 6.65, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 0.0, 2, 129801, 213, 24, "マコト", 0.0, "2024/03/31 12:00:00", 1, 213, 10142);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2030/12/17 14:59:59", 3.05, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 0.0, 1, 129801, 214, 24, "マコト", 0.0, "2024/03/31 12:00:00", 6, 214, 10142);
