INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2024/03/31 12:00:00", 100, 7, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 129901, 1, "2030/12/17 14:59:59", "エリコ", 0.0, 24, 0.0, 10.85, 10142, 100);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2024/03/31 12:00:00", 101, 2, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 129901, 1, "2030/12/17 14:59:59", "エリコ", 0.0, 24, 0.0, 11.15, 10142, 101);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2024/03/31 12:00:00", 102, 5, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 129901, 7, "2030/12/17 14:59:59", "エリコ", 0.0, 24, 0.0, 6.4, 10142, 102);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2024/03/31 12:00:00", 103, 3, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 129901, 3, "2030/12/17 14:59:59", "エリコ", 0.0, 24, 0.0, 0.0, 10142, 103);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2024/03/31 12:00:00", 104, 8, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 129901, 2, "2030/12/17 14:59:59", "エリコ", 0.0, 24, 0.0, 2.43, 10142, 104);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2024/03/31 12:00:00", 105, 3, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 129901, 4, "2030/12/17 14:59:59", "エリコ", 0.0, 24, 0.0, 9.08, 10142, 105);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2024/03/31 12:00:00", 106, 6, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 129901, 6, "2030/12/17 14:59:59", "エリコ", 0.0, 24, 0.0, 0.0, 10142, 106);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2024/03/31 12:00:00", 107, 8, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 129901, 3, "2030/12/17 14:59:59", "エリコ", 0.0, 24, 0.0, 10.6, 10142, 107);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2024/03/31 12:00:00", 108, 4, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 129901, 6, "2030/12/17 14:59:59", "エリコ", 0.0, 24, 0.0, 7.54, 10142, 108);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2024/03/31 12:00:00", 109, 7, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 129901, 2, "2030/12/17 14:59:59", "エリコ", 0.0, 24, 0.0, 11.5, 10142, 109);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2024/03/31 12:00:00", 110, 1, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 129901, 2, "2030/12/17 14:59:59", "エリコ", 0.0, 24, 0.0, 8.83, 10142, 110);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2024/03/31 12:00:00", 111, 2, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 129901, 1, "2030/12/17 14:59:59", "エリコ", 0.0, 24, 0.0, 6.68, 10142, 111);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2024/03/31 12:00:00", 112, 2, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 129901, 6, "2030/12/17 14:59:59", "エリコ", 0.0, 24, 0.0, 8.8, 10142, 112);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2024/03/31 12:00:00", 113, 1, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 129901, 2, "2030/12/17 14:59:59", "エリコ", 0.0, 24, 0.0, 8.9, 10142, 113);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2024/03/31 12:00:00", 114, 5, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 129901, 2, "2030/12/17 14:59:59", "エリコ", 0.0, 24, 0.0, 3.7, 10142, 114);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2024/03/31 12:00:00", 200, 2, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 129801, 1, "2030/12/17 14:59:59", "マコト", 0.0, 24, 0.0, 7.6, 10142, 200);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2024/03/31 12:00:00", 201, 1, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 129801, 2, "2030/12/17 14:59:59", "マコト", 0.0, 24, 0.0, 2.25, 10142, 201);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2024/03/31 12:00:00", 202, 1, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 129801, 2, "2030/12/17 14:59:59", "マコト", 0.0, 24, 0.0, 3.28, 10142, 202);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2024/03/31 12:00:00", 203, 1, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 129801, 4, "2030/12/17 14:59:59", "マコト", 0.0, 24, 0.0, 2.98, 10142, 203);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2024/03/31 12:00:00", 204, 1, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 129801, 4, "2030/12/17 14:59:59", "マコト", 0.0, 24, 0.0, 3.0, 10142, 204);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2024/03/31 12:00:00", 205, 2, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 129801, 1, "2030/12/17 14:59:59", "マコト", 0.0, 24, 0.0, 10.3, 10142, 205);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2024/03/31 12:00:00", 206, 1, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 129801, 6, "2030/12/17 14:59:59", "マコト", 0.0, 24, 0.0, 2.93, 10142, 206);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2024/03/31 12:00:00", 207, 1, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 129801, 3, "2030/12/17 14:59:59", "マコト", 0.0, 24, 0.0, 9.3, 10142, 207);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2024/03/31 12:00:00", 208, 1, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 129801, 4, "2030/12/17 14:59:59", "マコト", 0.0, 24, 0.0, 6.9, 10142, 208);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2024/03/31 12:00:00", 209, 2, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 129801, 2, "2030/12/17 14:59:59", "マコト", 0.0, 24, 0.0, 0.0, 10142, 209);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2024/03/31 12:00:00", 210, 2, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 129801, 1, "2030/12/17 14:59:59", "マコト", 0.0, 24, 0.0, 5.95, 10142, 210);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2024/03/31 12:00:00", 211, 1, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 129801, 2, "2030/12/17 14:59:59", "マコト", 0.0, 24, 0.0, 6.73, 10142, 211);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2024/03/31 12:00:00", 212, 2, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 129801, 2, "2030/12/17 14:59:59", "マコト", 0.0, 24, 0.0, 0.0, 10142, 212);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2024/03/31 12:00:00", 213, 2, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 129801, 1, "2030/12/17 14:59:59", "マコト", 0.0, 24, 0.0, 6.65, 10142, 213);
INSERT INTO `v1_2b9f0059b2a36528c18f1edf8f95b31bec07d323fc31d8229d9717e44fb0f49a` VALUES ("2024/03/31 12:00:00", 214, 1, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 129801, 6, "2030/12/17 14:59:59", "マコト", 0.0, 24, 0.0, 3.05, 10142, 214);
