INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (1, "ペコリーヌが丹精込めて握った特大おにぎり。\nごはんは命のエネルギーを体現しているらしい。\n普通の人が満腹に感じる量の倍のサイズの中に\n最高級の具を閉じ込めた【美食殿】らしい一品。", 52001, 500, 0, "ペコリーヌの\n満腹ツインおにぎり", 2, "500マイルを獲得する。", 10, 1, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 1, 2, "ペコリーヌの満腹ツインおにぎり", 51001, 0, 1);
