INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (1, "アオイがお弁当として作ったエルフ伝統の野菜\nサンド。一緒についてきてくれる最高の友だち\nの助言により、どんなに深い森の中でも全速力\nで駆け抜けることができるらしい。（アオイ談）", 52016, 500, 0, "アオイの疾走\nサンドイッチ", 20005, "なし", 0, 1, "次に振るダイスの目が必ず「6」になる。", 2, 0, "アオイの疾走サンドイッチ", 51016, 0, 3);
