INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (1, "コッコロの指導により料理の腕を上げたランファ\nが手作りした肉じゃが。まだ改善の余地はある\nものの、不器用なランファが誰かのために積み\n重ねた努力を思うととてもおいしく味わえる。", 52018, 500, 0, "ランファの世話焼き三昧肉じゃが", 20006, "1000マイルを獲得する。", 10, 1, "次に振るダイスの目が必ず「3」になる。", 2, 6, "ランファの世話焼き三昧肉じゃが", 51018, 0, 1);
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (1, "バンディ・シスターズの昨夜の夕食カレー。作り\n過ぎて食べきれずにいたものをワカナがお裾分け\nしてくれた。寝かされていたためしっかりと味の\n染みた、極上の二日目カレーとなっている。", 52019, 500, 0, "ワカナの節約腹八分目カレー", 20007, "なし", 0, 1, "次に振るダイスの目が必ず「8」になる。", 2, 0, "ワカナの節約腹八分目カレー", 0, 0, 1);
