INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (0, "ペコリーヌの\n満腹ツインおにぎり", "500マイルを獲得する。", 0, "ペコリーヌの満腹ツインおにぎり", 51001, 500, 1, 1, 2, 52001, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 1, 2, 10, "ペコリーヌが丹精込めて握った特大おにぎり。\nごはんは命のエネルギーを体現しているらしい。\n普通の人が満腹に感じる量の倍のサイズの中に\n最高級の具を閉じ込めた【美食殿】らしい一品。", 1);
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (0, "コッコロの\nゆるりと休憩サラダ", "500マイルを獲得する。", 0, "コッコロのゆるりと休憩サラダ", 51002, 500, 2, 1, 2, 52002, "次に振るダイスの目が必ず「1」になる。さらに、次のターン数のカウントをスキップする。", 1, 30001, 10, "コッコロが皆のことを思って用意したサラダ。\n栄養バランスも考えられたうえに腹持ちもよく\n味だけではなく見た目にも気を配られている。\n冒険への活力を与えてくれる慈愛溢れる一品。", 3);
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (0, "キャルの\n絶品五つ星バーガー", "平凡な未鑑定トレジャーを1個獲得する。", 0, "キャルの絶品五つ星バーガー", 51003, 500, 1, 1, 1, 52003, "次に振るダイスの目が必ず「5」になる。", 1, 20004, 10, "キャルが食べたい物を挟み込んだハンバーガー。\n香ばしいパンとお肉の香りが食欲を刺激する。\n魔物でない素材で作られた、冒険中に食べるには\nゴージャスすぎる一品、とキャルは信じている。", 2);
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (0, "リリのコツコツ\nまい進かしわ餅", "800マイルを獲得する。", 0, "リリのコツコツまい進かしわ餅", 51004, 500, 3, 3, 4, 52004, "ダイスを3回振るまでの間、ダイスの目が必ず「4」になる。", 1, 20001, 10, "リリが作った柏餅……の中でも、上手く作れた\n選りすぐりの品。最高の素材を使用した上、\n失敗した分はリリが自分で食べてしまったため\n単価でみるとかなりの高級品となっている。", 2);
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (0, "クリアの彩り華やか\nバラエティ弁当", "次にダイスを振った際、1マス多く進める。", 0, "クリアの彩り華やかバラエティ弁当", 51005, 500, 3, 0, 1, 52005, "ランダムなイベントを発生させる。", 3, 1, 11, "クリアが朝早くから用意した冒険用のお弁当。\nおかずは様々でどれも絶品だが、どんなものが\n入っているかはみんなにも秘密にされているため\n蓋を開けるまでのお楽しみとなっている。", 4);
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (0, "プレシアの食いしん坊\nぶーぶー肉まん", "800マイルを獲得する。", 0, "プレシアの食いしん坊ぶーぶー肉まん", 51006, 500, 1, 1, 4, 52006, "次に振るダイスの目が「4・5・6」のみになる。", 1, 20002, 10, "プレシアが選び抜いた具を詰め込んだ肉まん。\n彼女の希望で、通常の肉まんの数倍のサイズに\nなっている。味もボリュームも満点でハッピー\n間違いなしですよ♪とは調理担当クリアの談。", 2);
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (0, "カヤとイノリの\n食欲倍増ケバブサンド", "福引券を1枚獲得する。", 0, "カヤとイノリの食欲倍増ケバブサンド", 51007, 500, 1, 2, 3, 52007, "次に振るダイスの目を必ず「3」に、その次に振るダイスの目を必ず「6」にする。", 1, 1, 10, "カヤとイノリが弁当代わりに持ってきたケバブ。\nイノリ行きつけの店の硬派な味わいで、ギャング\nとしてのやる気が一人3倍、コンビで6倍となり\n下剋上を成し遂げるパワーが湧く……らしい。", 7);
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (1, "タマキの金運招く\nレインボーたい焼き", "500マイルを獲得する。", 0, "タマキの金運招くレインボーたい焼き", 51008, 500, 2, 0, 2, 52008, "次にマイルマスに止まった際の獲得マイルが200%アップする。", 2, 200, 10, "タマキたちが『ジオ・テオゴニア』に持ち込んだ\nたい焼きの概念が現地の文化と溶け合い生まれた\n魔改造スイーツ。虹色に輝くその甘さは、実に\nランドソルのたい焼きの数倍と言われている。", 5);
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (0, "クローチェの\nフルパワーポテトサラダ", "平凡な未鑑定トレジャーを1個獲得する。", 1, "クローチェのフルパワーポテトサラダ", 51009, 500, 2, 2, 1, 52009, "[FF7C4E,D62146]【上書き不可】[-]次に振るダイスの目を必ず「8」に、その次に振るダイスの目を必ず「1」にする。", 1, 1, 10, "クローチェがフルパワーで作ったポテトサラダ。\n本来はガレットを作ろうとしたが、うっかり\n握り潰してしまったため、軌道修正して仕上げ\nた一品。経緯はともかく味は非常に美味である。", 7);
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (2, "アヤネとクルミの\n青空ピクニックセット", "500マイルを獲得する。", 0, "アヤネとクルミの青空ピクニックセット", 51010, 500, 3, 0, 2, 52010, "マップ上の「マイルマス」「トレジャーマス」 「ショップマス」のRANKが1つ上がる。効果は対象のマスに計2回止まるまで継続する。", 2, 1, 10, "アヤネとクルミが二人で用意した特別なランチ\nバスケット。仲間との冒険への希望が詰まった\nご機嫌な料理の数々で、道中の様々な出来事が\n１ランク上のイベントに感じられるようになる。", 6);
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (0, "リマの不思議な\nスイートアップル", "福引券を1枚獲得する。", 0, "リマの不思議なスイートアップル", 51011, 500, 3, 2, 3, 52011, "次に振るダイスの目を必ず「2」に、その次に振るダイスの目を必ず「7」にする。", 1, 1, 10, "リマがネネカにもらったリンゴで作った秘密の\nスイーツ。爽やかな酸味と濃厚な甘さがまるで\n魔法のようなバランスでお互いを引きたてており\n食べた者をたちまち虜にしてしまうという。", 7);
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (0, "ニノンのオリジナル\n一二三寿司", "平凡な未鑑定トレジャーを1個獲得する。", 0, "ニノンのオリジナル一二三寿司", 51012, 500, 2, 1, 1, 52012, "次に振るダイスの目が「1・2・3」のみになる。さらに、次のターン数のカウントをスキップする。", 1, 30002, 10, "ニノンがジオ・テオゴニアに持ち込んだ特別な\n寿司。厳選された新鮮な魚介を使った逸品だが、\n一部のネタは、寿司のセオリーを逸脱したニノン\n我流のものに仕上がっている。", 3);
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (0, "クレジッタの開運\nお菓子盛り合わせ", "800マイルを獲得する。", 0, "クレジッタの開運お菓子盛り合わせ", 51013, 500, 3, 0, 4, 52013, "RANK2のマイルショップを開く。", 3, 2, 10, "クレジッタが考案した、ジオ・テオゴニアで流通\nしているチョコの貨幣の盛り合わせ。一見すると\n金貨を口に入れるおバカさんではあるが、通貨を\n食べるという背徳感が隠し味となっている。", 8);
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (0, "リノの\n「時は金時」かき氷", "次にダイスを振った際、1マス多く進める。", 0, "リノの「時は金時」かき氷", 51014, 500, 2, 2, 1, 52014, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 1, 1, 11, "可愛い妹がお兄ちゃんのために心を込めて作った\nスペシャルなかき氷。一口食べるとたちどころに\nクールな甘さが精神を研ぎ澄まし、いかなる\nお得をも見逃さない要塞賢母と化すという。", 9);
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (0, "シズルの愛情\n盛り盛りハンバーグ", "次にダイスを振った際、1マス多く進める。", 0, "シズルの愛情盛り盛りハンバーグ", 51015, 500, 1, 1, 1, 52015, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 1, 2, 11, "お姉ちゃんが弟くんのために心を込めて作った\nスペシャルなハンバーグ。アツアツの肉汁と共に\n溢れんばかりの愛が詰まっており、一口食べる\nだけで、走り回りたくなるような美味しさ。", 1);
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (0, "アオイの疾走\nサンドイッチ", "なし", 0, "アオイの疾走サンドイッチ", 51016, 500, 3, 1, 0, 52016, "次に振るダイスの目が必ず「6」になる。", 1, 20005, 0, "アオイがお弁当として作ったエルフ伝統の野菜\nサンド。一緒についてきてくれる最高の友だち\nの助言により、どんなに深い森の中でも全速力\nで駆け抜けることができるらしい。（アオイ談）", 2);
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (0, "クウカの緊縛\n触手活け造り", "なし", 1, "クウカの緊縛触手活け造り", 51017, 500, 3, 2, 5, 52017, "[FF7C4E,D62146]【上書き不可】[-]次のターン「移動系カテゴリーの料理」が食べられない代わりに、その場で2000マイルを獲得する。", 1, 1, 10, "数多くのドM妄想をしてきたクウカの中で最近の\nマイブームである触手をモチーフとした活造り。\n新鮮な蛸足が、濃厚なうま味と共に物理的に絡み\n付き身も心も縛りあげられる様な体験ができる。", 2);
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (0, "ランファの世話焼き三昧肉じゃが", "1000マイルを獲得する。", 0, "ランファの世話焼き三昧肉じゃが", 51018, 500, 1, 1, 6, 52018, "次に振るダイスの目が必ず「3」になる。", 1, 20006, 10, "コッコロの指導により料理の腕を上げたランファ\nが手作りした肉じゃが。まだ改善の余地はある\nものの、不器用なランファが誰かのために積み\n重ねた努力を思うととてもおいしく味わえる。", 2);
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (0, "ワカナの節約腹八分目カレー", "なし", 0, "ワカナの節約腹八分目カレー", 0, 500, 1, 1, 0, 52019, "次に振るダイスの目が必ず「8」になる。", 1, 20007, 0, "バンディ・シスターズの昨夜の夕食カレー。作り\n過ぎて食べきれずにいたものをワカナがお裾分け\nしてくれた。寝かされていたためしっかりと味の\n染みた、極上の二日目カレーとなっている。", 2);
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (0, "リノの\n「時は金時」かき氷", "次にダイスを振った際、1マス多く進める。", 0, "リノの「時は金時」かき氷", 51014, 500, 2, 2, 1, 52020, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 2, 1, 11, "可愛い妹がお兄ちゃんのために心を込めて作った\nスペシャルなかき氷。一口食べるとたちどころに\nクールな甘さが精神を研ぎ澄まし、いかなる\nお得をも見逃さない要塞賢母と化すという。", 9);
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (0, "カスミの真実見抜く\nボンボン・ショコラ", "	平凡な未鑑定トレジャーを2個獲得する。", 0, "カスミの真実見抜くボンボン・ショコラ", 51021, 500, 3, 3, 7, 52021, "トレジャーマスをスキップして移動できる。効果は3ターンの間継続する。", 2, 2, 10, "綺麗な装飾箱に納められたボンボン・ショコラ。\nそのほろ苦さを秘めた濃密な甘さが灰色の脳細胞\nに働きかける時、名探偵の鋭い推理が隠された\n謎を全て白日の下へと晒し、事件を解決へ導く。", 9);
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (0, "アキノのゲヘニアン\nチキンプレート", "なし", 0, "アキノのゲヘニアンチキンプレート", 51022, 500, 1, 3, 0, 52022, "ダイスを3回振るまでの間、「5と6」の⽬が出る確率が3倍になる。", 1, 20008, 0, "アキノがジオ・ゲヘナで仕入れてきたスパイスを\n使用した、お洒落で豪華なチキンプレート。牧場\n特製ヨーグルトソースと奏でる刺激的な味わいは\nまさに二つの世界の奇跡のマリアージュである。", 2);
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (0, "ミミのコロコロ\nお手玉おにぎり", "平凡な未鑑定トレジャーを1個獲得する。", 1, "ミミのコロコロお手玉おにぎり", 51023, 500, 1, 2, 1, 52023, "[FF7C4E,D62146]【上書き不可】[-]次に振るダイスの目を必ず「6」に、その次に振るダイスの目を必ず「3」にする。", 1, 1, 10, "ミミが、ジオ・ゲヘナで助けてくれたお姉さん達\nのことを思い作ったおにぎり。それぞれ異なる\n味付けがされているが、ミミの願い通りお互いに\nケンカをせず仲良くハーモニーを奏でている。", 7);
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (0, "アユミの隠密\nガトーインビジブル", "次にダイスを振った際、3マス多く進める。", 0, "アユミの隠密ガトーインビジブル", 51024, 500, 3, 0, 3, 52024, "RANK2のマイルショップを開く。", 3, 2, 11, "気が付くとリリの手元にあった不思議なお菓子。\n一見普通のケーキだが、内部に様々な具材が\n隠れるように入っている。複雑で重層的な\n味わいを見せる様は、まるで誰かさんのよう。", 8);
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (0, "アネモネの\n花爛漫ステーキサラダ", "なし", 0, "アネモネの花爛漫ステーキサラダ", 51025, 500, 3, 1, 0, 52025, "「マイルマス」「トレジャーマス」をスキップして移動できる。効果は1ターンの間継続する。", 2, 3, 0, "アネモネが世話する秘密の菜園の野菜を使った\nサラダ。栄養を重視するアネモネは草食獣の肉を\n野菜と解釈、当初は野菜の上に肉を置いただけ\nであったが、ネラの指導で盛り付け直された。", 9);
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (0, "アンナの\n煉獄蕃茄煮混沌卵", "福引券を2枚獲得する。", 0, "アンナの煉獄蕃茄煮混沌卵", 51026, 500, 1, 0, 8, 52026, "ランダムなイベントを発生させる。", 3, 1, 10, "ジオ・ゲヘナを探索中の冥姫たちが現地で採取\nしたスパイスで味付けした一皿。赤く煮え滾る\nソースは刺激的かつ非常に奥深い味わいで未知の\n大地の如く冒険者たちをその深奥へと誘う。", 4);
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (0, "ユイの\n栄養満点まごころ弁当", "なし", 0, "ユイの栄養満点まごころ弁当", 51027, 500, 3, 2, 0, 52027, "ダイスを2回振るまでの間、ダイスの目が必ず「5」になる。", 1, 20004, 0, "ユイが食べる人のことを考え、心を込めて作った\nお弁当。程よいサイズのお弁当箱に愛情と手間暇\nをたっぷりとかけた極上の料理が詰まっており、\n食べた者の心と胃袋を優しく満たしてくれる。", 2);
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (0, "リンの\nナッツ入りぐうたらあんパン", "なし", 0, "リンのナッツ入りぐうたらあんパン", 51028, 500, 3, 1, 9, 52028, "次に振るダイスの目が「1・2」のみになる\n代わりに、その場で福引券を3枚獲得する。", 1, 30003, 10, "リンからもらった非常食のあんパン。ナッツが\n練り込まれクランキーな食感で食べ飽きない。\nゲームのため部屋に引きこもる際のお供であり、\n必要な栄養素が全て賄える完全栄養食、らしい。", 2);
