INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (52001, "ペコリーヌの\n満腹ツインおにぎり", "ペコリーヌの満腹ツインおにぎり", 1, 51001, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 0, 1, 2, 1, 2, "500マイルを獲得する。", 1, 10, 500, 0, "ペコリーヌが丹精込めて握った特大おにぎり。\nごはんは命のエネルギーを体現しているらしい。\n普通の人が満腹に感じる量の倍のサイズの中に\n最高級の具を閉じ込めた【美食殿】らしい一品。");
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (52002, "コッコロの\nゆるりと休憩サラダ", "コッコロのゆるりと休憩サラダ", 1, 51002, "次に振るダイスの目が必ず「1」になる。さらに、次のターン数のカウントをスキップする。", 0, 3, 30001, 2, 2, "500マイルを獲得する。", 1, 10, 500, 0, "コッコロが皆のことを思って用意したサラダ。\n栄養バランスも考えられたうえに腹持ちもよく\n味だけではなく見た目にも気を配られている。\n冒険への活力を与えてくれる慈愛溢れる一品。");
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (52003, "キャルの\n絶品五つ星バーガー", "キャルの絶品五つ星バーガー", 1, 51003, "次に振るダイスの目が必ず「5」になる。", 0, 2, 20004, 1, 1, "平凡な未鑑定トレジャーを1個獲得する。", 1, 10, 500, 0, "キャルが食べたい物を挟み込んだハンバーガー。\n香ばしいパンとお肉の香りが食欲を刺激する。\n魔物でない素材で作られた、冒険中に食べるには\nゴージャスすぎる一品、とキャルは信じている。");
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (52004, "リリのコツコツ\nまい進かしわ餅", "リリのコツコツまい進かしわ餅", 1, 51004, "ダイスを3回振るまでの間、ダイスの目が必ず「4」になる。", 0, 2, 20001, 3, 4, "800マイルを獲得する。", 3, 10, 500, 0, "リリが作った柏餅……の中でも、上手く作れた\n選りすぐりの品。最高の素材を使用した上、\n失敗した分はリリが自分で食べてしまったため\n単価でみるとかなりの高級品となっている。");
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (52005, "クリアの彩り華やか\nバラエティ弁当", "クリアの彩り華やかバラエティ弁当", 3, 51005, "ランダムなイベントを発生させる。", 0, 4, 1, 3, 1, "次にダイスを振った際、1マス多く進める。", 0, 11, 500, 0, "クリアが朝早くから用意した冒険用のお弁当。\nおかずは様々でどれも絶品だが、どんなものが\n入っているかはみんなにも秘密にされているため\n蓋を開けるまでのお楽しみとなっている。");
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (52006, "プレシアの食いしん坊\nぶーぶー肉まん", "プレシアの食いしん坊ぶーぶー肉まん", 1, 51006, "次に振るダイスの目が「4・5・6」のみになる。", 0, 2, 20002, 1, 4, "800マイルを獲得する。", 1, 10, 500, 0, "プレシアが選び抜いた具を詰め込んだ肉まん。\n彼女の希望で、通常の肉まんの数倍のサイズに\nなっている。味もボリュームも満点でハッピー\n間違いなしですよ♪とは調理担当クリアの談。");
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (52007, "カヤとイノリの\n食欲倍増ケバブサンド", "カヤとイノリの食欲倍増ケバブサンド", 1, 51007, "次に振るダイスの目を必ず「3」に、その次に振るダイスの目を必ず「6」にする。", 0, 7, 1, 1, 3, "福引券を1枚獲得する。", 2, 10, 500, 0, "カヤとイノリが弁当代わりに持ってきたケバブ。\nイノリ行きつけの店の硬派な味わいで、ギャング\nとしてのやる気が一人3倍、コンビで6倍となり\n下剋上を成し遂げるパワーが湧く……らしい。");
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (52008, "タマキの金運招く\nレインボーたい焼き", "タマキの金運招くレインボーたい焼き", 2, 51008, "次にマイルマスに止まった際の獲得マイルが200%アップする。", 0, 5, 200, 2, 2, "500マイルを獲得する。", 0, 10, 500, 1, "タマキたちが『ジオ・テオゴニア』に持ち込んだ\nたい焼きの概念が現地の文化と溶け合い生まれた\n魔改造スイーツ。虹色に輝くその甘さは、実に\nランドソルのたい焼きの数倍と言われている。");
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (52009, "クローチェの\nフルパワーポテトサラダ", "クローチェのフルパワーポテトサラダ", 1, 51009, "[FF7C4E,D62146]【上書き不可】[-]次に振るダイスの目を必ず「8」に、その次に振るダイスの目を必ず「1」にする。", 1, 7, 1, 2, 1, "平凡な未鑑定トレジャーを1個獲得する。", 2, 10, 500, 0, "クローチェがフルパワーで作ったポテトサラダ。\n本来はガレットを作ろうとしたが、うっかり\n握り潰してしまったため、軌道修正して仕上げ\nた一品。経緯はともかく味は非常に美味である。");
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (52010, "アヤネとクルミの\n青空ピクニックセット", "アヤネとクルミの青空ピクニックセット", 2, 51010, "マップ上の「マイルマス」「トレジャーマス」 「ショップマス」のRANKが1つ上がる。効果は対象のマスに計2回止まるまで継続する。", 0, 6, 1, 3, 2, "500マイルを獲得する。", 0, 10, 500, 2, "アヤネとクルミが二人で用意した特別なランチ\nバスケット。仲間との冒険への希望が詰まった\nご機嫌な料理の数々で、道中の様々な出来事が\n１ランク上のイベントに感じられるようになる。");
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (52011, "リマの不思議な\nスイートアップル", "リマの不思議なスイートアップル", 1, 51011, "次に振るダイスの目を必ず「2」に、その次に振るダイスの目を必ず「7」にする。", 0, 7, 1, 3, 3, "福引券を1枚獲得する。", 2, 10, 500, 0, "リマがネネカにもらったリンゴで作った秘密の\nスイーツ。爽やかな酸味と濃厚な甘さがまるで\n魔法のようなバランスでお互いを引きたてており\n食べた者をたちまち虜にしてしまうという。");
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (52012, "ニノンのオリジナル\n一二三寿司", "ニノンのオリジナル一二三寿司", 1, 51012, "次に振るダイスの目が「1・2・3」のみになる。さらに、次のターン数のカウントをスキップする。", 0, 3, 30002, 2, 1, "平凡な未鑑定トレジャーを1個獲得する。", 1, 10, 500, 0, "ニノンがジオ・テオゴニアに持ち込んだ特別な\n寿司。厳選された新鮮な魚介を使った逸品だが、\n一部のネタは、寿司のセオリーを逸脱したニノン\n我流のものに仕上がっている。");
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (52013, "クレジッタの開運\nお菓子盛り合わせ", "クレジッタの開運お菓子盛り合わせ", 3, 51013, "RANK2のマイルショップを開く。", 0, 8, 2, 3, 4, "800マイルを獲得する。", 0, 10, 500, 0, "クレジッタが考案した、ジオ・テオゴニアで流通\nしているチョコの貨幣の盛り合わせ。一見すると\n金貨を口に入れるおバカさんではあるが、通貨を\n食べるという背徳感が隠し味となっている。");
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (52014, "リノの\n「時は金時」かき氷", "リノの「時は金時」かき氷", 1, 51014, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 0, 9, 1, 2, 1, "次にダイスを振った際、1マス多く進める。", 2, 11, 500, 0, "可愛い妹がお兄ちゃんのために心を込めて作った\nスペシャルなかき氷。一口食べるとたちどころに\nクールな甘さが精神を研ぎ澄まし、いかなる\nお得をも見逃さない要塞賢母と化すという。");
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (52015, "シズルの愛情\n盛り盛りハンバーグ", "シズルの愛情盛り盛りハンバーグ", 1, 51015, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 0, 1, 2, 1, 1, "次にダイスを振った際、1マス多く進める。", 1, 11, 500, 0, "お姉ちゃんが弟くんのために心を込めて作った\nスペシャルなハンバーグ。アツアツの肉汁と共に\n溢れんばかりの愛が詰まっており、一口食べる\nだけで、走り回りたくなるような美味しさ。");
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (52016, "アオイの疾走\nサンドイッチ", "アオイの疾走サンドイッチ", 1, 51016, "次に振るダイスの目が必ず「6」になる。", 0, 2, 20005, 3, 0, "なし", 1, 0, 500, 0, "アオイがお弁当として作ったエルフ伝統の野菜\nサンド。一緒についてきてくれる最高の友だち\nの助言により、どんなに深い森の中でも全速力\nで駆け抜けることができるらしい。（アオイ談）");
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (52017, "クウカの緊縛\n触手活け造り", "クウカの緊縛触手活け造り", 1, 51017, "[FF7C4E,D62146]【上書き不可】[-]次のターン「移動系カテゴリーの料理」が食べられない代わりに、その場で2000マイルを獲得する。", 1, 2, 1, 3, 5, "なし", 2, 10, 500, 0, "数多くのドM妄想をしてきたクウカの中で最近の\nマイブームである触手をモチーフとした活造り。\n新鮮な蛸足が、濃厚なうま味と共に物理的に絡み\n付き身も心も縛りあげられる様な体験ができる。");
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (52018, "ランファの世話焼き三昧肉じゃが", "ランファの世話焼き三昧肉じゃが", 1, 51018, "次に振るダイスの目が必ず「3」になる。", 0, 2, 20006, 1, 6, "1000マイルを獲得する。", 1, 10, 500, 0, "コッコロの指導により料理の腕を上げたランファ\nが手作りした肉じゃが。まだ改善の余地はある\nものの、不器用なランファが誰かのために積み\n重ねた努力を思うととてもおいしく味わえる。");
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (52019, "ワカナの節約腹八分目カレー", "ワカナの節約腹八分目カレー", 1, 0, "次に振るダイスの目が必ず「8」になる。", 0, 2, 20007, 1, 0, "なし", 1, 0, 500, 0, "バンディ・シスターズの昨夜の夕食カレー。作り\n過ぎて食べきれずにいたものをワカナがお裾分け\nしてくれた。寝かされていたためしっかりと味の\n染みた、極上の二日目カレーとなっている。");
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (52020, "リノの\n「時は金時」かき氷", "リノの「時は金時」かき氷", 2, 51014, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 0, 9, 1, 2, 1, "次にダイスを振った際、1マス多く進める。", 2, 11, 500, 0, "可愛い妹がお兄ちゃんのために心を込めて作った\nスペシャルなかき氷。一口食べるとたちどころに\nクールな甘さが精神を研ぎ澄まし、いかなる\nお得をも見逃さない要塞賢母と化すという。");
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (52021, "カスミの真実見抜く\nボンボン・ショコラ", "カスミの真実見抜くボンボン・ショコラ", 2, 51021, "トレジャーマスをスキップして移動できる。効果は3ターンの間継続する。", 0, 9, 2, 3, 7, "	平凡な未鑑定トレジャーを2個獲得する。", 3, 10, 500, 0, "綺麗な装飾箱に納められたボンボン・ショコラ。\nそのほろ苦さを秘めた濃密な甘さが灰色の脳細胞\nに働きかける時、名探偵の鋭い推理が隠された\n謎を全て白日の下へと晒し、事件を解決へ導く。");
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (52022, "アキノのゲヘニアン\nチキンプレート", "アキノのゲヘニアンチキンプレート", 1, 51022, "ダイスを3回振るまでの間、「5と6」の⽬が出る確率が3倍になる。", 0, 2, 20008, 1, 0, "なし", 3, 0, 500, 0, "アキノがジオ・ゲヘナで仕入れてきたスパイスを\n使用した、お洒落で豪華なチキンプレート。牧場\n特製ヨーグルトソースと奏でる刺激的な味わいは\nまさに二つの世界の奇跡のマリアージュである。");
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (52023, "ミミのコロコロ\nお手玉おにぎり", "ミミのコロコロお手玉おにぎり", 1, 51023, "[FF7C4E,D62146]【上書き不可】[-]次に振るダイスの目を必ず「6」に、その次に振るダイスの目を必ず「3」にする。", 1, 7, 1, 1, 1, "平凡な未鑑定トレジャーを1個獲得する。", 2, 10, 500, 0, "ミミが、ジオ・ゲヘナで助けてくれたお姉さん達\nのことを思い作ったおにぎり。それぞれ異なる\n味付けがされているが、ミミの願い通りお互いに\nケンカをせず仲良くハーモニーを奏でている。");
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (52024, "アユミの隠密\nガトーインビジブル", "アユミの隠密ガトーインビジブル", 3, 51024, "RANK2のマイルショップを開く。", 0, 8, 2, 3, 3, "次にダイスを振った際、3マス多く進める。", 0, 11, 500, 0, "気が付くとリリの手元にあった不思議なお菓子。\n一見普通のケーキだが、内部に様々な具材が\n隠れるように入っている。複雑で重層的な\n味わいを見せる様は、まるで誰かさんのよう。");
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (52025, "アネモネの\n花爛漫ステーキサラダ", "アネモネの花爛漫ステーキサラダ", 2, 51025, "「マイルマス」「トレジャーマス」をスキップして移動できる。効果は1ターンの間継続する。", 0, 9, 3, 3, 0, "なし", 1, 0, 500, 0, "アネモネが世話する秘密の菜園の野菜を使った\nサラダ。栄養を重視するアネモネは草食獣の肉を\n野菜と解釈、当初は野菜の上に肉を置いただけ\nであったが、ネラの指導で盛り付け直された。");
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (52026, "アンナの\n煉獄蕃茄煮混沌卵", "アンナの煉獄蕃茄煮混沌卵", 3, 51026, "ランダムなイベントを発生させる。", 0, 4, 1, 1, 8, "福引券を2枚獲得する。", 0, 10, 500, 0, "ジオ・ゲヘナを探索中の冥姫たちが現地で採取\nしたスパイスで味付けした一皿。赤く煮え滾る\nソースは刺激的かつ非常に奥深い味わいで未知の\n大地の如く冒険者たちをその深奥へと誘う。");
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (52027, "ユイの\n栄養満点まごころ弁当", "ユイの栄養満点まごころ弁当", 1, 51027, "ダイスを2回振るまでの間、ダイスの目が必ず「5」になる。", 0, 2, 20004, 3, 0, "なし", 2, 0, 500, 0, "ユイが食べる人のことを考え、心を込めて作った\nお弁当。程よいサイズのお弁当箱に愛情と手間暇\nをたっぷりとかけた極上の料理が詰まっており、\n食べた者の心と胃袋を優しく満たしてくれる。");
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (52028, "リンの\nナッツ入りぐうたらあんパン", "リンのナッツ入りぐうたらあんパン", 1, 51028, "次に振るダイスの目が「1・2」のみになる\n代わりに、その場で福引券を3枚獲得する。", 0, 2, 30003, 3, 9, "なし", 1, 10, 500, 0, "リンからもらった非常食のあんパン。ナッツが\n練り込まれクランチな食感で食べ飽きない。\nゲームのため部屋に引きこもる際のお供であり、\n必要な栄養素が全て賄える完全栄養食、らしい。");
INSERT INTO `v1_2de336d78e5da0cfaaac8fba49ce82e210db463cb4cacf10311384b2e8c52a30` VALUES (52029, "シェフィの\n氷晶煌めく手作りアイス", "シェフィの氷晶煌めく手作りアイス", 1, 51029, "次に振るダイスの目が必ず「2」になる。さらに、次のターン数のカウントをスキップする。", 0, 3, 30004, 2, 0, "なし", 1, 0, 500, 0, "シェフィが自身の魔力の氷を使い作ったアイス。\nペコリーヌにねだられて作ったものだがキャル達\nにも好評である。コッコロ曰くひんやりしている\nのに食べると心が温かくなってきます、との事。");
