INSERT INTO `v1_2ea2fbd558f287f6d0d6d1e203f2c4a6c8ae75ae282f01bef2082bd007c47de8` VALUES (/*e2ea6ca2df6ca5fad61ed9ab9f7155aa7193a8760955face963dfd9853b8ff78*/307200, /*deb78dc40fc2c938a0308789e249dc690048140880730eef1aa92bb70a567551*/"バジリスク", /*7489fe449fadfca5f7c00a580dff1edad7378dfe631677c1c8ad5c7decf09097*/307200, /*f65bc453c735e13837d9d845d6f3d3ba35b94944b98325b380a630402a66df12*/0, /*e445d0d7c735188da12f8557093eb1b3da24f3f753e8228cc76d330609221a1a*/16, /*538cc696ee60936e32ae5f1bb41b19700481910b4bac2ac3de77c05f4e4f1a36*/270, /*d037b6ebee9d9ed3f8c62dbdef8a3f8594f4315306fe1fcb773c45f3dcaace97*/3000, /*1de54c59712133823b0e003b0e2ceec66380595b6c7aaf49bdff95d323e9b234*/2, /*6871bcc79c32f3f1163a01bb8ee7bb958c3ed85f4df80d69ddf47254e20623cb*/1.8, /*e5588ccd85bd235f1cb40f395303d70ad35eac150e751c308d5fe01fb0d4babf*/0, /*a28abb3506fac82150745aa33567be51f7e462609c341669e35a7002342b5b61*/0, /*cc1e8a5db0f07f1922f614ad3ca343c8fc92cfc06b36b4a047983eb397379541*/0, /*4546a910e3223aca0ad20dff3cdf5ab2bb5c028140baefaefad78a6d12737f23*/"【魔法】ルナの魔力を求め飛来した大怪鳥。\n雛鳥を従え、石化の呪剣を振り下ろす。\n\n・石化状態でないキャラ全員に魔法大ダメージ\n　を与え、短時間石化する。\n　石化状態のキャラ全員に最大ＨＰの80％の\n　ダメージを与える。\n・前から３キャラに魔法小ダメージを与える。\n　さらにこのスキルでダメージが発生した\n　毒状態のキャラを長時間石化する。\n・前方範囲内のキャラに魔法中ダメージを与え、\n　さらに毒状態にする。\n　自身のＨＰが50％以下の場合、さらに敵全体\n　をノックバックし、コッコーを召喚する。\n・コッコーは、前方範囲内で物理攻撃力が\n　一番高い１キャラに物理小ダメージを与え、\n　さらに毒状態にする。\n　また自身のＨＰが最大ＨＰの35％以下にな\n　ると、物理攻撃力が最も高い味方のＴＰを\n　中アップし、自身は戦闘不能になる。");
INSERT INTO `v1_2ea2fbd558f287f6d0d6d1e203f2c4a6c8ae75ae282f01bef2082bd007c47de8` VALUES (/*e2ea6ca2df6ca5fad61ed9ab9f7155aa7193a8760955face963dfd9853b8ff78*/307201, /*deb78dc40fc2c938a0308789e249dc690048140880730eef1aa92bb70a567551*/"バジリスク", /*7489fe449fadfca5f7c00a580dff1edad7378dfe631677c1c8ad5c7decf09097*/307201, /*f65bc453c735e13837d9d845d6f3d3ba35b94944b98325b380a630402a66df12*/0, /*e445d0d7c735188da12f8557093eb1b3da24f3f753e8228cc76d330609221a1a*/16, /*538cc696ee60936e32ae5f1bb41b19700481910b4bac2ac3de77c05f4e4f1a36*/270, /*d037b6ebee9d9ed3f8c62dbdef8a3f8594f4315306fe1fcb773c45f3dcaace97*/3000, /*1de54c59712133823b0e003b0e2ceec66380595b6c7aaf49bdff95d323e9b234*/2, /*6871bcc79c32f3f1163a01bb8ee7bb958c3ed85f4df80d69ddf47254e20623cb*/1.8, /*e5588ccd85bd235f1cb40f395303d70ad35eac150e751c308d5fe01fb0d4babf*/0, /*a28abb3506fac82150745aa33567be51f7e462609c341669e35a7002342b5b61*/0, /*cc1e8a5db0f07f1922f614ad3ca343c8fc92cfc06b36b4a047983eb397379541*/0, /*4546a910e3223aca0ad20dff3cdf5ab2bb5c028140baefaefad78a6d12737f23*/"【魔法】石化の呪剣を携えた怪鳥。尾蛇の毒牙で\n獲物を蝕み、呪剣の石化で冒険者の歩みを止める。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。\n　この防御力アップ効果で上昇した防御力は、バ\n　トル中に受ける防御力ダウンスキルの影響を受\n　けない。\n・バトル開始時、自身にＴＰダウン50%カット\n　効果を付与する。\n・ユニオンバーストは、行動不能状態でないキャ\n　ラ全員に魔法大ダメージを与え、石化状態にす\n　る。\n　行動不能状態のキャラ全員に最大ＨＰの80％\n　の魔法割合ダメージを与える。\n　さらに、敵全体のステータスアップ効果を解除。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・最も近い敵３キャラに魔法中ダメージを与える。\n　さらにこのスキルでダメージが発生した毒状態\n　のキャラを石化状態にする。\n　このスキルによるダメージでは、ＴＰ回復量は\n　減少する。\n・敵全体に魔法中ダメージを与え、さらに毒状態\n　にする。\n　このスキルによるダメージでは、ＴＰ回復量は\n　減少する。\n　このスキルにより付与された毒のダメージでは、\n　ＴＰが回復しない。\n・通常攻撃は最も近い敵１キャラに魔法大ダメー\n　ジを与える。\n　このスキルによるダメージでは、ＴＰ回復量は\n　減少する。");
