INSERT INTO `v1_2ea2fbd558f287f6d0d6d1e203f2c4a6c8ae75ae282f01bef2082bd007c47de8` VALUES (/*e2ea6ca2df6ca5fad61ed9ab9f7155aa7193a8760955face963dfd9853b8ff78*/310100, /*deb78dc40fc2c938a0308789e249dc690048140880730eef1aa92bb70a567551*/"ラースドラゴン", /*7489fe449fadfca5f7c00a580dff1edad7378dfe631677c1c8ad5c7decf09097*/310100, /*f65bc453c735e13837d9d845d6f3d3ba35b94944b98325b380a630402a66df12*/0, /*e445d0d7c735188da12f8557093eb1b3da24f3f753e8228cc76d330609221a1a*/14, /*538cc696ee60936e32ae5f1bb41b19700481910b4bac2ac3de77c05f4e4f1a36*/270, /*d037b6ebee9d9ed3f8c62dbdef8a3f8594f4315306fe1fcb773c45f3dcaace97*/1000, /*1de54c59712133823b0e003b0e2ceec66380595b6c7aaf49bdff95d323e9b234*/1, /*6871bcc79c32f3f1163a01bb8ee7bb958c3ed85f4df80d69ddf47254e20623cb*/2.6, /*e5588ccd85bd235f1cb40f395303d70ad35eac150e751c308d5fe01fb0d4babf*/0, /*a28abb3506fac82150745aa33567be51f7e462609c341669e35a7002342b5b61*/0, /*cc1e8a5db0f07f1922f614ad3ca343c8fc92cfc06b36b4a047983eb397379541*/0, /*4546a910e3223aca0ad20dff3cdf5ab2bb5c028140baefaefad78a6d12737f23*/"【物理】忘却の秘境に、住まう若き巨竜。\n禁忌に踏み入る冒険者を灼熱の業火で焼き払う。\n\n・ユニオンバーストは敵全体に物理割合ダメージ\n　を与え、さらにＴＰをダウンさせる。\n・バトル開始時に、自身のＴＰを特大ダウン。\n・前方1キャラを中心に一定範囲内のキャラに\n　魔法ダメージを与え、さらに物理防御力を\n　ダウンさせる。\n・敵全体に物理ダメージを与え、さらに火傷\n　状態にする。\n・敵全体に物理ダメージを与え、ノックバック\n　させる、さらに、ステータスアップ効果を\n　打ち消す。\n\n・全ターゲットがブレイクされると、自身のＴＰ\n　を特大ダウン。\n・ラースドラゴン自身の残りＨＰが10％を切る\n　とさらに攻撃が激しく強暴になり、ブレイク\n　されなくなる。\n・バトルがタイムアップすると、敵全体を戦闘\n　不能にする。\n");
INSERT INTO `v1_2ea2fbd558f287f6d0d6d1e203f2c4a6c8ae75ae282f01bef2082bd007c47de8` VALUES (/*e2ea6ca2df6ca5fad61ed9ab9f7155aa7193a8760955face963dfd9853b8ff78*/310101, /*deb78dc40fc2c938a0308789e249dc690048140880730eef1aa92bb70a567551*/"ラースドラゴン", /*7489fe449fadfca5f7c00a580dff1edad7378dfe631677c1c8ad5c7decf09097*/310101, /*f65bc453c735e13837d9d845d6f3d3ba35b94944b98325b380a630402a66df12*/0, /*e445d0d7c735188da12f8557093eb1b3da24f3f753e8228cc76d330609221a1a*/14, /*538cc696ee60936e32ae5f1bb41b19700481910b4bac2ac3de77c05f4e4f1a36*/270, /*d037b6ebee9d9ed3f8c62dbdef8a3f8594f4315306fe1fcb773c45f3dcaace97*/1000, /*1de54c59712133823b0e003b0e2ceec66380595b6c7aaf49bdff95d323e9b234*/1, /*6871bcc79c32f3f1163a01bb8ee7bb958c3ed85f4df80d69ddf47254e20623cb*/2.6, /*e5588ccd85bd235f1cb40f395303d70ad35eac150e751c308d5fe01fb0d4babf*/0, /*a28abb3506fac82150745aa33567be51f7e462609c341669e35a7002342b5b61*/0, /*cc1e8a5db0f07f1922f614ad3ca343c8fc92cfc06b36b4a047983eb397379541*/0, /*4546a910e3223aca0ad20dff3cdf5ab2bb5c028140baefaefad78a6d12737f23*/"【物理】忘却の秘境に、住まう若き巨竜。\n禁忌に踏み入る冒険者を灼熱の業火で焼き払う。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・ラースドラゴンから受けるすべてのダメージで\n　発生する、パーティキャラのＴＰ回復量を中ダ\n　ウンさせる。\n・ユニオンバーストは、敵全体のバリアを解除\n　し、敵全体にキャラの最大ＨＰに応じた物理割\n　合ダメージを与え、さらにＴＰを小ダウンさせ\n　る。\n・最も近い敵を中心にした範囲内の敵すべてに\n　キャラの最大ＨＰに応じた物理割合ダメージを\n　与え、さらに物理防御力を大ダウンさせる。\n　このスキルの効果はダメージを無効化しても付\n　与される。\n・敵全体に物理中ダメージを与え、さらに火傷状\n　態にする。\n　このスキルにより付与された火傷のダメージで\n　は、ＴＰが回復しない。\n・敵全体のバリアを解除し、敵全体に物理中ダ\n　メージを与え、ノックバックさせる、さらに、\n　ステータスアップ効果を打ち消す。\n　このスキルの状態異常と効果はダメージを無効\n　化しても付与される。\n・向かって左側のターゲットのブレイク時、自身\n　の物理防御力と魔法防御力が大ダウン。\n・中央のターゲットのブレイク時、自身の物理防\n　御力と魔法防御力が大ダウン。\n・向かって右側のターゲットのブレイク時、自身\n　の物理防御力と魔法防御力が大ダウン。\n・全ターゲットがブレイクされると、自身のＴＰ\n　を特大ダウン。\n・ラースドラゴン自身の残りＨＰが30％を切る\n　とさらに攻撃が激しく強暴になり、ブレイク\n　されなくなるが、自身の物理防御力と魔法防御\n　力が大ダウンする。\n・最も近い敵１キャラに物理大ダメージを与える。");
