INSERT INTO `v1_2ea2fbd558f287f6d0d6d1e203f2c4a6c8ae75ae282f01bef2082bd007c47de8` VALUES (/*e2ea6ca2df6ca5fad61ed9ab9f7155aa7193a8760955face963dfd9853b8ff78*/314104, /*deb78dc40fc2c938a0308789e249dc690048140880730eef1aa92bb70a567551*/"クイーンウタクラゲ", /*7489fe449fadfca5f7c00a580dff1edad7378dfe631677c1c8ad5c7decf09097*/314104, /*f65bc453c735e13837d9d845d6f3d3ba35b94944b98325b380a630402a66df12*/0, /*e445d0d7c735188da12f8557093eb1b3da24f3f753e8228cc76d330609221a1a*/16, /*538cc696ee60936e32ae5f1bb41b19700481910b4bac2ac3de77c05f4e4f1a36*/270, /*d037b6ebee9d9ed3f8c62dbdef8a3f8594f4315306fe1fcb773c45f3dcaace97*/3000, /*1de54c59712133823b0e003b0e2ceec66380595b6c7aaf49bdff95d323e9b234*/2, /*6871bcc79c32f3f1163a01bb8ee7bb958c3ed85f4df80d69ddf47254e20623cb*/1.6, /*e5588ccd85bd235f1cb40f395303d70ad35eac150e751c308d5fe01fb0d4babf*/0, /*a28abb3506fac82150745aa33567be51f7e462609c341669e35a7002342b5b61*/0, /*cc1e8a5db0f07f1922f614ad3ca343c8fc92cfc06b36b4a047983eb397379541*/0, /*4546a910e3223aca0ad20dff3cdf5ab2bb5c028140baefaefad78a6d12737f23*/"【ＭＯＤＥ１】襲い来る無数の魔物たち。その歌\n声は轟然とこだまし舞台を脅かす。ファンの笑顔\nを守るため、少女たちは歌い踊り、そして戦う。\n\n・バトル開始時に、ウタクラゲを３体召喚し\n　敵全体をノックバックさせる。\n・ウタクラゲを１体倒すたびに、残りウタクラゲ\n　数が１減少する。\n　その後、ウタクラゲが再度召喚され、敵全体を\n　ノックバックさせる。\n・ウタクラゲは、残りＨＰが最も高い敵１キャラ\n　に魔法中ダメージを与え、毒状態を付与する。\n・ユニオンバーストは、敵全体にキャラのＨＰに\n　応じた魔法割合ダメージを与える。\n　猛毒状態の敵にはダメージがアップする。\n　さらに敵に毒状態を付与し、既に毒か猛毒状態\n　の敵には猛毒状態を付与する。\n・目の前の敵１キャラに魔法大ダメージを与える。\n・クイーンウタクラゲとウタクラゲの付与する毒\n　および猛毒のダメージでは、キャラのＴＰが回\n　復しない。");
