INSERT INTO `v1_2ea2fbd558f287f6d0d6d1e203f2c4a6c8ae75ae282f01bef2082bd007c47de8` VALUES (/*e2ea6ca2df6ca5fad61ed9ab9f7155aa7193a8760955face963dfd9853b8ff78*/314901, /*deb78dc40fc2c938a0308789e249dc690048140880730eef1aa92bb70a567551*/"ゴブリンロード＆ゴブミニオン", /*7489fe449fadfca5f7c00a580dff1edad7378dfe631677c1c8ad5c7decf09097*/314901, /*f65bc453c735e13837d9d845d6f3d3ba35b94944b98325b380a630402a66df12*/0, /*e445d0d7c735188da12f8557093eb1b3da24f3f753e8228cc76d330609221a1a*/16, /*538cc696ee60936e32ae5f1bb41b19700481910b4bac2ac3de77c05f4e4f1a36*/270, /*d037b6ebee9d9ed3f8c62dbdef8a3f8594f4315306fe1fcb773c45f3dcaace97*/3000, /*1de54c59712133823b0e003b0e2ceec66380595b6c7aaf49bdff95d323e9b234*/1, /*6871bcc79c32f3f1163a01bb8ee7bb958c3ed85f4df80d69ddf47254e20623cb*/1.8, /*e5588ccd85bd235f1cb40f395303d70ad35eac150e751c308d5fe01fb0d4babf*/0, /*a28abb3506fac82150745aa33567be51f7e462609c341669e35a7002342b5b61*/0, /*cc1e8a5db0f07f1922f614ad3ca343c8fc92cfc06b36b4a047983eb397379541*/0, /*4546a910e3223aca0ad20dff3cdf5ab2bb5c028140baefaefad78a6d12737f23*/"【ＭＯＤＥ１】砂瀑一帯を根城にするゴブリン族\nの首魁。居城までたどり着いた手練れの冒険者を\n子分を引き連れ、一斉攻撃で迎え撃つ。\n\n▼ＭＯＤＥ１突破条件\nダメージを与え、ゴブリンロード＆ゴブミニオン\nのＨＰを8880万以下にすること。\n\n・バトル開始時、自身の物理防御力と魔法防御\n　力を小アップさせる。この防御力アップ効果\n　で上昇した防御力は、バトル中に受ける防御\n　力ダウンスキルの影響を受けない。\n・バトル開始時、ダメージ上限スキルを使用し\n　て、一度の行動で与えるダメージの値が一定\n　を超えた場合、与えるダメージの値に応じて\n　ダメージ量を減少させる。\n・ゴブリンロード＆ゴブミニオンから受けるす\n　べてのダメージで発生する、パーティキャラ\n　のＴＰ回復量を中ダウンさせる。\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、大き\n　く吹き飛ばす。このスキルの状態異常は\n　ダメージを無効化しても付与される。\n・敵全体にキャラの最大ＨＰに応じた物理割合\n　ダメージを与える。\n・装甲車に乗っているゴブミニオンのターゲッ\n　トは、ブレイクすると消滅し、ゴブリンロー\n　ド＆ゴブミニオンの物理防御力と魔法防御力\n　が小ダウンする。\n・装甲車のコアのターゲットは、ブレイクする\n　と、ゴブリンロード＆ゴブミニオンの物理防\n　御力と魔法防御力が小ダウンする。\n・ゴブリンロード＆ゴブミニオンのターゲット\n　が全てブレイクした時、物理防御力と魔法防\n　御力が特大ダウンし、攻撃行動を変更する。\n・行動変更後、コアにエネルギーを溜め始めて\n　一定時間経過すると、自身にＴＰ継続回復状\n　態を付与する。エネルギーを溜めている間に\n　一定ダメージを与えると、スキルが中断され、\n　自身を火傷状態にする。\n・バトルがタイムアップすると、敵全体を戦闘\n　不能にする。");
INSERT INTO `v1_2ea2fbd558f287f6d0d6d1e203f2c4a6c8ae75ae282f01bef2082bd007c47de8` VALUES (/*e2ea6ca2df6ca5fad61ed9ab9f7155aa7193a8760955face963dfd9853b8ff78*/315001, /*deb78dc40fc2c938a0308789e249dc690048140880730eef1aa92bb70a567551*/"ゴブリンロード", /*7489fe449fadfca5f7c00a580dff1edad7378dfe631677c1c8ad5c7decf09097*/315001, /*f65bc453c735e13837d9d845d6f3d3ba35b94944b98325b380a630402a66df12*/0, /*e445d0d7c735188da12f8557093eb1b3da24f3f753e8228cc76d330609221a1a*/16, /*538cc696ee60936e32ae5f1bb41b19700481910b4bac2ac3de77c05f4e4f1a36*/270, /*d037b6ebee9d9ed3f8c62dbdef8a3f8594f4315306fe1fcb773c45f3dcaace97*/3000, /*1de54c59712133823b0e003b0e2ceec66380595b6c7aaf49bdff95d323e9b234*/1, /*6871bcc79c32f3f1163a01bb8ee7bb958c3ed85f4df80d69ddf47254e20623cb*/1.8, /*e5588ccd85bd235f1cb40f395303d70ad35eac150e751c308d5fe01fb0d4babf*/0, /*a28abb3506fac82150745aa33567be51f7e462609c341669e35a7002342b5b61*/0, /*cc1e8a5db0f07f1922f614ad3ca343c8fc92cfc06b36b4a047983eb397379541*/0, /*4546a910e3223aca0ad20dff3cdf5ab2bb5c028140baefaefad78a6d12737f23*/"【ＭＯＤＥ２】装甲車を木端に破壊され怒る首魁。\nゴブリン族のつわものを配し、冒険者の次なる壁\nとし、容易く突破を許さない。\n\n▼ＭＯＤＥ２突破条件\nダメージを与え、ゴブリングレートとゴブリン\nメイスターのＨＰを０にすること。\n\n・バトル開始時、自身を無敵状態にして攻撃を\n　受け付けなくする。\n・ゴブリンロードから受けるすべてのダメージ\n　で発生する、パーティキャラのＴＰ回復量を\n　中ダウンさせる。\n・ユニオンバーストは、敵全体にキャラの残り\n　ＨＰに応じた物理割合ダメージを与える。\n・バトルがタイムアップすると、敵全体を戦闘\n　不能にする。");
INSERT INTO `v1_2ea2fbd558f287f6d0d6d1e203f2c4a6c8ae75ae282f01bef2082bd007c47de8` VALUES (/*e2ea6ca2df6ca5fad61ed9ab9f7155aa7193a8760955face963dfd9853b8ff78*/315002, /*deb78dc40fc2c938a0308789e249dc690048140880730eef1aa92bb70a567551*/"ゴブリンロード", /*7489fe449fadfca5f7c00a580dff1edad7378dfe631677c1c8ad5c7decf09097*/315002, /*f65bc453c735e13837d9d845d6f3d3ba35b94944b98325b380a630402a66df12*/0, /*e445d0d7c735188da12f8557093eb1b3da24f3f753e8228cc76d330609221a1a*/16, /*538cc696ee60936e32ae5f1bb41b19700481910b4bac2ac3de77c05f4e4f1a36*/270, /*d037b6ebee9d9ed3f8c62dbdef8a3f8594f4315306fe1fcb773c45f3dcaace97*/3000, /*1de54c59712133823b0e003b0e2ceec66380595b6c7aaf49bdff95d323e9b234*/1, /*6871bcc79c32f3f1163a01bb8ee7bb958c3ed85f4df80d69ddf47254e20623cb*/1.8, /*e5588ccd85bd235f1cb40f395303d70ad35eac150e751c308d5fe01fb0d4babf*/0, /*a28abb3506fac82150745aa33567be51f7e462609c341669e35a7002342b5b61*/0, /*cc1e8a5db0f07f1922f614ad3ca343c8fc92cfc06b36b4a047983eb397379541*/0, /*4546a910e3223aca0ad20dff3cdf5ab2bb5c028140baefaefad78a6d12737f23*/"【ＭＯＤＥ３】砂瀑に君臨するゴブリン族の首魁。\n配下を屠られた怒りを武器にのせ、圧倒的な力で\n眼前の全てを塵に変える。\n\n▼ＭＯＤＥ３突破条件\nダメージを与え、ゴブリンロードのＨＰを０にす\nること。\n\n・バトル開始時、自身の物理防御力と魔法防御\n　力を小アップさせる。この防御力アップ効果\n　で上昇した防御力は、バトル中に受ける防御\n　力ダウンスキルの影響を受けない。\n・バトル開始時、ダメージ上限スキルを使用し\n　て、一度の行動で与えるダメージの値が一定\n　を超えた場合、与えるダメージの値に応じて\n　ダメージ量を減少させる。\n・ゴブリンロードから受けるすべてのダメージ\n　で発生する、パーティキャラのＴＰ回復量を\n　中ダウンさせる。\n・ユニオンバーストは、敵全体にキャラの残り\n　ＨＰに応じた物理割合ダメージを与え、自身\n　の物理攻撃力を大アップさせる。自身の物理\n　攻撃力に応じて与えるダメージの割合が増加\n　する。\n・敵全体に物理大ダメージを与え、スタンさせ\n　る。このスキルの状態異常はダメージを無効\n　化しても付与される。\n・前方範囲内の敵に物理大ダメージを与え、大\n　きく吹き飛ばす。このスキルの状態異常は\n　ダメージを無効化しても付与される。\n・前方１キャラにキャラの最大ＨＰに応じた\n　物理割合ダメージを与える。\n・残りＨＰが1872万を下回ると、物理攻撃力\n　と行動速度が特大アップし、さらに攻撃が\n　激しく凶暴になる。\n・バトルがタイムアップすると、敵全体を戦闘\n　不能にする。");
