INSERT INTO `v1_2ea2fbd558f287f6d0d6d1e203f2c4a6c8ae75ae282f01bef2082bd007c47de8` VALUES (/*e2ea6ca2df6ca5fad61ed9ab9f7155aa7193a8760955face963dfd9853b8ff78*/315300, /*deb78dc40fc2c938a0308789e249dc690048140880730eef1aa92bb70a567551*/"魂喰らいの凶蛇", /*7489fe449fadfca5f7c00a580dff1edad7378dfe631677c1c8ad5c7decf09097*/315300, /*f65bc453c735e13837d9d845d6f3d3ba35b94944b98325b380a630402a66df12*/0, /*e445d0d7c735188da12f8557093eb1b3da24f3f753e8228cc76d330609221a1a*/16, /*538cc696ee60936e32ae5f1bb41b19700481910b4bac2ac3de77c05f4e4f1a36*/270, /*d037b6ebee9d9ed3f8c62dbdef8a3f8594f4315306fe1fcb773c45f3dcaace97*/3000, /*1de54c59712133823b0e003b0e2ceec66380595b6c7aaf49bdff95d323e9b234*/1, /*6871bcc79c32f3f1163a01bb8ee7bb958c3ed85f4df80d69ddf47254e20623cb*/1.6, /*e5588ccd85bd235f1cb40f395303d70ad35eac150e751c308d5fe01fb0d4babf*/0, /*a28abb3506fac82150745aa33567be51f7e462609c341669e35a7002342b5b61*/0, /*cc1e8a5db0f07f1922f614ad3ca343c8fc92cfc06b36b4a047983eb397379541*/0, /*4546a910e3223aca0ad20dff3cdf5ab2bb5c028140baefaefad78a6d12737f23*/"【物理】塔の魔力により顕現した、凶々しき霊蛇。\n怨嗟に満ちたその咢で、冒険者の魂を貪り食う。\n\n・魂喰らいの凶蛇が保持している【吸魂】の数に\n　応じて、通常攻撃と各スキルの効果が強化され\n　る。\n・通常攻撃のたびに【吸魂】を６つ追加する。\n・ユニオンバーストは、敵全体に魂喰らいの凶蛇\n　が保持している【吸魂】の数に応じた魔法割合\n　ダメージを与え、さらに【吸魂】を５つ追加す\n　る。\n・敵全体に魔法小ダメージを与えＴＰを減少させ、\n　さらにＴＰ上昇をダウンさせるフィールドを展\n　開する。ＴＰの減少量とフィールドの効果値は、\n　魂喰らいの凶蛇が保持している【吸魂】の数が\n　多い程強化される。\n・攻撃するたびに魂喰らいの凶蛇に【吸魂】を１\n　つ追加する分身体を２体召喚し、さらに敵全体\n　を小さく吹き飛ばす。\n・バトル開始時に、自身の物理攻撃力と魔法攻撃\n　力を中アップさせるが、ダメージを受けるたび\n　に、魂喰らいの凶蛇の【吸魂】が１つ減少する\n　状態にする。\n・残りＨＰが30％を切ると、魔法攻撃力が大\n　アップし、さらに【吸魂】を20追加する。");
