INSERT INTO `v1_2ea2fbd558f287f6d0d6d1e203f2c4a6c8ae75ae282f01bef2082bd007c47de8` VALUES (/*e2ea6ca2df6ca5fad61ed9ab9f7155aa7193a8760955face963dfd9853b8ff78*/316006, /*deb78dc40fc2c938a0308789e249dc690048140880730eef1aa92bb70a567551*/"解放されし財宝の守護者", /*7489fe449fadfca5f7c00a580dff1edad7378dfe631677c1c8ad5c7decf09097*/316004, /*f65bc453c735e13837d9d845d6f3d3ba35b94944b98325b380a630402a66df12*/0, /*e445d0d7c735188da12f8557093eb1b3da24f3f753e8228cc76d330609221a1a*/16, /*538cc696ee60936e32ae5f1bb41b19700481910b4bac2ac3de77c05f4e4f1a36*/270, /*d037b6ebee9d9ed3f8c62dbdef8a3f8594f4315306fe1fcb773c45f3dcaace97*/3000, /*1de54c59712133823b0e003b0e2ceec66380595b6c7aaf49bdff95d323e9b234*/1, /*6871bcc79c32f3f1163a01bb8ee7bb958c3ed85f4df80d69ddf47254e20623cb*/1.6, /*e5588ccd85bd235f1cb40f395303d70ad35eac150e751c308d5fe01fb0d4babf*/0, /*a28abb3506fac82150745aa33567be51f7e462609c341669e35a7002342b5b61*/0, /*cc1e8a5db0f07f1922f614ad3ca343c8fc92cfc06b36b4a047983eb397379541*/0, /*4546a910e3223aca0ad20dff3cdf5ab2bb5c028140baefaefad78a6d12737f23*/"【ＭＯＤＥ３】財宝を護り続けた躯体が最後の力\nを解き放つ。リミッターを失ったマリオネットを\n必殺の一撃で打ち倒せ！\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・財宝の守護者から受けるすべてのダメージで発\n　生する、パーティキャラのＴＰ回復量を中ダウ\n　ンさせる。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた物理割合ダメージを与え、自身の物\n　理攻撃力を大アップさせる。\n・前方１キャラを中心にした小範囲内の敵すべて\n　に物理大ダメージを与え、物理防御力を大ダウ\n　ンさせる。\n・自身の物理防御力と魔法防御力を極大アップさ\n　せる。財宝の守護者が毒、猛毒、呪い、火傷、\n　呪詛の効果を受けていると、自身の物理防御力\n　と魔法防御力がアップする時間が短くなり、極\n　大アップではなく小アップさせる。\n・財宝の守護者自身のＨＰが30％を切ると、自\n　身の物理攻撃力を大アップさせ、さらに攻撃が\n　激しく凶暴になる。その後、敵全体にキャラの\n　最大ＨＰに応じた物理割合ダメージを与え、一\n　定時間、ユニオンバーストのみ使用できない状\n　態にする。このスキルの状態異常はダメージを\n　無効化しても付与される。\n・前から１番目のキャラに物理大ダメージ。");
