INSERT INTO `v1_2ea2fbd558f287f6d0d6d1e203f2c4a6c8ae75ae282f01bef2082bd007c47de8` VALUES (/*e2ea6ca2df6ca5fad61ed9ab9f7155aa7193a8760955face963dfd9853b8ff78*/316505, /*deb78dc40fc2c938a0308789e249dc690048140880730eef1aa92bb70a567551*/"哀執のバロメッツ", /*7489fe449fadfca5f7c00a580dff1edad7378dfe631677c1c8ad5c7decf09097*/316505, /*f65bc453c735e13837d9d845d6f3d3ba35b94944b98325b380a630402a66df12*/0, /*e445d0d7c735188da12f8557093eb1b3da24f3f753e8228cc76d330609221a1a*/16, /*538cc696ee60936e32ae5f1bb41b19700481910b4bac2ac3de77c05f4e4f1a36*/270, /*d037b6ebee9d9ed3f8c62dbdef8a3f8594f4315306fe1fcb773c45f3dcaace97*/3000, /*1de54c59712133823b0e003b0e2ceec66380595b6c7aaf49bdff95d323e9b234*/1, /*6871bcc79c32f3f1163a01bb8ee7bb958c3ed85f4df80d69ddf47254e20623cb*/1.6, /*e5588ccd85bd235f1cb40f395303d70ad35eac150e751c308d5fe01fb0d4babf*/0, /*a28abb3506fac82150745aa33567be51f7e462609c341669e35a7002342b5b61*/0, /*cc1e8a5db0f07f1922f614ad3ca343c8fc92cfc06b36b4a047983eb397379541*/0, /*4546a910e3223aca0ad20dff3cdf5ab2bb5c028140baefaefad78a6d12737f23*/"【ＭＯＤＥ２】哀しき羊は被毛の鎧を硬化させ\nあらゆるものを拒絶する。刃も拳も魔法も\n差し伸べられた手でさえも。\n\n・バトル開始時、哀執のバロメッツの物理防御力\n　を中アップさせ、魔法防御力を小アップさせる。\n　この防御力アップ効果で上昇した防御力は、バ\n　トル中に受ける防御力ダウンスキルの影響を受\n　けない。\n・哀執のバロメッツから受けるすべてのダメージ\n　で発生する、パーティキャラのＴＰ回復量を中\n　ダウンさせる。\n・ユニオンバーストは、敵全体のバリアを解除し\n　物理割合ダメージを与える。哀執のバロメッツ\n　が毒、猛毒、呪い、火傷、呪詛の効果を受けて\n　いると、与えるダメージがダウンする。\n・残りＨＰ割合が少ない敵３キャラに物理大\n　ダメージを与える。哀執のバロメッツが毒、猛\n　毒、呪い、火傷、呪詛の効果を受けている\n　と、与えるダメージがダウンする。\n・哀執のバロメッツが毒、猛毒、呪い、火傷、呪\n　詛の効果を受けると、哀執のバロメッツの\n　物理・魔法防御力が特大ダウンする。");
INSERT INTO `v1_2ea2fbd558f287f6d0d6d1e203f2c4a6c8ae75ae282f01bef2082bd007c47de8` VALUES (/*e2ea6ca2df6ca5fad61ed9ab9f7155aa7193a8760955face963dfd9853b8ff78*/316506, /*deb78dc40fc2c938a0308789e249dc690048140880730eef1aa92bb70a567551*/"哀執のバロメッツ", /*7489fe449fadfca5f7c00a580dff1edad7378dfe631677c1c8ad5c7decf09097*/316506, /*f65bc453c735e13837d9d845d6f3d3ba35b94944b98325b380a630402a66df12*/0, /*e445d0d7c735188da12f8557093eb1b3da24f3f753e8228cc76d330609221a1a*/16, /*538cc696ee60936e32ae5f1bb41b19700481910b4bac2ac3de77c05f4e4f1a36*/270, /*d037b6ebee9d9ed3f8c62dbdef8a3f8594f4315306fe1fcb773c45f3dcaace97*/3000, /*1de54c59712133823b0e003b0e2ceec66380595b6c7aaf49bdff95d323e9b234*/1, /*6871bcc79c32f3f1163a01bb8ee7bb958c3ed85f4df80d69ddf47254e20623cb*/1.6, /*e5588ccd85bd235f1cb40f395303d70ad35eac150e751c308d5fe01fb0d4babf*/0, /*a28abb3506fac82150745aa33567be51f7e462609c341669e35a7002342b5b61*/0, /*cc1e8a5db0f07f1922f614ad3ca343c8fc92cfc06b36b4a047983eb397379541*/0, /*4546a910e3223aca0ad20dff3cdf5ab2bb5c028140baefaefad78a6d12737f23*/"【ＭＯＤＥ３】老旅人と結んだ約束と臆病な心の\n間で揺れる葛藤は、憂いの棘となり狂気を生む。\nその暴威に立ち向かい、かの獣に想いを届けろ。\n\n・バトル開始時、哀執のバロメッツの物理防御力\n　と魔法防御力を小アップさせる。この防御力\n　アップ効果で上昇した防御力は、バトル中に受\n　ける防御力ダウンスキルの影響を受けない。\n・哀執のバロメッツから受けるすべてのダメージ\n　で発生する、パーティキャラのＴＰ回復量を中\n　ダウンさせる。\n・哀執のバロメッツに毎秒【憂いの棘】を４つ追\n　加する。\n・哀執のバロメッツがダメージを受けるたびに哀\n　執のバロメッツの【憂いの棘】を１つ減少させ\n　る。\n・ユニオンバーストは、敵全体のバリアを解除し\n　敵全体に哀執のバロメッツの【憂いの棘】の数\n　に応じた物理割合ダメージを与える。\n・残りＨＰ割合が少ない敵３キャラに哀執のバロ\n　メッツの【憂いの棘】の数に応じた物理割合ダ\n　メージを与える。\n・前方３キャラに哀執のバロメッツの【憂いの\n　棘】の数に応じた物理割合ダメージを与える。\n・残り体力が25％を切ると哀執のバロメッツ\n　に【憂いの棘】を20個付与する。");
INSERT INTO `v1_2ea2fbd558f287f6d0d6d1e203f2c4a6c8ae75ae282f01bef2082bd007c47de8` VALUES (/*e2ea6ca2df6ca5fad61ed9ab9f7155aa7193a8760955face963dfd9853b8ff78*/316600, /*deb78dc40fc2c938a0308789e249dc690048140880730eef1aa92bb70a567551*/"ゴブリンライダー", /*7489fe449fadfca5f7c00a580dff1edad7378dfe631677c1c8ad5c7decf09097*/316600, /*f65bc453c735e13837d9d845d6f3d3ba35b94944b98325b380a630402a66df12*/0, /*e445d0d7c735188da12f8557093eb1b3da24f3f753e8228cc76d330609221a1a*/6, /*538cc696ee60936e32ae5f1bb41b19700481910b4bac2ac3de77c05f4e4f1a36*/270, /*d037b6ebee9d9ed3f8c62dbdef8a3f8594f4315306fe1fcb773c45f3dcaace97*/1000, /*1de54c59712133823b0e003b0e2ceec66380595b6c7aaf49bdff95d323e9b234*/1, /*6871bcc79c32f3f1163a01bb8ee7bb958c3ed85f4df80d69ddf47254e20623cb*/1.5, /*e5588ccd85bd235f1cb40f395303d70ad35eac150e751c308d5fe01fb0d4babf*/0, /*a28abb3506fac82150745aa33567be51f7e462609c341669e35a7002342b5b61*/0, /*cc1e8a5db0f07f1922f614ad3ca343c8fc92cfc06b36b4a047983eb397379541*/0, /*4546a910e3223aca0ad20dff3cdf5ab2bb5c028140baefaefad78a6d12737f23*/"【物理】人竜一体となって戦うゴブリン騎手。\n敏速に空を駆け巡り、地上の敵に襲いかかる。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・ゴブリンライダーから受けるすべてのダメージ\n　で発生する、パーティキャラのＴＰ回復量を特\n　大ダウンさせる。\n・ユニオンバーストは、召喚体、分身を除いた最\n　も遠い敵３キャラに物理大ダメージを与え、ス\n　タン状態を付与する。このスキルの状態異常は\n　ダメージを無効化しても付与される。\n・召喚体、分身を除いた最も遠い敵２キャラに物\n　理小ダメージを与え、物理防御力を中ダウンさ\n　せる。このスキルの状態異常はダメージを無効\n　化しても付与される。\n・通常攻撃は、挑発を無視して最も遠い敵１キャ\n　ラに物理中ダメージを与える。このスキルによ\n　るダメージでは、ＴＰが回復しない。");
INSERT INTO `v1_2ea2fbd558f287f6d0d6d1e203f2c4a6c8ae75ae282f01bef2082bd007c47de8` VALUES (/*e2ea6ca2df6ca5fad61ed9ab9f7155aa7193a8760955face963dfd9853b8ff78*/316601, /*deb78dc40fc2c938a0308789e249dc690048140880730eef1aa92bb70a567551*/"ゴブリンライダー", /*7489fe449fadfca5f7c00a580dff1edad7378dfe631677c1c8ad5c7decf09097*/316601, /*f65bc453c735e13837d9d845d6f3d3ba35b94944b98325b380a630402a66df12*/0, /*e445d0d7c735188da12f8557093eb1b3da24f3f753e8228cc76d330609221a1a*/6, /*538cc696ee60936e32ae5f1bb41b19700481910b4bac2ac3de77c05f4e4f1a36*/270, /*d037b6ebee9d9ed3f8c62dbdef8a3f8594f4315306fe1fcb773c45f3dcaace97*/1000, /*1de54c59712133823b0e003b0e2ceec66380595b6c7aaf49bdff95d323e9b234*/1, /*6871bcc79c32f3f1163a01bb8ee7bb958c3ed85f4df80d69ddf47254e20623cb*/1.5, /*e5588ccd85bd235f1cb40f395303d70ad35eac150e751c308d5fe01fb0d4babf*/0, /*a28abb3506fac82150745aa33567be51f7e462609c341669e35a7002342b5b61*/0, /*cc1e8a5db0f07f1922f614ad3ca343c8fc92cfc06b36b4a047983eb397379541*/0, /*4546a910e3223aca0ad20dff3cdf5ab2bb5c028140baefaefad78a6d12737f23*/"【物理】人竜一体となって戦うゴブリン騎手。\n敏速に空を駆け巡り、地上の敵に襲いかかる。\n\n・バトル開始時、自身の物理防御力を中アップさ\n　せ、魔法防御力を小アップさせる。この防御力\n　アップ効果で上昇した防御力は、バトル中に受\n　ける防御力ダウンスキルの影響を受けない。\n・バトル開始時、敵全体のＴＰ上昇を小ダウンさ\n　せるフィールドを展開する。\n・バトル開始時、自身にＴＰダウン50％カット\n　効果を付与する。\n・ゴブリンライダーから受けるすべてのダメージ\n　で発生する、パーティキャラのＴＰ回復量を特\n　大ダウンさせる。\n・敵から受けるすべてのダメージで発生する、ゴ\n　ブリンライダーのＴＰ回復量が大ダウンする。\n・ユニオンバーストは、召喚体、分身を除いた最\n　も遠い敵３キャラに物理大ダメージを与え、ス\n　タン状態を付与する。このスキルの状態異常は\n　ダメージを無効化しても付与される。\n・召喚体、分身を除いた最も遠い敵２キャラに物\n　理小ダメージを与え、物理防御力を大ダウンさ\n　せる。このスキルの状態異常はダメージを無効\n　化しても付与される。\n・通常攻撃は、挑発を無視して最も遠い敵１キャ\n　ラに物理中ダメージを与える。このスキルによ\n　るダメージでは、ＴＰが回復しない。");
