INSERT INTO `v1_2ea2fbd558f287f6d0d6d1e203f2c4a6c8ae75ae282f01bef2082bd007c47de8` VALUES (/*e2ea6ca2df6ca5fad61ed9ab9f7155aa7193a8760955face963dfd9853b8ff78*/316800, /*deb78dc40fc2c938a0308789e249dc690048140880730eef1aa92bb70a567551*/"黒の王ジャバウォック", /*7489fe449fadfca5f7c00a580dff1edad7378dfe631677c1c8ad5c7decf09097*/316800, /*f65bc453c735e13837d9d845d6f3d3ba35b94944b98325b380a630402a66df12*/0, /*e445d0d7c735188da12f8557093eb1b3da24f3f753e8228cc76d330609221a1a*/16, /*538cc696ee60936e32ae5f1bb41b19700481910b4bac2ac3de77c05f4e4f1a36*/270, /*d037b6ebee9d9ed3f8c62dbdef8a3f8594f4315306fe1fcb773c45f3dcaace97*/3000, /*1de54c59712133823b0e003b0e2ceec66380595b6c7aaf49bdff95d323e9b234*/1, /*6871bcc79c32f3f1163a01bb8ee7bb958c3ed85f4df80d69ddf47254e20623cb*/1.6, /*e5588ccd85bd235f1cb40f395303d70ad35eac150e751c308d5fe01fb0d4babf*/0, /*a28abb3506fac82150745aa33567be51f7e462609c341669e35a7002342b5b61*/0, /*cc1e8a5db0f07f1922f614ad3ca343c8fc92cfc06b36b4a047983eb397379541*/0, /*4546a910e3223aca0ad20dff3cdf5ab2bb5c028140baefaefad78a6d12737f23*/"【物理】ミロクにより再び狭界より顕現した黒の\n王。暗黒の瘴気で世界を蹂躙せんとする。\n希望の力のみが黒の王の逆風となる。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・ユニオンバーストは、敵全体に物理大ダメージ\n　を与え、毒状態にする。\n・前から３キャラに物理中ダメージを与え、物理\n　防御力とＴＰを大ダウンさせる。\n　このスキルの効果はダメージを無効化しても\n　付与される。\n・敵全体に物理中ダメージを与え、物理防御力を\n　大ダウンさせる。\n　さらにキャラのＨＰが50％未満の場合、行動\n　速度を中ダウンさせる。\n　このスキルの効果はダメージを無効化しても\n　付与される。\n・黒の王ジャバウォック自身のＨＰが40％を切\n　ると、さらに攻撃が激しく凶暴になる。\n・各ターゲットのブレイク時、物理防御力と魔法\n　防御力がそれぞれ大ダウン。");
INSERT INTO `v1_2ea2fbd558f287f6d0d6d1e203f2c4a6c8ae75ae282f01bef2082bd007c47de8` VALUES (/*e2ea6ca2df6ca5fad61ed9ab9f7155aa7193a8760955face963dfd9853b8ff78*/316900, /*deb78dc40fc2c938a0308789e249dc690048140880730eef1aa92bb70a567551*/"暴走のイノセントボウ", /*7489fe449fadfca5f7c00a580dff1edad7378dfe631677c1c8ad5c7decf09097*/316900, /*f65bc453c735e13837d9d845d6f3d3ba35b94944b98325b380a630402a66df12*/0, /*e445d0d7c735188da12f8557093eb1b3da24f3f753e8228cc76d330609221a1a*/16, /*538cc696ee60936e32ae5f1bb41b19700481910b4bac2ac3de77c05f4e4f1a36*/270, /*d037b6ebee9d9ed3f8c62dbdef8a3f8594f4315306fe1fcb773c45f3dcaace97*/3000, /*1de54c59712133823b0e003b0e2ceec66380595b6c7aaf49bdff95d323e9b234*/2, /*6871bcc79c32f3f1163a01bb8ee7bb958c3ed85f4df80d69ddf47254e20623cb*/1.6, /*e5588ccd85bd235f1cb40f395303d70ad35eac150e751c308d5fe01fb0d4babf*/0, /*a28abb3506fac82150745aa33567be51f7e462609c341669e35a7002342b5b61*/0, /*cc1e8a5db0f07f1922f614ad3ca343c8fc92cfc06b36b4a047983eb397379541*/0, /*4546a910e3223aca0ad20dff3cdf5ab2bb5c028140baefaefad78a6d12737f23*/"【魔法】ミロクの弩級な力を受け、再び暴走した\nラフィの聖弓。その矢に貫かれると非力なエン\nジェルと化し、歪な呪いで生を侵蝕される。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・ユニオンバーストは、敵全体に魔法大ダメージ\n　を与え、魔法防御力を大ダウンさせる。\n　エンジェル状態の場合は呪いを付与する。\n　このスキルの効果はダメージを無効化しても\n　付与される。\n・敵全体に魔法中ダメージを与え、さらにＨＰが\n　70％未満のキャラを一定時間エンジェル状態\n　にする。\n　エンジェルになったキャラは、通常攻撃しか使\n　用できなくなり、かかっているステータスアッ\n　プ効果を解除される。\n　エンジェル状態はＨＰを回復することで解除\n　される。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・前から３キャラに魔法中ダメージを与え、スタ\n　ンさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・暴走のイノセントボウ自身のＨＰが40％を切\n　ると、さらに攻撃が激しく凶暴になる。");
INSERT INTO `v1_2ea2fbd558f287f6d0d6d1e203f2c4a6c8ae75ae282f01bef2082bd007c47de8` VALUES (/*e2ea6ca2df6ca5fad61ed9ab9f7155aa7193a8760955face963dfd9853b8ff78*/317000, /*deb78dc40fc2c938a0308789e249dc690048140880730eef1aa92bb70a567551*/"ファントムバロン", /*7489fe449fadfca5f7c00a580dff1edad7378dfe631677c1c8ad5c7decf09097*/317000, /*f65bc453c735e13837d9d845d6f3d3ba35b94944b98325b380a630402a66df12*/0, /*e445d0d7c735188da12f8557093eb1b3da24f3f753e8228cc76d330609221a1a*/16, /*538cc696ee60936e32ae5f1bb41b19700481910b4bac2ac3de77c05f4e4f1a36*/270, /*d037b6ebee9d9ed3f8c62dbdef8a3f8594f4315306fe1fcb773c45f3dcaace97*/3000, /*1de54c59712133823b0e003b0e2ceec66380595b6c7aaf49bdff95d323e9b234*/2, /*6871bcc79c32f3f1163a01bb8ee7bb958c3ed85f4df80d69ddf47254e20623cb*/1.6, /*e5588ccd85bd235f1cb40f395303d70ad35eac150e751c308d5fe01fb0d4babf*/0, /*a28abb3506fac82150745aa33567be51f7e462609c341669e35a7002342b5b61*/0, /*cc1e8a5db0f07f1922f614ad3ca343c8fc92cfc06b36b4a047983eb397379541*/0, /*4546a910e3223aca0ad20dff3cdf5ab2bb5c028140baefaefad78a6d12737f23*/"【魔法】禍々しい呪縛で幽冥より誘われた悲しき\n亡親の霊。天に昇ったはずの魂がミロクの術で絶\n大な魔力となり、立ち塞がる者を容赦なく屠る。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・ユニオンバーストは、最も近い敵１キャラに\n　魔法大ダメージを与え、さらに魔法防御力を\n　大ダウンさせる。\n　このスキルの効果はダメージを無効化しても\n　付与される。\n・一番遠くの敵１キャラに物理中ダメージを\n　与え、さらに一番前にいるキャラの位置まで\n　引き寄せてスタンさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・最も近い敵１キャラに魔法中ダメージを\n　与える。\n　さらに、前方３キャラに魔法中ダメージを\n　与え、ノックバックさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・ファントムバロン自身のＨＰが40％を切ると、\n　さらに攻撃が激しく狂暴になる。");
INSERT INTO `v1_2ea2fbd558f287f6d0d6d1e203f2c4a6c8ae75ae282f01bef2082bd007c47de8` VALUES (/*e2ea6ca2df6ca5fad61ed9ab9f7155aa7193a8760955face963dfd9853b8ff78*/317100, /*deb78dc40fc2c938a0308789e249dc690048140880730eef1aa92bb70a567551*/"テンタパス", /*7489fe449fadfca5f7c00a580dff1edad7378dfe631677c1c8ad5c7decf09097*/317100, /*f65bc453c735e13837d9d845d6f3d3ba35b94944b98325b380a630402a66df12*/0, /*e445d0d7c735188da12f8557093eb1b3da24f3f753e8228cc76d330609221a1a*/16, /*538cc696ee60936e32ae5f1bb41b19700481910b4bac2ac3de77c05f4e4f1a36*/270, /*d037b6ebee9d9ed3f8c62dbdef8a3f8594f4315306fe1fcb773c45f3dcaace97*/3000, /*1de54c59712133823b0e003b0e2ceec66380595b6c7aaf49bdff95d323e9b234*/1, /*6871bcc79c32f3f1163a01bb8ee7bb958c3ed85f4df80d69ddf47254e20623cb*/1.6, /*e5588ccd85bd235f1cb40f395303d70ad35eac150e751c308d5fe01fb0d4babf*/0, /*a28abb3506fac82150745aa33567be51f7e462609c341669e35a7002342b5b61*/0, /*cc1e8a5db0f07f1922f614ad3ca343c8fc92cfc06b36b4a047983eb397379541*/0, /*4546a910e3223aca0ad20dff3cdf5ab2bb5c028140baefaefad78a6d12737f23*/"【物理】暴食娘により踊り食いの餌食とされた憤\n怒の軟体魔獣。体に刻まれた歯形は魔獣の決意。\nミロクの力を携え、雪辱を晴らすべく猛り狂う。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与える。\n・敵全体にキャラの最大ＨＰに応じた物理割合ダ\n　メージを与え、物理防御力を大ダウンさせ、麻\n　痺させる。\n　このスキルの効果や状態異常はダメージを\n　無効化しても付与される。\n・目の前の敵１キャラに物理大ダメージを\n　与える。\n・目の前の敵１キャラにキャラの最大ＨＰに応じ\n　た物理割合ダメージを与える。\n・テンタパス自身のＨＰが40％を切ると、さら\n　に攻撃が激しく凶暴になる。");
INSERT INTO `v1_2ea2fbd558f287f6d0d6d1e203f2c4a6c8ae75ae282f01bef2082bd007c47de8` VALUES (/*e2ea6ca2df6ca5fad61ed9ab9f7155aa7193a8760955face963dfd9853b8ff78*/317200, /*deb78dc40fc2c938a0308789e249dc690048140880730eef1aa92bb70a567551*/"ゴウシン", /*7489fe449fadfca5f7c00a580dff1edad7378dfe631677c1c8ad5c7decf09097*/317200, /*f65bc453c735e13837d9d845d6f3d3ba35b94944b98325b380a630402a66df12*/0, /*e445d0d7c735188da12f8557093eb1b3da24f3f753e8228cc76d330609221a1a*/16, /*538cc696ee60936e32ae5f1bb41b19700481910b4bac2ac3de77c05f4e4f1a36*/270, /*d037b6ebee9d9ed3f8c62dbdef8a3f8594f4315306fe1fcb773c45f3dcaace97*/3000, /*1de54c59712133823b0e003b0e2ceec66380595b6c7aaf49bdff95d323e9b234*/1, /*6871bcc79c32f3f1163a01bb8ee7bb958c3ed85f4df80d69ddf47254e20623cb*/1.6, /*e5588ccd85bd235f1cb40f395303d70ad35eac150e751c308d5fe01fb0d4babf*/0, /*a28abb3506fac82150745aa33567be51f7e462609c341669e35a7002342b5b61*/0, /*cc1e8a5db0f07f1922f614ad3ca343c8fc92cfc06b36b4a047983eb397379541*/0, /*4546a910e3223aca0ad20dff3cdf5ab2bb5c028140baefaefad78a6d12737f23*/"【物理】《救い手》から力を授かり変貌を遂げた\n元貴族議会議長。闇の力を揮い、痛恨の単体攻撃\nで目の前の敵から順に敵陣を豪腕粉砕する。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・ユニオンバーストは、目の前の敵１キャラに物\n　理極大ダメージを与える。\n・目の前の敵１キャラに物理大ダメージを与え、\n　ノックバックさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・自身の物理攻撃力を大アップさせる。\n・目の前の敵１キャラに物理大ダメージを与え、\n　物理防御力を中ダウンさせる。\n　このスキルの効果はダメージを無効化しても\n　付与される。\n・ゴウシン自身のＨＰが50％を切ると、さらに\n　攻撃が激しく凶暴になる。");
