INSERT INTO `v1_2ea2fbd558f287f6d0d6d1e203f2c4a6c8ae75ae282f01bef2082bd007c47de8` VALUES (/*e2ea6ca2df6ca5fad61ed9ab9f7155aa7193a8760955face963dfd9853b8ff78*/318104, /*deb78dc40fc2c938a0308789e249dc690048140880730eef1aa92bb70a567551*/"穢れし湯ノ神・陽卵", /*7489fe449fadfca5f7c00a580dff1edad7378dfe631677c1c8ad5c7decf09097*/318104, /*f65bc453c735e13837d9d845d6f3d3ba35b94944b98325b380a630402a66df12*/0, /*e445d0d7c735188da12f8557093eb1b3da24f3f753e8228cc76d330609221a1a*/16, /*538cc696ee60936e32ae5f1bb41b19700481910b4bac2ac3de77c05f4e4f1a36*/270, /*d037b6ebee9d9ed3f8c62dbdef8a3f8594f4315306fe1fcb773c45f3dcaace97*/3000, /*1de54c59712133823b0e003b0e2ceec66380595b6c7aaf49bdff95d323e9b234*/1, /*6871bcc79c32f3f1163a01bb8ee7bb958c3ed85f4df80d69ddf47254e20623cb*/1.6, /*e5588ccd85bd235f1cb40f395303d70ad35eac150e751c308d5fe01fb0d4babf*/0, /*a28abb3506fac82150745aa33567be51f7e462609c341669e35a7002342b5b61*/0, /*cc1e8a5db0f07f1922f614ad3ca343c8fc92cfc06b36b4a047983eb397379541*/0, /*4546a910e3223aca0ad20dff3cdf5ab2bb5c028140baefaefad78a6d12737f23*/"【ＭＯＤＥ１】悦楽に溺れる御身から、罪と穢れ\nが煮え滾る。限外に溢れ出る負の瘴気は、人々に\n禍をもたらすと供に、自らの身もを蝕む。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・穢れし湯ノ神・陽卵から受けるすべてのダメー\n　ジで発生する、パーティキャラのＴＰ回復量を\n　中ダウンさせる。\n・ユニオンバーストは、敵全体に魔法特大ダメー\n　ジを与え、呪い状態を付与する。既に呪い状態\n　の敵には代わりに呪詛状態を付与する。呪詛に\n　よる継続ダメージは毎秒ごとに増加する。\n・敵全体に魔法大ダメージを与え、呪い状態を付\n　与する。さらに、魔法防御力を大ダウンさせる。\n　その後、自身に呪い状態を付与する。このスキ\n　ルの状態異常と効果はダメージを無効化しても\n　付与される。\n・自身に近い敵３キャラに物理大ダメージを与え\n　る。\n・穢れし湯ノ神・陽卵の付与する呪いおよび呪詛\n　のダメージでは、キャラのＴＰが回復しない。");
INSERT INTO `v1_2ea2fbd558f287f6d0d6d1e203f2c4a6c8ae75ae282f01bef2082bd007c47de8` VALUES (/*e2ea6ca2df6ca5fad61ed9ab9f7155aa7193a8760955face963dfd9853b8ff78*/318105, /*deb78dc40fc2c938a0308789e249dc690048140880730eef1aa92bb70a567551*/"穢れし湯ノ神・陽卵", /*7489fe449fadfca5f7c00a580dff1edad7378dfe631677c1c8ad5c7decf09097*/318106, /*f65bc453c735e13837d9d845d6f3d3ba35b94944b98325b380a630402a66df12*/0, /*e445d0d7c735188da12f8557093eb1b3da24f3f753e8228cc76d330609221a1a*/16, /*538cc696ee60936e32ae5f1bb41b19700481910b4bac2ac3de77c05f4e4f1a36*/270, /*d037b6ebee9d9ed3f8c62dbdef8a3f8594f4315306fe1fcb773c45f3dcaace97*/3000, /*1de54c59712133823b0e003b0e2ceec66380595b6c7aaf49bdff95d323e9b234*/1, /*6871bcc79c32f3f1163a01bb8ee7bb958c3ed85f4df80d69ddf47254e20623cb*/1.6, /*e5588ccd85bd235f1cb40f395303d70ad35eac150e751c308d5fe01fb0d4babf*/0, /*a28abb3506fac82150745aa33567be51f7e462609c341669e35a7002342b5b61*/0, /*cc1e8a5db0f07f1922f614ad3ca343c8fc92cfc06b36b4a047983eb397379541*/0, /*4546a910e3223aca0ad20dff3cdf5ab2bb5c028140baefaefad78a6d12737f23*/"【ＭＯＤＥ２】名湯の権化は、争いの最中すら気\nままに湯を浴み寛ぐ。神をも骨抜きにする至極の\n湯は、御身を鎮めんとする者への好機となるか？\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・穢れし湯ノ神・陽卵から受けるすべてのダメー\n　ジで発生する、パーティキャラのＴＰ回復量を\n　中ダウンさせる。\n・敵全体に物理小ダメージを与え、ノックバック\n　させる。さらに、一定範囲内の敵すべての行動\n　速度を大ダウンさせる。その後、お風呂に浸か\n　り始める。穢れし湯ノ神・陽卵がお風呂に浸\n　かっている間、自身の物理防御力と魔法防御力\n　を極大ダウンさせる。このスキルの状態異常と\n　効果はダメージを無効化しても付与される。\n・自身に近い敵３キャラに物理小ダメージを与え\n　る。");
